	font-family:"Comic Sans MS", cursive, sans-serif;
}

.fullwidth_main.front_page h2 span{
	font-family:"Comic Sans MS", cursive, sans-serif;
}

.browse-this h4:after{
	font-family:"Comic Sans MS", cursive, sans-serif;
}


}
