css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {

* {
    border-radius: 0 !important;
    box-shadow: none !important;
/*     font-weight: bold; */
}

a:hover, a.highlightlink, a[rb-highlight] {
    box-shadow: none !important;
}

html {
    background: grey url("https://i.ytimg.com/vi/f9-DpaFipVQ/maxresdefault.jpg") fixed no-repeat 60% center/ 1290px auto;
    height: auto;
}

body {
    background: rgba(0, 0, 0, .7) !important;
    height: inherit;
}

/*BG None*/#searchbar, #contentArea, .darkFpBlock, .article table, .autoPagerS, #categoryBrowser, .bar, .searchAanbod #nearbyAdForm, #jobs, #filter, .pageTabsContainer, #entity, .relatedBlock, div.message, #tracker .fakeTop, .fancyButton.white, .filterBlock {
    background: none !important;
}

/*BG Transparent*/#portalPlayerWrapper, .ScoreCard *, .ScoreCard, .reactieBody.informative, .col_specs .popular_specs, .filterOption.active {
    background-color: transparent !important;
}

/*Border none*/#top, #contentArea *, .darkFpBlock, .pageTabsContainer, table.spec-detail td.spec-column, #entity > div, #ticker {
    border: 0 !important;
}
.thumb{
    border-radius: 0;
}
.pageTabs li {
    border: 0;
    border-radius: 15px;
}

[class="fpaImageContainer"]:not(:hover), .secondColumn.frontpage > .darkFpBlock > ul > li > .thumb:not(:hover), .mainColumn.frontpage > #community > div > ul > li > .thumb:not(:hover), .mainColumn.frontpage #videos > ul > li > .thumb:not(:hover), #recentlyTested li .thumb:not(:hover), #highlightedProducts .fpaItem .thumb:not(:hover), .frontpage #community .userReview .productThumb:not(:hover),  .col_images:not(:hover), .editionOverview .imageCarousel .thumb:not(:hover) {
    opacity: .6;
}

/*Header*/
    /*fixed*/ .fixedHeader #menubar {
        position: absolute;
    }

#menubar {
    background: linear-gradient(rgba(180, 35, 60, .6), rgba(156, 15, 55, 1));
    border-bottom: 0;
}

#top {
    background: linear-gradient(transparent 44px, black 44px, black 67px, rgba(200, 200,200, .8) 67px, rgba(238, 239, 239, .6) 122px, transparent 122px)
}

.col_specs tbody tr:hover td, .nextPrevLinks.large.review li:hover {
    background-color: rgba(225, 239, 251, .6);
    text-shadow: none !important;
}

#bottom {
    background: rgba(0, 0, 0, .6);
}

#tracker .fakeTop > div {
/*     background: url('http://i.imgur.com/jcG6ZxZ.png') no-repeat scroll 0% 0% transparent; */
}
/*/Header*/

/*Content*/
/* .articleColumn, .mainColumn.frontpage {
    width: 69%;
}

#menu, #search, #contentArea, #ticker p, #about {
    width: 95%;
}

.fpaItems {
    margin: 0 auto !important;
} */
/*Content*/


/*Hover kader*/a.fancyButton.grey.clickOut, #poll label:hover {
    background: rgba(49, 50, 50, .5) !important;
    text-shadow: none;
}

/*Display none*/ #bottom div.hr, #about, .thumb.usericonsmall, #menubottombar div.socialcount, .notificationsContainer, #trackerHeader .bulletSeparatedList, #tracker .fakeTop .giveFeedback, .smallTracker #tracker .fakeTop .giveFeedback.full {
    display: none !important;
}

#layout {
    padding-bottom: 50px !important;
}

#listingContainer {
    min-height: 100%;
}

/*Sidebar*/body.hasTracker {
    margin-left: 0px !important;
}

#tracker{
/*     background: linear-gradient(transparent 0px, transparent 67px, rgba(0, 0, 0, .9) 67px) !important; */
    background: rgba(0, 0, 0, 1);
/*     position: absolute; */
    bottom: 0;
    z-index: 110;
    padding-top: unset;
    top: 0 !important;
/*     font-size-adjust: .6; */
}

.smallTracker #tracker {
    left: -236px;
/*     width: 500px; */
}

#trackerContentContainer {
/*     height: 300px !important; */
/*         overflow: auto !important; */
}

.trackerVisible #tracker .fakeTop > div {
    right: -20px;
/*     z-index: 111; */
}

/*backgrounf UserReview*/ div.userReview > div > div.userReviewDescription {
    background: rgba(255, 255, 255, .75) !important;
}

#tracker .fakeTop {
    border-bottom: 1px solid transparent;
}

.smallTracker #tracker {
/*     background: black; */
}
/*Sidebar*/

#categoryBrowser div, #categoryBrowser .sublist, [class="filterOption"], .pageTabs li {
    background: rgba(255, 255, 255, .8) !important
}

/*Hover specs/info*/
    .colSpecs .specs table tr:hover td * {
        color: black !important;
        text-shadow: none !important;
    }

    .colSpecs .specs table tr:hover td:not(h3) {
        background-color: rgba(255, 255, 255, .8) !important;
    }
/*Hover specs/info*/

/*Text*/
#layout *, .editionOverview .specs *, .entityHeader .subInfo *, .entityHeader .breadcrumb > li , .entityHeader .breadcrumb > li a, .frontpage #community .communityItem .user, #highlightedProducts .productTitle a, #bestBuyGuides > ul:nth-child(3) > li > div > p > a, #bestBuyGuides > p.description {
    color: white; 
    text-shadow: 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black, 0px 0px 3px black;
    font-size-adjust: .67;
}

    /*Zoekoptie*/ .filterOption h4.toggleCollapse {
        color: white!important;;
        text-shadow: 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black, 1px 1px 3px black !important;
    }

#pricewatch li .fancyButton, #poll label:hover span, .commentCount , .scoreButton, a.fancyButton, span.fancyButton, .portalBlock #categoryBrowser *, .pageDistribution b, .fancyButton, .sortable.sortorder li a, [id="overviewCtaButton"] *, .sortable > ul > li > a, .dropdown ul li, .nextPrevLinks.large.review li:hover *, .fpaItemsWrapper .fpaItem a {
    text-shadow: none !important;
}

#categoryBar ul a, #jobs li .location a, #community p , #ticker *, .latestVideos p.views, a.videoNextPage, #header h1 .subtitle, .editionOverview table.spec-detail .spec-index-column, #ScoreCardDiv h3, .entityHeader .inventory span, .entityHeader .wishlist span, #history .date, #header h1 .subtitle a, .rbProductlist .item .fancyButton, #navMenu > li > a, #recentlyTested p > a, .ScoreCard .userReviewScoreDiv h3 > a, .frontpage #community .userReview .userInfo h2 a, .frontpage #community .communityItem .category, #highlightedProducts .productTitle a {
    color: white !important;
}

input.text, .pageTabs > ul a, .forumList table.listing *, #select_days, #select_days *, .fancyButton.grey, .messagecontent .message-quote-div *, .messagecontent .message-quote-div, .sortable.select .specName, .sortable.sortorder small, .toggleSwitch span.checked, #reacties #reactieContainer > .reactie > .reactieBody.spotlight *, [id="tab:forum"] table.listing *, [class="reactie"] .reactieBody.spotlight *, .pageDistribution .next, .frontpage #community .userReview .userReviewDescription p, div.userReview > div > div > p > a, #brandFilter > div > div, #reactieForm *, .popularSpecs tbody tr *, .popularSpecs h3 > span, ul.topKarma li > a {
    color: black !important;
    text-shadow: none !important;
}
/*/Text*/

#tracker .fakeTop .toggleVisibility {
    bottom: -64px;
    right: -11.5px;
    background: none !important;
    width: 0;
    height: 0;
    border-top: 12px solid transparent;
    border-bottom: 12px solid transparent;
/*     display: none; */
    border-left: 12px solid rgba(166, 30, 57, .9);
}

.trackerVisible #tracker .fakeTop > div {
/*     background-position: 0 ; */
    right: -11.5px;
    width: 0;
    height: 0;
    background-image: none;
    width: 500px;
/*     height: 0; */
    border-top:  12px solid black;
    border-bottom: 12px solid black;
    
    border-right: 12px solid rgb(166, 30, 57);
}

}
