@-moz-document domain("lurkmore.to"){
.mw-body {
max-width:/*[[mw]]*/ !important;
}
div#mw-panel{width:12.5rem !important}
.mw-body{margin-left: 13rem !important}
#left-navigation {
    margin-left: 13rem !important;
}
/* ESI 2.5 core */
html {font-size: /*[[fs]]*/ !important; line-height: /*[[lh]]*/ !important; }
esi-widest-text-block {max-width: /*[[mw]]*/ !important}
a {text-decoration: none !important}
esi-text-block {width: x rem; height: y rem}	
* {font-size: inherit !important; line-height: inherit !important;font-family:/*[[ff]]*/ !important}
h1 {font-size: 1.7 rem !important; font-size: calc(1.4375rem + 8px) !important; line-height: calc(1.4375rem * 1.052 + 8px * 1.052 + 5px) !important}
h2 {font-size: 1.4 rem !important; font-size: calc(1.25rem + 4px) !important; line-height: calc(1.25rem * 1.052 + 1.052 + 4px + 5px) !important}
h3 {font-size: 1.17 rem !important; font-size: calc(1.0625rem + 2px) !important; line-height: calc(1.0625rem * 1.052 + 2px * 1.052 + 5px) !important}
sup, sub{line-height: 1 !important; font-size: 0.6875rem; font-size: calc(1rem - 5px) !important;}
h1,h2,h3{padding-bottom: 1rem !important}
#coordinates{display:none !important}
}
