@-moz-document url("chrome://browser/content/pageinfo/pageInfo.xul") {

/*  WINDOW  */
#mainDeck {
  border-top: 1px solid rgb(55,65,77) !important;
  background-color: rgb(45,53,63) !important;
  color: rgb(77,148,227) !important;
}
.textbox-input {
  color: rgb(200,200,200) !important;
}
/*  ----  */



/*  HEADER TOOLBAR  */
#topBar, .paneSelector {
    border-bottom: 1px solid rgb(24,29,32) !important;
    background-color: rgb(52,60,69) !important;
    color: white !important;
}
#viewGroup > radio {
    width: 70px;
    height: 48px !important;
    background-color: rgb(52,60,69) !important;
    color: rgb(182,186,191) !important;
}
#viewGroup > radio:hover {
    background-color: rgb(43,50,57) !important;
    color: black;
}
#viewGroup > radio[selected="true"] {
    -moz-appearance: none !important;
    background: none  !important;
    background-color: rgb(26,70,102) !important;
    color: rgb(245,247,250) !important;
    box-shadow: 0 2px 0 #d7f1ff inset,
              0 8px 3px -5px #2b82bf inset,
              0 -2px 0 rgba(0,0,0,.2) inset;
}
/*  ----  */



/*  GROUPBOX  */
caption {
    background-color: rgba(29,33,37,1) !important;
}
.caption-text{
    color: rgb(182,186,191) !important;
}
groupbox {
    -moz-appearance: none;
    border: 2px solid rgb(71,84,99);
    border-radius: 3px;
    -moz-border-top-colors: rgba(0,0,0,1) rgba(58,70,80,1);
    -moz-border-right-colors: rgba(58,70,80,1) rgba(0,0,0,1);
    -moz-border-bottom-colors: rgba(58,70,80,1) rgba(0,0,0,1);
    -moz-border-left-colors: rgba(0,0,0,1) rgba(58,70,80,1);
    padding: 3px 3px 6px;
    margin: 3px;
}
groupbox.collapsable caption .caption-icon { 
    width: 10px;
    height: 10px;
    -moz-margin-start: 2px;
    -moz-margin-end: 2px;
    background-image: url("chrome://browser/skin/devtools/controls.png") !important;
    background-repeat: no-repeat;
    background-position: -43px -17px;
}
groupbox.collapsable[closed="true"] caption .caption-icon { 
    width: 10px;
    height: 10px;
    background-position: -29px -15px;
}
/*  ----  */



/*  TREE  */
tree {
    -moz-appearance: none !important;
    border: 1px solid rgb(0,0,0) !important;
    color: rgb(200,200,200) !important;
    background-color: rgb(19,28,38) !important;
}
treecol,
treecolpicker {
    -moz-appearance: none !important;
    border: 0px solid;
    border-bottom: 1px solid;
    -moz-border-bottom-colors: rgb(0,0,0) !important;
    background:
            linear-gradient(to bottom, 
                rgba(56,70,82,1) 0%,
                rgba(35,39,48,1) 100%) !important;
    color: white;
    padding: 0px 4px;
    height: 18px;
}
tree splitter {
    -moz-appearance: none !important;
    border-width: 0px 1px 1px 1px !important;
    -moz-border-left-colors: transparent transparent;
    -moz-border-right-colors: transparent transparent;
    width: 1px !important;
    min-width: 1px;
    max-width: 1px;
    background: none no-repeat scroll 0px 0px rgb(0,0,0) !important;
}
treechildren::-moz-tree-row {
    background-color: rgb(19,28,38) !important;
    overflow-y: scroll  !important;
}
treechildren::-moz-tree-row(selected) {
    background-color: rgba(51,159,253,0.15) !important;
}
treechildren::-moz-tree-cell-text(selected) {
    color: rgb(51,159,253) !important;
}
treechildren::-moz-tree-row(current, focus) {
    border: 1px solid rgb(2,83,155) !important;
}
.tree-columnpicker-icon {
  list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAMCAYAAABSgIzaAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPElEQVQoz2NgoDdghDH+////n6BiRkZGDEF0jYT49LeRheJQIsapRNuIzTCYP3EGDkwBsjjWwKGmN/ACAA6wM+xPEB+mAAAAAElFTkSuQmCC
                );
}
/*  ----  */


/*  PERMISSION  */
#permList {
    margin-top: .5em;
    border: 1px solid rgb(19,28,38) !important;
    background-color: rgb(19,28,38) !important;
    color: rgb(182,186,191) !important;
}
.permission {
    padding-top: 3px;
    padding-bottom: 3px;
    -moz-padding-start: 3px;
    -moz-padding-end: 3px;
    border-bottom: 1px solid rgba(34,50,68,0.8) !important;
}
.permission:hover {
    background-color: rgb(34,50,68) !important;
}
.permissionLabel {
    color: rgb(51,159,253) !important;
}
/*  ----  */



/*  RSS  */
#feedListbox {
    -moz-appearance: none !important;
    border: 1px solid rgb(19,28,38) !important;
    margin: 2px 4px;
    background-color: rgb(19,28,38) !important;
    color: rgb(231,150,83) !important;
}
#feedListbox richlistitem {
    padding-top: 6px;
    padding-bottom: 6px;
    -moz-padding-start: 7px;
    -moz-padding-end: 7px;
    min-height: 25px;
    border-bottom: 1px solid rgba(34,50,68,0.8) !important;
    color: rgb(231,150,83) !important;
}
#feedListbox richlistitem[selected="true"] {
    background-color: rgb(34,50,68) !important;
}
#feedListbox richlistitem .text-link {
    color: rgb(51,159,253) !important;
}
#feedListbox > richlistitem > vbox > hbox:first-child {
    margin: 0px 80px 0px 0px;
}
.feed-subscribe {
    margin: -36px 0px 14px 0px;
}
/*  ----  */




/*  CHECKBOX  */
.checkbox-check {
    -moz-appearance: none !important;
    border: 1px solid !important;
    border-color: rgba(255,255,255,0.04) !important;
    border-bottom-color: rgba(255,255,255,0.08) !important;
    -moz-border-top-colors: transparent ;
    -moz-border-right-colors: transparent ;
    -moz-border-bottom-colors: transparent ;
    -moz-border-left-colors: transparent ;
    min-width: 14px;
    min-height: 14px;
    background: transparent no-repeat 50% 50%;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.0);
    transition: border-color, box-shadow 500ms ease-out 0s !important;
}
checkbox:-moz-focusring > .checkbox-label-box {
    border: 1px solid transparent;
}
checkbox:hover > .checkbox-check,
checkbox:-moz-focusring > .checkbox-check {
    border-color: rgba(128,255,255,0.4) !important;
    transition: border-color 500ms ease-out 0s !important;
}
checkbox[checked="true"]:hover > .checkbox-check {
    border-color: rgb(255,255,128) !important;
}
checkbox[checked="true"] > .checkbox-check {
    border-color: rgb(128,255,255) !important;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.45);
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPUlEQVQI13WMQQrAQAwCR8i/03357ClQSioI4qBR2XTA+gMAdcCGfAFATdGQN2hIbYt5iroCAFRUHnXy+AKf3Cn4sMNREQAAAABJRU5ErkJggg==
                ) ;
}
/*  ----  */

/*  RADIO CHECKBOX  */
.radio-check-box1 {
    -moz-appearance: none !important;
    margin: 1px 0px !important;
    border: 0px solid transparent;
    border-radius: 50% !important;
    width: 16px !important;
    height: 16px !important;
    background: none no-repeat scroll 0px 0px rgba(30,30,30,0.7) !important;
    box-shadow: inset 1px 1px 3px 0px rgba(0,0,0,1),
                          inset -1px -1px 0px 0px rgba(100,100,100,0.7);
    transition: all 250ms ease-in-out 0s !important;
}
.radio-check-box2 {
    border: 0px solid transparent !important;
    border-radius: 50%;
    padding: 0px !important;
    margin: 3px 3px !important;
    height: 10px !important;
    width: 10px !important;
    max-height: 10px !important;
    max-width: 10px !important;
    list-style-image: none;
    transition: all 500ms ease-out 0s !important;
}

radio:not([disabled="true"]):not([selected="true"]):hover > .radio-check-box1 > .radio-check-box2 ,
radio:not([disabled="true"]):not([selected="true"])[focused="true"] > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(28,155,155);
    transition: none !important;
}
radio:not([disabled="true"]):hover:active > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(28,155,155);
}

radio[selected="true"]:not([disabled="true"]) > .radio-check-box1 {
    box-shadow: inset 1px 1px 3px 0px rgba(0,0,0,1),
                          inset -1px -1px 0px 0px rgba(128,255,255,0.7);
}
radio[selected="true"] > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(128,255,255);
}
radio[selected="true"][disabled="true"] > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(60,60,60);
}
/*  ----  */

/*  ----  */
checkbox:not([checked="true"]) > .checkbox-check {
    color: rgb(192,192,192) !important;
}
radio[focused="true"] > .radio-label-box,
checkbox:-moz-focusring > .checkbox-label-box {
    border: 1px solid transparent !important;
}
/*  ----  */




/*  BUTTONS  */
button
{
     -moz-appearance: none !important;
    background-color: rgba(58,68,79,0) !important;
    background-image: linear-gradient(to bottom,
                                     rgba(122,137,152,0.47),
                                     rgba(129,139,150,0.14)
                                     ) !important;
    border-style: solid !important;
    border-width: 0px !important;
    border-color: rgb(29,37,46) !important;
    border-radius: 3px;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
    color: rgb(215,241,255) !important;
    padding: 2px 2px 1px 4px;
    text-shadow: 0px -1px 0px rgba(11, 12, 13, 0.45) !important;
    transition: all 300ms ease-out 0s !important;
}
button:hover,
button:focus,
button:focus:hover
{
    background-color: rgba(48,58,69,0) !important;
    background-image: linear-gradient(to bottom,
                                     rgba(111,126,140,0.45),
                                     rgba(93,105,118,0.20)
                                     ) !important;
    border-style: solid !important;
    border-width: 0px !important;
    border-color: rgb(29,37,46) !important;
    border-radius: 3px;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 0px 3px 2px rgba(10,18,25,0.25),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
	color: rgb(152,217,252) !important;
}
button:active,
button:active:hover
{
    background-color: rgba(5,15,26,0.05) !important;
    background-image: linear-gradient(to bottom,
                                     rgba(255,255,255,0.0),
                                     rgba(0,0,0,0.0)
                                     ) !important;
    border-style: solid !important;
    border-width: 0px !important;
    border-color: rgb(29,37,46) !important;
    border-radius: 3px;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
	color: rgb(101,191,255) !important;
}
/*  ----  */




/*  VIEW MEDIA  */
.inset.iframe {
  border: 1px solid rgb(55,65,77) !important;
  box-shadow:
            inset 0px 0px 200px 0px rgba(0, 0, 0, 0.65),
            inset 0px 0px 15px 0px rgba(0, 0, 0, 0.99)
             !important;
  background-color: rgb(19,28,38) !important;
  background-image: url(
                data:image/png;base64,
                ) !important;}
/*  ----  */
#blockImageBox > label {
  display: none  !important;}

}/*  END  */
