css:
@-moz-document domain("github.com") {
.blob-code, .lines {
    background-color: #000!important;
    color: #fff!important;
    font-weight: bold!important;
}

.blob-code-addition {
  background-color: #131!important;
}
  
.blob-code-deletion {
  background-color: #311!important;    
}

.blob-num, .line-number {
    background-color: #000!important;
    color: #6897BB!important;
    font-weight: bold!important;
}

.blob-num-addition {
  background-color: #292!important;
  color: #fff!important;
}

.blob-num-deletion {
  background-color: #922!important;
  color: #fff!important; 
}

.blob-num:hover {
  background-color: #444!important;
  color: #6897BB!important;
}

.highlight pre {
  background-color: #000!important;
  color: #fff!important;
}

.blob-code-addition .x {
    background-color: #292!important;
}

.blob-code-deletion .x {
    background-color: #922!important;
}

.c  { color: #888!important; font-style: normal!important } /* Comment */
