css:
@-moz-document domain("volafile.io") { 
/* Main Page */
.page_index #main_below_buttons_text {
    margin-top: 2em !important;
}
.page_index .main small a {
    padding: 2px 6px !important;
    color: rgba(127, 150, 173, 1) !important;
    background-color: rgba(0, 0, 0, 0.2) !important;
    border-radius: 4px !important;
    text-decoration: none !important;
}
.page_index .main a:hover {
    background-color: rgba(0, 0, 0, 0.1) !important;
    color: #fff !important;
}
.page_index .main small {
    color: #ddd !important;
}
.page_index #main_learn_more.button.light a:hover {
    text-decoration: none !important;
}
.page_index #main_logo {
    font-size: 1000% !important;
    letter-spacing: -1pt !important;
    margin-top: 0.5em !important;
    text-shadow: 5px 5px 0px rgba(0, 0, 0, 0.2) !important;
}
#main_header_description {
    font-size: 2.5em !important;
    text-shadow: 3px 3px 0px rgba(0, 0, 0, 0.2) !important;
}
.button.huge, input.huge[type="submit"] {
    font-size: 4em !important;
    text-shadow: 2px 2px 0px rgba(0, 0, 0, 0.2) !important;

}
.button, input[type="submit"] {
    padding-right: 1em !important;
    padding-left: 1em !important;
    border: 4px solid rgba(127, 150, 173, 1) !important;
    box-shadow: 0px 3px 0px 5px rgba(0, 0, 0, 0.2) !important;
}
.button:hover, input[type="submit"]:hover {
    box-shadow: 0px 2px 0px 5px rgba(0, 0, 0, 0.2) !important;
}
#main_howitworks {
    margin-top: 4em !important;
    margin-bottom: 4em !important;
}
.howitworks_title {
    font-size: 400% !important;
    text-shadow: 7px 7px 0px rgba(0, 0, 0, 0.2) !important;
    margin-left: 0px !important;
}
.howitworks_point {
    margin-top: .6em !important;
    font-size: 3em !important;
    text-shadow: 3px 3px 0px rgba(0, 0, 0, 0.2) !important;
    color: #7F96AD !important;
    border-bottom: 1px dashed #7F96AD !important;
    margin-bottom: 7px !important;
}
.howitworks_explain {
    font-family: "OpenSansLight",sans-serif !important;
    font-style: italic !important;
    font-size: 1.3em  !important;
    margin-left: 1.5em  !important;
    color: rgba(218, 218, 218, 1)  !important;
}
#top_navigation {
    position: fixed !important;
    box-shadow: 0px 2px 1px 5px rgba(0, 0, 0, 0.2) !important;
    width: 100% !important;
}
#main_small_links {
    text-align: center !important;
    height: .18em !important;
    box-shadow: 0px -3px 0px 5px rgba(0, 0, 0, 0.2) !important;
   -webkit-transition: opacity .8s linear !important;
   -webkit-transition: all .8s ease-in-out !important;
   -moz-transition: all .8s ease-in-out !important;
   -o-transition: all .8s ease-in-out !important;
}
#main_small_links:hover{
    height: 1.5em !important;

}
small {
    font-size: 100% !important;
}

/* /about */
.text {
    margin-top: 0em !important;
}
.main.text h1{
    font-size: 3.5em !important;
    margin: 1em 0px !important;
    text-shadow: 3px 3px 0px rgba(0, 0, 0, 0.2) !important;
    color: #7F96AD !important;
    margin-bottom: 20px !important;
}
p.large {
    font-family: "OpenSansLight",sans-serif !important;
    font-size: 1.2em !important;
}
.main.text h2{
    margin: 1em 0px !important;
    text-shadow: 2px 2px 0px rgba(0, 0, 0, 0.2) !important;
    color: #7F96AD !important;
}
.main.text p {
    font-family: "OpenSansLight",sans-serif !important;
    margin-top: -1.5em !important;
}
.main.text a {
    padding: 2px 6px !important;
    color: rgba(127, 150, 173, 1) !important;
    background-color: rgba(0, 0, 0, 0.2) !important;
    border-radius: 4px !important;
    text-decoration: none !important;
}
.main.text a:hover {
    background-color: rgba(0, 0, 0, 0.1) !important;
    color: #fff !important;

}

/* /discover */
#discover_header  {
    font-size: 6em !important;
    margin-top: .5em !important;
}
.discover_room_description {
    font-family: "OpenSansLight",sans-serif !important;
    font-size: 1em !important;
}
/* /donate */
.page_donate .main .button.light, input.light[type="submit"] {
    margin: 1em !important;
}
/* /impressum */
.page_impressum a{
    margin: 0em !important;
}
.page_impressum p{
    margin: 2em !important;
}
.page_impressum .main.text{
    font-family: "OpenSansLight",sans-serif !important;
}

/* /tos */
.page_tos p{
    margin: 2em !important;
}
.page_tos .main.text{
    font-family: "OpenSansLight",sans-serif !important;
}

/* /top/uploaded/ */
.page_toplist .main.text.toplist table a {
    padding: 0px !important;
    color: inherit !important;
    background-color: transparent !important;
    border-radius: 0px !important;
    text-decoration: none !important;  
}
.page_toplist .main.text.toplist p {
    margin: 2em !important;
}

/* /rules */
.page_rules .main.text.left h3{
    margin: 1.5em !important;
}

/* /privacy */
.page_privacy .main.text p{
    margin: .5em !important;
}
/* /faq */
.page_faq .main.text p{
    margin: .5em !important;
}
/* /different */
.page_different .main.text p{
    margin: .5em !important;
}
/* rooms */
.page_room .button, input[type="submit"] {
    padding-right: 1em !important;
    padding-left: 1em !important;
    border: 0px solid rgba(127, 150, 173, 1) !important;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0.2) !important;
}
.page_room .button:hover, input[type="submit"]:hover {
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0.2) !important;
}
/* /user/ */
.page_user .main.text p{
    margin: .5em !important;
}
.page_user #profile_gravatar {
    display: inline-table !important;
}
.page_user .main.text img {
    margin-top: 4em !important;

}
.page_user .main.text #profile_header {
    display: block;
    margin-top: 2.5em !important;
}
.page_user .main.text a {
    padding: 0px !important;
    color: inherit !important;
    background-color: transparent !important;
    border-radius: 0px !important;
    text-decoration: none !important; 
}
}
