css:
@-moz-document url-prefix("https://volafile.io/") {
html {
	background-color: hotpink !important;
}

* {
 	margin-left: 10px;
  	font-family: impact;
}
	
#header {
	background-color: lime;
 	border-color: transparent;
}

.header_row_element, #header_row1, #header_row2, #room_name_container, #chat_frame {
	border: 3px groove blue;
}

#chat_messages {
	background-color: yellow;  
}
    
#chat_messages_frame {
    border: 3px groove purple;
}

#chat_input {
	background-color: hotpink;
  	border: 3px solid maroon;
}

#chat_hbar {
  	background-color: #767;
}

#chat_name {
  	/* Changes background color for the name change input */
 	background-color: brown;
  	color: black;
}

#upload_container .button {
  	/* Changes the color of the upload button */
	background-color: black;
}

.ui_frame_buttons .button {
    /* Change color for menu buttons (Login menu, for example) */
	background-color: white;
}
  
.ui_frame_buttons .button:hover {
  	/* Change color when hovering over a button in a menu (Login menu, for example) */
	background-color: white;
}

.ui_frame_buttons .button:active {
  	/* Change color when pressing a button in a menu (Login menu, for example) */
	background-color: white;
}

.chat_file {
  	/* Changes the background color and border color for files linked in chat */
  	background-color: green;
    border: 3px solid purple;
}
    
#chat_messages .chat_message.user a.username { /* Regular user */
	color: white;
}
  
#chat_messages .chat_message a.username { /* Unregistered user */
	color: white;
}

#chat_messages .chat_message.admin a.username { /* Administrator/Moderator */
	color: white;
}

#chat_messages .chat_message.donator a.username { /* Donators */
  	color: white;
}

/*
#chat_messages .chat_message.self a.username {
  	color: #dd4343;
}
*/

#file_list.uneven .filelist_file:nth-child(2n+1) { /* First color. */
  	background-color: lime !important;
}

#file_list.even .filelist_file:nth-child(2n) { /* Second color. */
  	/* background-color: #264256; Deprecated*/
  	background-color: lime !important;
  	
}

#file_list.even .filelist_file:nth-child(2n-1){ /* First color. */
	background-color: purple !important;
  	
}

#file_list.uneven .filelist_file:nth-child(2n+2){ /* Second color */
	/*background-color: #2a3a46; Deprecated*/
  	background-color: purple !important;
  	
}

/*
I have no fucking clue how the above thing handles the file list colors. it just werks

*/

.file_right_part .hover_show{
    display:inline !important;
}

.file_right_part .hover_show:after{
    content: ' - ';
}

.file_right_part:hover .hover_hide{
    display:inline !important;
}

#gallery_frame {
	background-color: rgba(66,255,0,.68);
}

.ui_frame {
	background-color: white;
  	border-color: pink;
}

.dropdown:hover, a.nodecoration:hover {
	background-color: white;
}

.dropdown_list {
	background-color: yellow;
    border-color: white;
}
      
#filter_reminder, #search_input {
	background-color: black;
}
      
#filter_reminder:hover {
	background-color: white;
}

#radio_container {
	background-color: pink;
}

#radio_current {
	background-color: red;
  	border-color: lime;
}

#top_navigation, .top_nav_item, #top_navigation_contents {
	background-color: maroon;
  	border-color: blue;
  	border-right-color: aqua;
}

.top_nav_item[href='/donate'] {
	border-right-color: aqua;
}

.top_nav_item:hover {
	background-color: aqua;
}

#user_toplist th {
	background-color: lime;
}

tbody td {
	border-color: white;
}

.button.light {
	background-color: purple;
}
    
.button.light:visited {
	background-color: red;
}

#radio_load_progress {
	background-color: green;
}

#radio_play_progress {
	background-color: blue;
}

#radio_progress {
 	background-color: hotpink; 
}

hr, .explain_left {
	border-color: gray;
}

#main_small_links {
	background-color: purple;
}

#chat_messages .highlight {
 	background-color: brown !important; 
}

.dropdown_list .dropdown_item:hover {
    background-color: white;
}
	
	@keyframes move {
  from { top: 0; left: 0; }
  to   { top: 100px; left: 100px; }
	}
	
	@-webkit-keyframes move {
  from { top: 0; left: 0; }
  to   { top: 100px; left: 100px; }
}
	
	@-moz-keyframes move {
  from { top: 0; left: 0; }
  to   { top: 100px; left: 100px; }
}
	
	@-o-keyframes move {
  from { top: 0; left: 0; }
  to   { top: 100px; left: 100px; }
}
	
	#header_row2, #header_row1 {
		-webkit-animation: move 10s steps(10) infinite alternate;
		-moz-animation:    move 10s steps(10) infinite alternate;
		-o-animation:      move 10s steps(10) infinite alternate;
		animation:         move 10s steps(10) infinite alternate;
	}
	
}
