css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("beautifullyred.co.uk") {
  
  /* 放大 GIF 寬度*/
  .entry img[src$=".gif"], .entry .alignnone{
    width:  100% !important;
    height: auto !important;
  }
  
  /* 隱藏浮動框架 */
  .mcfloater.ads_enabled{
    display: none !important;
  }
  
  
 /* 隱藏置頂廣告 */
  body > a:nth-child(6){
    display: none !important;
  }
  
  /* 調整 logo 高度與位置 */
  #header{
    height: 65px;
    overflow-y: hidden;
  }
  
  #logo{
    margin-top: -90px;
  }

}
