css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://bbs.kafan.cn/"),
url-prefix("http://www.kafan.cn/"),
url-prefix("http://bds.kafan.cn/"),
url-prefix("http://u.kafan.cn/"){
  
  body{
   background : url(
    http://imgsrc.baidu.com/forum/pic/item/cac703d7912397dd127d549b5b82b2b7d1a2872d.jpg
   )  !important; /*背景图*/
  background-repeat : no-repeat !important;
  background-size : cover !important;
  background-position : center right !important;
  background-attachment : fixed !important;
  }
  
  /*首页透明*/
  .column, .frame-1-c, div.title, li, .comeing_index_firstarea, .cl{
    background : transparent !important;
  }
  
  
  /*论坛透明*/
  .comeing_vt_pf, .comeing_viewthread, .pls, .quote, .bm_h, .bm, th, td,
  #autopbn, #visitedforums a, div.pg a, label, input.px, div.area, div.bar,
  #ct, #e_textarea, div.comeing_mu, div.comeing_show_box, li a{
    background : transparent !important;
  }
  
  .comeing_vt_pf{border:none !important;} /*整体外边框*/
  
  
   /*首页内容区上层背景*/
  #frameOMl9bE, .area p a, #comeing_toptb.cl, .dxb_bc, .comeing_index_map li{
    background : rgba(228,228,228,0.5) !important;
  }
  
   /*论坛内容区上层背景*/
  .comeing_vt_pf, .quote, .comeing_viewthread, .bm_h, .bm, th, td,
  #autopbn, #visitedforums a, div.pg a, label, input.px, #fastpostmessage.pt,
  div.bar, #ct, #e_textarea, div.comeing_mu, div.comeing_show_box{
    background : rgba(228,228,228,0.5) !important;
  }
  
  /*首页搜索框*/
  #scbar {
    background: url("http://fj.ikafan.com//template/comeing_city/style/t14/scbar.gif")
      no-repeat scroll 0 100%  rgba(0, 0, 0, 0) !important;
  }
  
  /*首页最新资讯下知道背景图*/
  .comeing_index_fzhidao{
    background: url("http://www.kafan.cn/source/plugin/comeing_channel/template/city/zhidao.gif")
        no-repeat scroll 10px 0 rgba(0, 0, 0, 0) !important;
  }
  
  /*隐匿一些个人信息，只留下用户名、头像和发消息*/
  .pls p, .pls dl, .pls table{
    display: none !important;
  }

  /*隐匿个性签名*/
  .plc .sign{
    display: none !important;
  }
  
  /*帖子里的行距*/
  .t_f{
   line-height:20px !important;
  }
  
  /*帖子里的行距--解决一些字体加粗后引起的重叠问题*/
  .t_f strong{
   line-height:40px !important;
  }
  
  /*解决返回图标被覆盖问题*/
  div.pg a.prev {
    background-image: url('http://fj.ikafan.com/image/common/arw_l.gif') !important;
    background-position: 50% 50% !important;
  }

}
