style_setting_options.value:
/*Backgrounds*/

#phpbb, html, body,.mainleft,.mainright,.topic-actions .buttons,table.buttons{background: /*[[color]]*/ !important;}
  
.navbar,#div_chat{background: #545454 !important;}

.row,.panel,.post,.buttons,.bg1,.bg2{background: #ededed !important;}

li.row:hover{background:white !important;}

.panel h2{background: #ededed !important; color: black !important;}

blockquote{background: #d3d3d3 !important;}

/*Colors*/

a.forumtitle,a.topictitle,.linklist a{color: blue !important;text-decoration: none !important;}

.header a,.navbar a,.navbar a,.alert_text a,.alert_title{color: white !important;}

dl,#page-body,ul .linklist a,fieldset,h3,#search-box a,h3 a,.copyright a,dd strong,.content,.signature,#viewprofile dt,.details dt,.active{color: black !important;}

ul li a:not(.username-coloured),#phpbb:not(em) a,fieldset label,a:not(.username-coloured),.author,.lastpost a{color:black;}

.content a,#viewprofile dd,.details dd{color: blue !important;}

.alt1 a{color: white;}

/*Misc*/

dd.lastpost a,dt a:not(.username-coloured){text-decoration: none;}

em a{font-weight: bold;}

a.username-coloured{text-decoration: underline !important;}

h2 a{color: blue !important;font-weight: bold;text-decoration: none !important;}

.pagination a{color: blue !important;background: none !important;text-decoration: none !important;}

.post h3 a{display: none;}
