css:
@-moz-document domain("flightrising.com") {
.dragoncard div{
    text-transform:uppercase;
  	color:#cc3434!important;
	font: 8px arial!important;
	font-weight:normal!important;
	letter-spacing: 2px;
	-moz-transition-duration:1s; 
    -webkit-transition-duration:1s; 
    -o-transition-duration:1s;
	opacity:1;}

.dragoncard {
	position:relative;
	padding:0px;
	margin:0px;
	width:125px; 
	height:185px;
	border: 1px solid #ff8080!important;
	text-align: center;
	border-radius:0px!important;
	overflow:hidden;
	background: rgba(250, 250, 250, 0.7) url("http://i.imgur.com/rhlHUad.png") center 135px no-repeat!important;
	-moz-transition-duration:1s; 
    -webkit-transition-duration:1s; 
    -o-transition-duration:1s;}


.dragoncard:hover {background: rgba(250, 250, 250, 0.5) url('http://i.imgur.com/7Co4zH2.png') center 135px no-repeat!important;
	border: 1px solid #ffbfbf!important;  
  	-moz-transition-duration:1s; 
    -webkit-transition-duration:1s; 
    -o-transition-duration:1s;}
.dragoncard:hover div{color:#ffbfbf!important;}
.dragoncard:hover img{opacity:0.85; 	
  	-moz-transition-duration:1s; 
    -webkit-transition-duration:1s; 
    -o-transition-duration:1s;}
.dragoncard img{opacity:1; 	
  	-moz-transition-duration:1s; 
    -webkit-transition-duration:1s; 
    -o-transition-duration:1s;}
select{
    text-transform:none;
    letter-spacing:0px;}

}
