@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document 
    url("chrome://inspector/content/inspector.xul") ,
    url("chrome://inspector/content/viewers/dom/findDialog.xul") ,
    url("chrome://inspector/content/viewers/dom/insertDialog.xul") ,
    url("chrome://inspector/content/viewers/domNode/domNodeDialog.xul") ,
    url("chrome://inspector/content/viewers/accessibleEvents/accessibleEvents.xul") ,
    url("chrome://inspector/content/viewers/accessibleEvents/handlerHelpDialog.xul") ,
    url("chrome://inspector/content/viewers/jsObject/evalExprDialog.xul") ,
    url("chrome://inspector/content/object.xul") ,
    url("chrome://global/content/commonDialog.xul") 
{

window, dialog {
    background-color: rgb(37,44,54) !important;
    color: rgb(220,220,220)  !important;
}



/*  TOOLBARS  */
menubar > menu {
    -moz-appearance: none !important;
    background-color: rgba(0,0,0,0) !important;
    border: 0px !important;
    color: rgb(200,200,200) !important;
    transition: background-color 700ms ease 0s, color 700ms ease 0s !important;
}
menubar > menu:hover {
    background-color: rgba(0,0,0,0.3) !important;
    color: rgb(255,255,255) !important;
    transition-duration: 0ms !important;
}
menubar > menu[open="true"] {
    background-color: rgba(0,0,0,1) !important;
    color: rgb(51,159,255) !important;
    transition-duration: 500ms !important;
}
menubar {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    border-bottom: 0px solid rgb(20,20,20) !important;
    padding: 4px 5px 4px 0px !important;
}
toolbox {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    border-width: 0px !important;
}
#tbxInsToolbox {
    -moz-appearance: none !important;
    -moz-box-orient: horizontal !important;
}
#tbInspectorPrimary {
    -moz-appearance: none !important;
    -moz-box-flex: 1 !important;
    background-color: rgb(45,53,63) !important;
    border-width: 0px !important;
    width: 100% !important;
    margin: -2px 0px 0px 0px !important;
}

.viewer-title.label.toolbar {
    color: rgb(77,148,227) !important;
}
.viewer-pane-toolbox {
    padding: 0px 2px 0px 2px;
}
.viewer-pane-box-1 .viewer-pane-box-2 {
    border-left: 0px solid rgba(0,0,0,1);
    border-right: 0px solid rgba(0,0,0,1);
}
/*  ----  */



/*  AUTOCOMPLETE  */
.autocomplete-result-popupset {
    -moz-appearance: none !important;
    width: 100% !important;
}
.autocomplete-result-popupset panel[type="autocomplete"] {
    -moz-appearance: none !important;
    background-color: transparent !important;
    border: 1px solid transparent !important;
    margin: -1px 0px 0px -0px !important;
}
.autocomplete-tree.plain {
    -moz-appearance: none !important;
    margin: 0px 0px 10px 0px !important;
    border: 0px solid transparent !important;
    background-color: transparent !important;
    background-image:
            linear-gradient(to bottom,
                rgb(27,36,44),
                rgb(26,30,35)
                ) !important;
    color: rgb(204,204,204) !important;
    box-shadow:
        0px 1px 7px 2px rgba(0,0,0,0.7)
        !important;
}
#tfURLBar treechildren{
    -moz-appearance: none !important;
    cursor: pointer !important;
}
#tfURLBar treechildren::-moz-tree-row(selected) {
    -moz-appearance: none !important;
    background-color: rgb(20,20,20) !important;
}
#tfURLBar treechildren::-moz-tree-cell-text(selected) {
    -moz-appearance: none !important;
    color: hsl(208,100%,60%) !important;
}/*  ----  */







/*  INPUT TEXT  SINGLE LINE  */
textbox:not([multiline="true"]) {
    -moz-appearance: none !important;
    border-radius: 0px !important;
    padding: 3px !important;
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(255,255,255) !important;
    background-color: rgba(105,114,122,1) !important;
    background-image: linear-gradient(
                                  rgba(66,73,79,1),
                                  rgba(66,73,79,0)
                                  ) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(66,73,79,1) inset;
    transition: all 700ms ease 0s !important;
}
textbox:hover:not([multiline="true"]) {
    -moz-appearance: none !important;
    border: 1px solid rgba(164,167,172,0.5) !important;
    transition: border 500ms ease 0s !important;
}
textbox[focused="true"]:not([multiline="true"]) {
    border-color: rgb(51,159,255) !important;
    color: rgb(185,191,198) !important;
    background-color: rgba(105,114,122,1) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 0px 0px 0px rgba(66,73,79,1) inset;
    transition: all 500ms ease 0s !important;
}
textbox[disabled="true"]:not([multiline="true"]) {
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(185,191,198) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(247,185,23,0.3) inset;
    transition: all 500ms ease 0s !important;
}
#tfURLBar {
    margin: 0px 3px 0px 3px !important;
}/*  ----  */



/*  CLOSE BUTTON  */
.titled-splitter {
    border-top: 0px solid transparent;
    border-bottom: 0px solid transparent;
    background-color: rgb(45,53,63) !important;
    color: rgb(200,200,200) !important;
    box-shadow:
            inset 0px 1px 0px 0px rgba(58,70,80,0.8),
            inset 0px -1px 0px 0px rgba(0,0,0,0.4);
}
.titledsplitter-titlebox {
    margin: -1px 0px 0px 0px;
}
.titledsplitter-closebutton {
    margin: 0px;
    border: 0px solid transparent;
    padding: 0px;
    min-width: 0px;
    background-color: transparent;
 }
.titledsplitter-closeimage {
    list-style-image: url("chrome://global/skin/icons/close.png") !important;
    -moz-image-region: rect(0px, 16px, 16px, 0px) !important;
}
.titledsplitter-closebutton:hover .titledsplitter-closeimage {
    -moz-image-region: rect(0px, 32px, 16px, 16px) !important;
}
.titledsplitter-closebutton:hover:active {
    border-style: solid;
    padding: 0px 0px 0px 0px;
}
.titledsplitter-closebutton:hover:active .titledsplitter-closeimage {
    -moz-image-region: rect(0px, 48px, 16px, 32px) !important;
}/*  ----  */




/*  SPLITTER  FOR LEFT AND RIGHT  */
splitter {
    border-width: 0 0;
    -moz-appearance: none !important;
    -moz-border-left-colors: transparent transparent  !important;
    -moz-border-right-colors: transparent transparent  !important;
    -moz-border-top-colors: transparent transparent  !important;
    -moz-border-bottom-colors: transparent transparent  !important;
    background-color: rgb(37,44,54) !important;
}
grippy {
    border-bottom:  1px solid rgb(58,70,80);
    border-right: none;
    border-top:  1px solid rgb(58,70,80);
    border-left: none;
    background-color: rgba(51,159,253,0) !important;
    transition: background-color 2s ease-in-out 1s !important;
}
grippy:hover {
    background-color: rgba(51,159,253,1) !important;
    transition: background-color 300ms ease-in-out 0s !important;
}
splitter[collapse="before"] > grippy ,
splitter[collapse="after"] > grippy:-moz-locale-dir(rtl) ,
splitter[collapse="after"][state="collapsed"] > grippy ,
splitter[collapse="before"][state="collapsed"] > grippy:-moz-locale-dir(rtl) {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAABzCAYAAAC/1qMHAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAfElEQVQ4y+2VzQmAMAxGX6GbdIT25ACeHVzXaMf4vLQQKiiKF6XfJfCSkJ9D4nLOWHk63QeyQDZi7VNitZu3XiB5620pW4WxgdQqAKs34RGIDaS+DwDXdwrgnKTzaY+SRAhB07xI0hsrHGCAAQb4PLjW8eKWUn77Xx6scAcMSy8MrpYoUwAAAABJRU5ErkJggg==
                ); }
splitter[collapse="after"] > grippy,
splitter[collapse="before"] > grippy:-moz-locale-dir(rtl) ,
splitter[collapse="before"][state="collapsed"] > grippy ,
splitter[collapse="after"][state="collapsed"] > grippy:-moz-locale-dir(rtl) {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAABzCAYAAAC/1qMHAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAf0lEQVQ4y+2VQQ6FIAxEH0lvwhFk9Q/g2oPrNeAY8zdgKiZqXBom6ea1A7SLEnLOeBmdngEBoa/YobesQDJgA6YaGJCqBWBtlr2qgaklrB7WlMxn+2uTB4eXBt9LkHTd7VmSiDHqNy+S9HaEAwwwwACfAvc6b9xSymf/lxcj/ANxDSt8ivyyZgAAAABJRU5ErkJggg==
                ); }
splitter[collapse="before"] > grippy:hover ,
splitter[collapse="after"] > grippy:-moz-locale-dir(rtl):hover ,
splitter[collapse="after"][state="collapsed"] > grippy:hover ,
splitter[collapse="before"][state="collapsed"] > grippy:-moz-locale-dir(rtl):hover {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAABzCAYAAAC/1qMHAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAZElEQVQ4y+2Vyw2AQAhEh2Q7oQsL8GzhWtN4cTeQMdEYTwq3eXwCHAB432hRAEA7xNqpRS+ArXsZ4BAjLUWc1khR0od0KsTIDNuNeUm4O6d5IUlNKVCgQIEfgmuTi/vp//JgHzt73TqAxz9z4gAAAABJRU5ErkJggg==
                ); }
splitter[collapse="after"] > grippy:hover ,
splitter[collapse="before"] > grippy:-moz-locale-dir(rtl):hover ,
splitter[collapse="before"][state="collapsed"] > grippy:hover ,
splitter[collapse="after"][state="collapsed"] > grippy:-moz-locale-dir(rtl):hover {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAABzCAYAAAC/1qMHAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAW0lEQVQ4y+2VsQ3AMAgEQcombJEBUmdwMtOlimQZrEiUCVee3n6LAisgI5uUIBNkwh/hY0qne66QShM+N4WW9B1rtDAPQMyM/TgB4pEWLVq0+KF4J2zcT/8vBW6iR0JwtJSOqgAAAABJRU5ErkJggg==
                ); }
/*  ----  */
}




/*  INPUT TEXT  MULTILINE  */
@-moz-document
    url("chrome://inspector/content/viewers/domNode/domNode.xul") ,
    url("chrome://inspector/content/viewers/boxModelPlus/boxModelPlus.xul") ,
    url("chrome://inspector/content/viewers/boxModel/boxModel.xul") ,
    url("chrome://inspector/content/viewers/xblBindings/xblBindings.xul") ,
    url("chrome://inspector/content/viewers/accessibleProps/accessibleProps.xul") ,
    url("chrome://inspector/content/viewers/accessibleRelations/accessibleRelations.xul") ,
    url("chrome://inspector/content/viewers/accessibleEvents/accessibleEvents.xul") ,
    url("chrome://inspector/content/viewers/accessibleTree/evalJSDialog.xul") ,
    url("chrome://inspector/content/viewers/dom/insertDialog.xul")
{
textbox[multiline="true"] {
    -moz-appearance: none !important;
    background-color: rgb(20,23,26) !important;
    color: rgb(220,220,220) !important;
    margin: 2px 4px;
    border: 1px solid rgba(255,255,255,0.3) !important;
    -moz-border-top-colors: transparent;
    -moz-border-right-colors: transparent;
    -moz-border-bottom-colors: transparent;
    -moz-border-left-colors: transparent;
    padding: 2px 2px 3px;
    -moz-padding-start: 4px;
    transition: all 500ms ease 0s !important;
}
textbox[multiline="true"]:hover:not([disabled="true"]) {
    border-color: rgba(255,255,255,0.5) !important;
}
textbox[multiline="true"][focused="true"]:not([disabled="true"]) {
    border-color: rgb(51,159,255) !important;
}
textbox[multiline="true"][focused="true"][readonly="true"]:not([disabled="true"]) {
    border-color: rgb(255,159,51) !important;
}
textbox[multiline="true"][disabled="true"] {
    background-color: rgba(247,185,23,0.07) !important;
    box-shadow:
             inset 0px 0px 15px 0px rgba(0,0,0,1);
} 
}/*  ----  */





/*  SPLITTER  FOR TOP AND BOTTOM  */
@-moz-document 
    url("chrome://inspector/content/viewers/styleRules/styleRules.xul") ,
    url("chrome://inspector/content/viewers/xblBindings/xblBindings.xul") 
{
splitter[orient="vertical"] {
    -moz-appearance: none !important;
    -moz-border-left-colors: transparent transparent  !important;
    -moz-border-right-colors: transparent transparent  !important;
    -moz-border-top-colors: transparent transparent  !important;
    -moz-border-bottom-colors: transparent transparent  !important;
    background-color: rgb(37,44,54) !important;
}
splitter[orient="vertical"] > grippy {
    border-top: none;
    border-right: 1px solid rgb(58,70,80);
    border-bottom: none;
    border-left: 1px solid rgb(58,70,80);
}
grippy {
    background-color: rgba(51,159,253,0) !important;
    transition: background-color 2s ease-in-out 1s !important;
}
grippy:hover {
    background-color: rgba(51,159,253,1) !important;
    transition: background-color 300ms ease-in-out 0s !important;
}

splitter[collapse="before"][orient="vertical"] > grippy ,
splitter[collapse="after"][state="collapsed"][orient="vertical"] > grippy {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAfElEQVQ4y+2UsQ2AIBQF7yfu4ADSWsHe1g5gJyPQ/jWwIjGiIoaSq8mFvCOIqlJgByzgAXd3wDmHqhJjpFOHiFBoUNw/MXyQJCwQ36TGmF7zR89W+z/FvEbxJ3GSZpcYp7mnqSSE0Gz/oSC6voLza8mE27pIz1P/zbba/wDquishwo97qgAAAABJRU5ErkJggg==
                ); }
splitter[collapse="after"][orient="vertical"] > grippy ,
splitter[collapse="before"][state="collapsed"][orient="vertical"] > grippy {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAi0lEQVQ4y+2UsQ0DIQxFnyV2yAChTQV7p84A6XIjUAJjOEVykUUSJE6UvMqWrG/pf4OUUmhQU29ANP0DCKYXgBhfIzlnFmOICKpKrfWw/zvul74RDO96a0S+hAC897riGc9zlv+us8BeQehcy4fT+bKiGSSlNM1/19kTm6f9V2TnfrvKimf8m53l/xOuHSeRI6pkMAAAAABJRU5ErkJggg==
                ); }
splitter[collapse="before"][orient="vertical"] > grippy:hover ,
splitter[collapse="after"][state="collapsed"][orient="vertical"] > grippy:hover {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAbklEQVQ4y+2Uuw2AMAwFzxI7MABpqcL+E5ARMoupLKHwCQkpfbX1ZL2zLNTZgQgkYLsbUFWcPkTkd//G9CHEiIC+hYYQ3GqHz1H9P8kspaRTsIVelpiX1dU0knMe1r9UgsorKK9F/M0Of7PN/RsHF48hPXgiD9wAAAAASUVORK5CYII=
                ); }
splitter[collapse="after"][orient="vertical"] > grippy:hover ,
splitter[collapse="before"][state="collapsed"][orient="vertical"] > grippy:hover {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAeklEQVQ4y+2UsQ2AMAwE7yV2YADSUoX9J4ARMotpCLIIIIJS5iq7OUtvy6LEXL0Bi+tXILpeAGaGJMyMTh05N0m/888Md34njEe9XSSFCCCE0Lf5Y5+t8h9eBvgriC/XcjJOc19NJSmlZvnrw7wsfZT0N9v0zVbnn9kB58UrQ+H+aigAAAAASUVORK5CYII=
                ); }
}/*  ----  */


/*  TREE  LISTBOX  */
@-moz-document 
    url("chrome://inspector/content/viewers/dom/dom.xul") ,
    url("chrome://inspector/content/viewers/domNode/domNode.xul") ,
    url("chrome://inspector/content/viewers/xblBindings/xblBindings.xul") ,
    url("chrome://inspector/content/viewers/styleRules/styleRules.xul") ,
    url("chrome://inspector/content/viewers/computedStyle/computedStyle.xul") ,
    url("chrome://inspector/content/viewers/usedFontFaces/usedFontFaces.xul") ,
    url("chrome://inspector/content/viewers/jsObject/jsObject.xul") ,
    url("chrome://inspector/content/viewers/accessibleObject/accessibleObject.xul") ,
    url("chrome://inspector/content/viewers/accessibleProps/accessibleProps.xul") ,
    url("chrome://inspector/content/viewers/accessibleRelations/accessibleRelations.xul") ,
    
    url("chrome://inspector/content/viewers/stylesheets/stylesheets.xul") ,
    url("chrome://inspector/content/viewers/accessibleEvents/accessibleEvents.xul") ,
    url("chrome://inspector/content/viewers/accessibleTree/accessibleTree.xul")
{

/*  TREE HEADERS  */
tree {
    -moz-appearance: none !important;
    border: 1px solid rgb(0,0,0) !important;
    color: rgb(200,200,200) !important;
    background-color: rgb(19,28,38) !important;
    background-color: rgb(20,23,26) !important;
}
treecol,
treecolpicker {
    -moz-appearance: none !important;
    border: 0px solid;
    border-bottom: 1px solid;
    border-right: 1px solid rgb(0,0,0) !important;
    -moz-border-bottom-colors: rgb(0,0,0) !important;
    background:
            linear-gradient(to bottom, 
                rgba(56,70,82,1) 0%,
                rgba(35,39,48,1) 100%) !important;
    color: white;
    padding: 0px 4px;
    height: 18px;
}
treecolpicker {
    border-right: 0px !important;
}
#trDOMTree treecol:last-child:not([hidden="true"]) {
    border-right: 0px solid transparent !important;
}
#trDOMTree treecol:hover {
    background:
            linear-gradient(to bottom, 
                rgba(194,116,0,1) 0%,
                rgba(185,49,0,1) 100%) !important;
}
#trDOMTree treecol:hover:active {
    background:
            linear-gradient(to bottom, 
                rgba(224,146,32,1) 0%,
                rgba(215,79,37,1) 100%) !important;
}
treecol[dragging="true"] {
    background:
            linear-gradient(to bottom, 
                rgba(32,146,224,1) 0%,
                rgba(37,79,215,1) 100%) !important;
}
treechildren::-moz-tree-column(insertbefore) {
  -moz-border-start: 1px solid rgba(215,79,37,1) !important;
}
treechildren::-moz-tree-column(insertafter) {
  -moz-border-end: 1px solid rgba(215,79,37,1) !important;
}
.tree-columnpicker-icon {
    list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAMCAYAAABSgIzaAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPElEQVQoz2NgoDdghDH+////n6BiRkZGDEF0jYT49LeRheJQIsapRNuIzTCYP3EGDkwBsjjWwKGmN/ACAA6wM+xPEB+mAAAAAElFTkSuQmCC
                );
}/*  --  */




/*  TREE LIST  */
treechildren::-moz-tree-row {
    background-color: transparent !important;
    overflow-y: scroll  !important;
}
treechildren::-moz-tree-row(selected) {
    background-color: rgba(51,159,253,0.15) !important;
}
treechildren::-moz-tree-cell-text(selected) {
    color: rgb(51,159,253) !important;
}
treechildren::-moz-tree-row(current, focus) {
    border: 1px solid rgb(2,83,155) !important;
}
treechildren::-moz-tree-row(hover) {
    background-color: rgba(51,159,253,0.15) !important;
}

tree[seltype="cell"] > treechildren::-moz-tree-line,
tree[seltype="text"] > treechildren::-moz-tree-line,
treechildren::-moz-tree-line {
    border: 1px dotted rgba(255,255,255,0.3) !important;
}
tree[seltype="cell"] > treechildren::-moz-tree-line(active, selected, focus),
treechildren::-moz-tree-line(selected, focus) {
    border: 1px dotted rgba(0,255,255,1) !important;
}

treechildren::-moz-tree-twisty {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAYklEQVQY033PywmAQAxF0RtwKQo2qFiZWy1JwRa0AIfnxoFhiHm7wMkPikiaJQ04aaq6A0ZJi5ldEQToganGHnTxH8w4n3FHEKAFBiCED7Ca2RmtzuiInknAViIPpm/SXne/bAklqD8BRaEAAAAASUVORK5CYII=
                ) !important;
    -moz-padding-end: 4px;
    padding-top: 1px;
    width: 9px; /* The image's width is 9 pixels too*/} 
treechildren::-moz-tree-twisty(open) {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPElEQVQY02P8//8/AzGAhRhFjCd+/WchRhFBE2GK8CpEVoRTIboirAqxKcJQiEsRikJ8ihgYGBgYiQ1wAJ56HC2vHF1bAAAAAElFTkSuQmCC
                ) !important;
    width: 9px; /* The image's width is 9 pixels too*/}
treechildren::-moz-tree-checkbox(checked) {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAQklEQVQI13WM0QnAUAwCL5CN3H+Dl5muX4FSXgVBPLRUbpoZ+w8A9MyYpL4AoLdIUm+QpPq22KdSrwAAFZVzjpvXD72LOZBnLCA/AAAAAElFTkSuQmCC
