css:
@-moz-document domain("anonyme.com") {
/* Style Author  : Seann            */
/* Style Version : v1.0.0           */
/* Style Updated : 05-09-2014       */

/* BASE TAGS */
body {
  background-color: #111 !important;}

footer {
  background-color: #333 !important;}  

ul.tagit, ul.tagit li.tagit-choice {
  border: 1px solid #555 !important;
  background-color: #252525 !important;
  color: #888 !important;}

ul.tagit input[type="text"] {
  background-color: #252525 !important;
  color: #888 !important;}
  
/* IDs */
#layout-header-container {
  background-color: #333 !important;}

#layout-menu-seperator {
  background-color: #252525 !important;}

#layout-search-input{
  background-color: #252525 !important;
  color: #888 !important;
}

#layout-shadow-box {
  background-color: #333 !important;}

#myblog-statistics-box, #myblog-statistics-bottom-container, #footer-links-container {
  border-top: 1px solid #555 !important;}

#myblog-newpost-title-input, #myblog-newpost-input {
  border: 1px solid #555 !important;
  background-color: #252525 !important;
  color: #888 !important;}

#myblog-newpost-box{
  background: none !important;
  border-bottom: 1px solid #555 !important;}

#getstarted-title {
  background-color: #444 !important;}

/* CLASSES */
.blue-link, .blue-link a, .gray-link, .gray-link:hover, .black, .myblog-whotofollow-name, .myblog-whotofollow-name:active, .myblog-whotofollow-name:visited, .recent-post-name a, .help-section-content:hover, .help-link, .help-root-cateegory a, .help-root-cateegory a:visited, .featured-category a, .me-stats-post-title a {
  color: lightblue;}

.siteblog-recent-entry-title, .siteblog-recent-entry-title a {
  color:#888; }

.activities-activity td, .border-top, .toolbox-container {
  border-top: 1px solid #555 !important;}

.activities-activity:hover {
  background-color: #333 !important;}

.getstarted-feature {
  background-color: #444 !important;
  color: #888 !important;}
  
.layout-menu-item {
  color: #888 !important;}

.layout-menu-item-selected {
  color: #555 !important;}
  
.gray-link:hover {
  color: #00A5E0 !important;}
        
.blue-link:hover, .blue-link a:hover, .me-profile-action:hover {
  text-decoration: none;
  color: #00A5E0 !important;}

.activities-activity-content {
  color: #888 !important;}

/* MISC */
#help-content-categories, #messages-title, #featured-categories, #mobile-head, #following-title, .following-profile-collapsed, #featured-tagscloud, #mobile-features {
  border-bottom: 1px solid #555 !important;}

hr {
  border-top: 1px solid #555 !important;}
}
