#header_menu {
  background-color: #9eeebd;
  border-radius: 50em/150em;
}

#header_menu ul li a {
  text-shadow: 0.1em 0.1em 0.2em #7ee8a8;
}

#header_menu ul li a:hover {
  background: #c7fadb !important;
  text-shadow: 0.1em 0.1em 0.2em #9eeebd;
}

#content_holder {
  background: rgba(181, 222, 244, .86) !important;
  border-radius: 15px !important;
}

