  color: #3093c1;
}

a:hover {
  color: #eef4f7;
}


body {
  background: url(http://i62.tinypic.com/14uywhx.png) !important;
  background-attachment: fixed !important;
}

#main_content_holder {
  background: url(http://i62.tinypic.com/14uywhx.png) !important;
  background-position: 50% 50% !important; 
  background-attachment: fixed !important;
}

div.row1 {
  border-bottom: 1px #5aaed6 solid;
}

