  background: rgba(191, 250, 219, 0.5);
  border-radius: 6px;
}

input.button {
  background: -webkit-gradient(linear, left top, left bottom, from(#eef4f7), to(#b5c9df)) !important;
  border: 1px solid #8dacce !important;
  box-shadow: 2px 2px 2px #7399c2;
  -webkit-box-shadow: 2px 2px 2px #7399c2;
  text-shadow: 0 1px 1px #7399c2;
}

#header {
  border-bottom: 3px solid #e2feff;
  background-image: url(http://i58.tinypic.com/5dms1d.png) !important;
  background-position: 0% 55%;
  height: 150px;
  border-bottom: 4px solid #def1fa;
}

#header_holder {
  height: 120px;
}

#right .block .title {
  background-color: #89cdee;
  text-shadow: 0.1em 0.1em 0.2em #40a1cf;
  border-bottom: 1px solid #40a1cf;
  border-top-right-radius: 14px;
}

#left .block .title {
  background-color: #d8e6ed;
  text-shadow: 0.1em 0.1em 0.2em #8dacce;
  border-bottom: 1px solid #8dacce;
  border-top-right-radius: 14px;
}

