css:
@namespace url(http://www.w3.org/1999/xhtml);
/*本样式来自https://userstyles.org/styles/102582/qidian*/
/*2014/12/11有问题到贴吧@幽理之刻*/
/*A版*/
@-moz-document domain("qidian.com"), domain("big5ploy.qidian.com"),
domain("big5.qidian.com"),domain("read.qidian.com"){

/*ALL*/
    
    html,#form1{background-color: #252C33;}
    
/*最上方*/

    
    /*隱藏商城ˋ隱藏遊戲*/
    span.af:nth-child(5),
    #newheadyx,
    #newheadbz
    {display:none !important;}
    
    
    /*最上方灰色背景*/
    .user_opt,div.clearfix:nth-child(3),#qd_reader_header .user_opt,.newheadcss  {
        background: none;
        background-color:#D9D6CF;
    }
    .user_opt{
        box-shadow: 0px 5px 5px #888888;
    }
    
    
    #qd_reader_header a {
    color: #000;
    text-decoration: none;
    }
    
    
    #spanNewMsgCount{
        color:#00f !important;
    }

    
    /*青色欄*/

    .reader_nav{
        background-color: #E4ECF7;
        border-radius:25px;
        box-shadow: 3px 3px 5px #BEBEBE;
    }

    #qd_reader_header {        
        margin-bottom: 12px ;
    }
    
    .page_site{
        margin-left: 7px;        
    }
    
    .reader_nav .opt{
        margin-right: 7px;  
    }

    #readerheader{
        margin-left: 4%;
        margin-right: -4%;
    }


    /*隱藏青色欄右方不用的東西*/
    
    .reader_nav .opt{color:#E4ECF7;}
    
    .reader_nav 
    #aaddbookshelftop,    
    #bookUrl,
    #spanBackToChapterList > a:nth-child(1),    
    .reader_nav > div:nth-child(2) > span:nth-child(4)
    {color:#000;}
    
    
    #aaddbookshelftop:before{font-size: 11pt;content:"加入书架";}
    #aaddbookshelftop{font-size: 0pt;}
    
    
    .reader_nav > div:nth-child(2) > a:nth-child(1),
    .reader_nav > div:nth-child(2) > a:nth-child(2)
    {display:none !important}


   
    /*小說閱讀區*/
    #readercontent{
        background-color: #E4E1D8;
        border-radius: 25px;
        box-shadow: 5px 10px 15px #BEBEBE;
        margin-bottom: 40px;
      }

    #maincontent{max-width:100%}
    #bookreader .bigcontbox .office_inner,
    #bookreader #bigcontbox{margin:0 auto !important}

    #maincontent{margin: 0px -230px -80px 0px;}
    #bookreader{
        margin-bottom:-90px
    }
    
    #bookreader .bookcontent{
        padding-right:5%;
        padding-left:5%;
        font-size: 12pt !important;
    }
    

    
    /*去除最後一行廣告*/
    #maincontent > #content > div,
    #content p:nth-last-of-type(1) a[href="http://www.qidian.com"],
    #content p:nth-last-of-type(1) a[href="http://www.qidian.com"]+a
    {display:none !important}
    
    
    /*閱讀區下方*/
    .bottom_opt .bookopt .pageopt{border-bottom: 0px dashed #CCC;}
    
    /**/
    .bottom_opt .useropt {display:none !important;}
    /*
    .bottom_opt .useropt {border-top: 1px dashed #CCC;padding:6px 0 8px 0;} 
    */

    
    
    /*已閱讀顯示灰色*/
    .bottom_opt .pageopt a:visited,
    .bottom_opt .pageopt a:visited *,
    .bottom_opt .pageopt a:active,
    .bottom_opt .pageopt a:active *
    {color: gray  !important ; }
    
 
    
    /*閱讀頁去廣告*/
    #form1 >.fixed_page+div[height="0"] *[style*="z-index"][style*='position: absolute'] ,
    #spanShangchengFooter,
    div.box_title:nth-child(10),
    #content > span,
    ins,
    #divUserGuide,
    #maincontent > #content > div,
    .show_ggpic_945X90,
    #sykzAdTestReadTop,
    .ma_ico,
    #pnlVipReadNote,
    div.box_title:nth-child(15),
    #two_dimensional,
    .normallink,
    .reader_right_tools,
    .all_tj,    
    #page_set,
    #author_android,
    #reader_zancb,
    #reader_zancenter,
    #bookreader  #sidecontbox
    {display:none !important;}
    


    

    
    /*小說目錄*/
    
    #bookdirectory{
        background-color: #E4E1D8;
        border-radius: 15px;
        box-shadow: 5px 10px 12px #BEBEBE;
        margin:0px 0px 50px 0px;
        padding:0px 15px 0px 15px;
      }
    
    #bookdirectory .booktitle{
        margin: 0px 0px -10px 0px;
    }
    
     #bookdirectory .booktitle > h1:nth-child(1){
        margin: -10px 0px 0px 0px;
    }
        
    #bigcontbox{
        margin-bottom: 10px;
    }
    
    
    
    /*下載小說-查看詳細-轉為黑色字體*/
    #bookdirectory .like_box .box_title h3 span.elinkdetail a {color: black ;font-weight:normal;}
     
    /*快車下載條左方*/
    #bookdirectory .bigcontbox .book_opt .opt {
      position: relative;
  right: 20px;
}

    /*目錄去廣告*/
    #ad_continer_1,
    #DoublePop_left,
    #DoublePop_right,
    #ifmfooter,
    .book_op,
    #down_3g_20130621,
    .shop_tj,
    #sidecontbox > div:nth-child(1) > div:nth-child(7),
    div.box_title:nth-child(12),
    .game_tj,
    div.box_title:nth-child(14),
    .opthelp,
    #downsidecontboxdiv > div:nth-child(6),
    #downsidecontboxdiv > div:nth-child(8),
    #downsidecontboxdiv > div:nth-child(9),
    div.show_ggpic_990X60:nth-child(2),
    .sao_mobile_box,
    #div_10015082
    {display:none !important}
    
    
    /*已閱讀顯示灰色*/
    .zj_list .box_cont  a:visited ,
    .zj_list .box_cont  a:visited *,
    .zj_list .box_cont  a:active,
    .zj_list .box_cont  a:active *
    {color: gray  !important ; }
    
    
}


@-moz-document domain("qidian.com") {
    
    
    #DoublePop_left,
    #DoublePop_right,
    .qdRead_client,
    .RightAD
    {display:none !important}
      
    
}
/*[[是否去除书架页-剩余可藏书籍]]*/


/*點娘書架*/
@-moz-document domain("me.qidian.com") {

    /*背景*/
    html,body,        
    .post-field,
    .content-body-all,
    .column-wild-content{
    background:none;
    background-color: #E4E1D8;
    }
    
    
    /*閱讀區置中 */   
    .fix-layout .fl-right-wrap {margin:0 auto;float:none;}    
    

    /*搜書右移*/
    #search_floater  img{text-align:right;}
    
    #search_floater  ul > li:nth-child(2){
        float: right;
        text-align:right;
        position: relative;
        left: -10px;    }
    

    /*青色導航*/
    .bookcaseSubLine{
        background-color: #E4ECF7;
        border-radius:25px;
        box-shadow: 3px 3px 3px #888888;
    }
    

    #case_1,#case_2,#case_3{ font-size:0px; }
    
    #case_1:before{content:"书架1";font-size:12px;}
    #case_2:before{content:"书架2";font-size:12px;}
    #case_3:before{content:"书架3";font-size:12px;}


    
    /*書架*/
    .fl-right-wrap{ background-color: #E4E1D8;}
   
    /*顯示剩餘藏書數目*/
    .r{margin:-25px 10px 0px 0px;}
       

    /*已經閱讀的顯示紫色*/

    .bookcaseTable td:nth-child(3) > a:nth-child(3):visited,
    .bookcaseTable td:nth-child(3) > a:nth-child(3):visited *,
    .bookcaseTable td:nth-child(3) > a:nth-child(3):active,
    .bookcaseTable td:nth-child(3) > a:nth-child(3):active *
    {color: #7600AF  !important ; }   
    
    
    /*左方美化*/
    
    .fix-layout.column-wild-content{width:1130px;}
    
    .column-wild-content > .fl-alpha{       
        position: fixed;
        top:15%;
        border: 1px solid #CCC;
        border-radius:8px;        
        padding:0px 0px 2px 0px ;  
        background:#E3DED3;
        width: 130px;      
    }
    

  
    .fl-alpha li{border:1px  solid  #E3DED3;}
    .fl-alpha li:hover{ 
        background:white;
        border-radius:5px ;
        border:1px  solid  #CCC;
    }
    

    
    .app-list               { margin-top: -8px ; }
    .app-list ol li:hover   { background:#E3DED3; }    
    .app-list li.hasub a.ch { border-radius:5px 0px 0px 5px; }
    
    

    
    #liBookcase > ol,
    #liMyQidian > ol  {   
        border-radius:0px 5px 5px 5px;
        padding-top:3px;
        padding-bottom:3px;
    }
    
    #liMyQidian,
    #liBookcase,
    #liBookcase:hover,
    #liMyQidian:hover
    {border:0px ;}
    
 
    #ulSystemMenu > li > a:before{ content:"●";  }
    #ulSystemMenu > li:nth-child(6) > a:before{ content:"● ";}
    
    #liMyQidian  img:nth-child(2),
    #ulSystemMenu  > li > a > img:nth-child(1)
    {display:none !important}
    
    .app-list li.hasub {background-position: 96px 10px;}
    

    /*左方內部美化*/
    .app-list li ol {
        width: 110px;
        position: absolute;
        left: 109px;
    }
    
    #liMyQidian > ol > li:nth-child(14) > a:nth-child(1) {font-size:0px;}
    #liMyQidian > ol > li:nth-child(14) > a:nth-child(1):after {content:"麦库云记事";font-size:14px;}
    
    
    #liMyQidian > ol > li:nth-child(4),
    #liMyQidian > ol > li:nth-child(7),
    #liMyQidian > ol > li:nth-child(8),
    #liMyQidian > ol > li:nth-child(9),
    #liMyQidian > ol > li:nth-child(10)
    {display:none !important}
    
    
    /*all*/
    .ad-shell,
    #footer,
    #depositFloater_iframe+div:not(.me_login),
    .shell_tuijian,
    .sub-bar-rightside    
    {display:none !important}
    
    
    /*右上*/
    #span_help,
    #span_webGame,
    #span_webGame+span
    {display:none !important}
    
    /*左方*/
    #ulSystemMenu > li:nth-child(9),
    .alpha-split-line,    
    #liTaoJin,
    #ulSystemMenu > li:nth-child(7),
    #ulSystemMenu > li:nth-child(8),
    #ulUserMenu,
    .app-more-link,
    .space-block,
    #divShopRecommend,
    #divShopRecommend+div,
    .game_info
    {display:none !important}
    

    /*下方美化*/
    .bookcaseIntro > dd:before{ content:"("; }
    .bookcaseIntro > dd{ text-indent: 15px; }



}

/*閱讀完畢頁面*/
@-moz-document 
url-prefix("http://read.qidian.com/BookReader/LastPage.aspx?bookId="){
    
   
    
    .last_page{background-color: #E4E1D8;}
    
    .rank_list .ranks_box .title {
        background:none;
        background-color: #E4ECF7;
        box-shadow: 3px 3px 5px #BEBEBE;
    }

     
    .rank_list .ranks_box .title .right a.active{color:#004E00;}
    
    .last_news .box_title {
        background:none;
        background-color: #F1F0F0;
    }
    
    .lastpageA img:nth-child(1),
    .last_page > div:nth-child(2),
    .last_page_footer{display:none !important}
    

   #yesLogin:before,
    .last_news > div:nth-child(1) > h3:nth-child(1):before{
        content:"▌";
        color:#EA4801;
    }
    
    
    .last_page_header .useropt{
        display:inherit !important;
        background:none;
        background-color: #E4E1D8;
    }
   
}



/*分卷閱讀頁*/
@-moz-document 
url-prefix("http://www.qidian.com/BookReader"),
url-prefix("http://www.qidian.com/BookReaderOld"){
    
    /*整體挪動*/
    #leftmenu{
          position: relative;
          left: 40px;
    }
    
    /*粉紅欄裡的*/
    .nav,
    .navb,
    #content > div,
    .chat
    {
        background:#E5DBDA;
        width: 100%;
        border-top: 1px solid #E5DBDA;
    }
    .nav    {
        width: 99%!important;
    }
    
    
    /*各章節標題*/
    #content  a    {
        font-size: 18pt !important;
        color: #900 !important;
    }
    
    #content  div{        
        color: #999 !important;
    }
    
    
    
    /*黃色*/
    #content,#form1    {
        background:#EDE9E0;
    }
    
    /*背景灰色*/
    html  {
        background:#CCCCCC;
    }
    
 
    /*其他*/
    .nav > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > a:nth-child(3),
    .nav > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > a:nth-child(4)
    {color:black !important;}
    
    #ColorSetting,
    #showTB
    {display:none !important}
    

}

@-moz-document url-prefix("http://read.qidian.com/BookReader/"){
.bookreadercontent{
    line-height: 1.4 !important;
    }
.bookreadercontent p {
    line-height: 1.4 !important;
    margin: 0;
    }
p {
    margin: 0;
    }
}

@-moz-document url-prefix("http://m.qidian.com/book/"){
.rdcontent16,.rdcontent16 p{
    font-size: 13pt !important;
    line-height: 1.3 !important;
    margin: 0;
}
.rdcontent18,.rdcontent18 p{
    font-size: 14pt !important;
    line-height: 1.3 !important;
    margin: 0;
}
.rdcontent20,.rdcontent20 p{
    font-size: 15pt !important;
    line-height: 1.3 !important;
    margin: 0;
}
.rdcontent22,.rdcontent22 p{
    font-size: 16pt !important;
    line-height: 1.3 !important;
    margin: 0;
}
.rdcontent24,.rdcontent24 p{
    font-size: 17pt !important;
    line-height: 1.3 !important;
    margin: 0;
}
p{
    margin: 0;
}
}

/*全文閱讀修復...這裡真的有人來嘛？*/
@-moz-document url-prefix("http://read.qidian.com/BookReader/ReadChapterFlash.aspx?Bookid="){
    #form1
    {
        background:#EDE9E0;
    }
}

/*恢復點娘標籤*/
@-moz-document url-prefix("http://www.qidian.com/Book/"),
url-prefix("http://big5.qidian.com/Book/")
{
   #authorRecommendDiv+div > div[style="display: none;"]    {display: inline!important;}

}
@-moz-document url-prefix("http://free.qidian.com/Free/ReadChapter.aspx"){
    
         /*...背景*/
    html,body,
    .readbg_tinge .read_up .read_title,
    .base_layer_up_layout .base_up > div ,
    .readbg_tinge .book_read .site 
    {    background: url('http://free.qidian.com/Free/Style/read_skin/bg/tinge/body_bg.png') repeat scroll 0px 0px transparent; }

    .readbg_dark .book_read .site 
    {    background: url('http://free.qidian.com/Free/Style/read_skin/bg/dark/body_bg.png') repeat scroll 0px 0px transparent;   }

    
     /*夜間階梯*/
    .readbg_dark .book_read .site .site_rect{
        padding:0px 20px 0px 20px;
        background-color: #454545;
        border-radius:8px;
    }
        
    .readbg_dark  .read_site .site_rect
    {   width: 820px; }

    
    
    
    /*懸浮條*/
    .book_read .read_box .left_tools {      
        margin-top: 0px;
        position: fixed;
        top: 30%;
        left: 45%;
    }
    .book_read .read_box .right_tools {        
        margin: 0px 0px 0px 450px ;
        position: fixed;
        top: 30%;   
    
    }

    
    .goto_top {
        position: fixed;
        margin:0 0 0 0 ;
        right: 100px;
        bottom: 50px;
    }
    

    /*消息顏色*/
    #spanNewMsgCount{color: #DA1A27;margin-left:3px;}


  
    /*閱讀區*/
    
    
    .read_box .zhangjie .chapter .cont   {font-size: 18px; }    
    .book_read .read_box .zhangjie       {width: 850px;}
    .read_box .zhangjie .chapter .show   {width: 90%;}
    
    

    /*標題*/
    
    .book_read .read_box .zhangjie  {width: 850px;}    
    .read_box .zhangjie .chapter .show {width: 90%;}

    
    .readbg_tinge .read_box .zhangjie .chapter .title h3,
    .readbg_dark  .read_box .zhangjie .chapter .title h3 
    {  font-size: 26px;  }
    
    /*白日標題*/
    .readbg_tinge .read_box .zhangjie .chapter .title   {  text-align:center;  border-width: 0px ; }
    .readbg_tinge .read_box .zhangjie .chapter .title h3{ color: #D32E39;   }
    .readbg_tinge .read_box .zhangjie .chapter {  background-color: #EDEDE5;    }
    
    
    /*夜間標題*/
    .readbg_dark .read_box .zhangjie .chapter .title {
        border-color: #2D2D2D;
        outline: 1px solid #464646;
        background-color: #454545;
        border-radius:5px;
        text-align:center;
    }
    
    
  
    
    /*目錄*/
    /*目錄標題*/
    
    .read_up .read_title .left b {font-size:35px;}
    
    .readbg_tinge .read_dir_listbox .box .title { background:#EDE8D8 ; }
    .readbg_dark  .read_dir_listbox .box .title { background:#252729 ; }
    

    /*已讀變色*/
    .readbg_tinge  .read_dir_listbox .box .list ul li   a:visited, 
    .readbg_tinge  .read_dir_listbox .box .list ul li   a:visited *,
    .readbg_tinge  .read_dir_listbox .box .list ul li   a:active, 
    .readbg_tinge  .read_dir_listbox .box .list ul li  a:active *
    {color: #7600AF !important  ; }   

  
    .readbg_dark .read_dir_listbox .box .list ul li    a:visited,     
    .readbg_dark .read_dir_listbox .box .list ul li    a:visited *,
    .readbg_dark .read_dir_listbox .box .list ul li    a:active, 
    .readbg_dark .read_dir_listbox .box .list ul li    a:active *
    {color: #745D40  !important; }
    
    
    .readbg_tinge  .read_box .page   a:visited, 
    .readbg_tinge  .read_box .page   a:visited *,
    .readbg_tinge  .read_box .page   a:active, 
    .readbg_tinge  .read_box .page   a:active *
    {color: #7600AF !important ; }
    

    .readbg_dark  .read_box .page   a:visited, 
    .readbg_dark  .read_box .page   a:visited *,
    .readbg_dark  .read_box .page   a:active, 
    .readbg_dark  .read_box .page   a:active *
    {color: #745D40 !important ; }
    
    
    /*目錄*/
    .readbg_dark a, a:link { color: #A6A6A6; }
    
    .readbg_dark  .read_dir_listbox .box .list ul li.active 
    {background: url('http://free.qidian.com/Free/Style/read_skin/bg/tinge/bk_chapter_list.png') no-repeat scroll 0px 0px transparent;}

    
    .readbg_tinge .read_dir_listbox .box .list ul li.active a span {color: #BE5252 !important;}
    .readbg_dark  .read_dir_listbox .box .list ul li.active a span {color: #BE5252 !important;}
   

    
    
    
    
    /*無用去除*/
    #ifmfooter,
    div.right:nth-child(3) > div:nth-child(4),
    div.right:nth-child(3) > div:nth-child(5),
    div.right:nth-child(3) > div:nth-child(6) 
    {display:none !important}
    

    .func > ul > li:nth-child(1),/*打賞按鈕*/
    body > *:not(.read_site):not(.goto_top):not(#base_layer_up_box),
    .book_read > div:nth-child(1),
 /*   #content > p:nth-last-of-type(1) > a ,*/
    .loaded > .ggbox,
    .nice_books,
    .done_books
    {display:none !important}
    
    

}
@-moz-document url-prefix("http://free.qidian.com/Free/ChapterList.aspx?bookId="){
    
    html,body,
    .newheadcss
    {    background: url('http://free.qidian.com/Free/Style/read_skin/bg/tinge/body_bg.png') repeat scroll 0px 0px transparent; }

    
    body > *:not(.container),
    .top_pic,
    .bottom_pic
    {display:none !important}
    
    
}
@-moz-document domain("www.qidian.com"),url-prefix("http://c.pingba.qidian.com/BookComment"){
html,#form1,iframe#frmBookReview
{background-color: white!important;}
}

@-moz-document 
url-prefix("http://down1.qidian.com/bookall"),
url-prefix("http://down2.qidian.com/bookall"){
    
    /*各章節標題*/
    #content  a    {
        font-size: 12pt !important;
    }
 
.content {
    font-size: 12pt !important;
    font-weight: normal;
    line-height: 150% !important;
    text-align: left;
}
.TitleTR {
    background-color: #dbedf5;
    cursor: pointer;
    font-size: 12pt !important;
    height: 20px;
    padding-left: 10px;
    padding-right: 5px;
}
.zt {
    border-bottom: 1px solid #c5d2e0;
    color: blue;
    font-family: "宋体","verdana","Arial","MS Sans Serif";
    font-size: 12pt !important;
    line-height: 150% !important;
    padding-bottom: 10px;
    padding-top: 10px;
    text-align: center;
}
.xt {
    font-family: "宋体","verdana","Arial","MS Sans Serif";
    font-size: 12pt !important;
    line-height: 150% !important;
}

    /*各章節標題*/
    #content  a    {
        font-size: 12pt !important;
