css:
@-moz-document regexp("http://www.anitube.se.*") {
body {background-image: url(http://dl.dropbox.com/s/ibbwuyuvdbupoqi/background.jpg);}
#header {background:none!important;}
#viewBox ul.tabs-nav{background:none!important; box-shadow:none;}
.mainBoxTitle{box-shadow:inset 0px 0px 20px #000;background:none;margin-bottom:0px;}
#mainContent ul{margin-top:1px!important;}
.sidebarTitle,.cometchat_tabpopup{background:none!important;}
.sidebarBox{border:none;}
#viewBox{border:none!important;}
.mainBox{border:none!important;}
#mainContent ul, #textBoxInfo {background: none;background-color: transparent; box-shadow:inset 0px 0px 20px #000;}
.videoInfo{color: #fff !important;font-size:11px!important;}
.videoTitle a{color:#fff;font-size:12px!important;text-align:center;}
.videoTitle a:hover{color:#c24767;}
.tabs-nav a:hover span{color:#c24767;}
 .tabs-nav a{text-decoration:none!important;}
.videoThumb{border:2px solid #000}
.videoThumb:hover{border:2px solid #fff}
.moreLink a{text-decoration:none !important;}
.midLink{color:#fff;font-size:1em;margin-top:-10px;}
#footer{border:none;background:none;margin:none;float:left;}
#footer ul li{font-size:14px; margin: 3px 10px 0 1px;}
#footer ul li a{box-shadow:inset 0px 1px 20px #000;padding:5px;}
#footer ul li a:hover{box-shadow:inset 0px 1px 40px #c24767;padding:5px;text-decoration:none;}
#footer ul{float:left;}
p.textFooter{font-size:0px;}
#mainNavigation{background:none !important;}
#smallNavigation{background:none;}
ul#topNavigation li a{background:none;box-shadow:inset 0px 0px 40px #000;}
ul#rightNavigation li{background:none;}
ul#bottomNavigation li{background:none;}
ul#topNavigation{background:none;}
ul#topNavigation li:hover{background:none;box-shadow:inset 0px 0px 40px #ffffff; color:white!important;}
ul#topNavigation li.topNavHome:hover{background:none;}
ul#rightNavigation li a:hover{text-decoration:none;}
ul#bottomNavigation li a:hover{text-decoration:none; color:#c24767;}
ul#bottomNavigation li{box-shadow:inset 0px 0px 40px #c24767;margin-top:-3px;}
.anylinkmenu{border:none!important; background:none!important;width:120px;}
.anylinkmenu ul li a{box-shadow:inset 0px 0px 70px #000;border: 1px solid #000;padding:5px;}
.anylinkmenu a:hover{background:#c24767;}
ul#language li a{font-size:14px; font-weight:bold; text-align:center;border:none!important;text-decoration: none;color: #c24767;}
.tabs-nav li{background:none; box-shadow:inset 0px 0px 20px #000;}
#mainNavigation{border-bottom: 2px dashed #000;border-top: 2px dashed #000;}
.tabs-nav .tabs-selected a{background:none; box-shadow:inset 0px 0px 20px #c24767}
.tabs-nav .tabs-selected a:link, .tabs-nav .tabs-selected a:visited, .tabs-nav .tabs-disabled a:link, .tabs-nav .tabs-disabled a:visited{cursor:auto;}
.searchBox2 input{border:2px solid #333;}
#searchBox button{background: url(http://dl.dropbox.com/s/7642hqzkspeg230/search.png)!important;}
#searchBox{}
.sidebarInfo{box-shadow:inset 0px 1px 20px #000;background:none;background-color:none;}
li.optionLink{color:white;}
li.optionLink a{color:#c24767;}
.moreSidebar a{text-decoration:none;font-weight:bold;color:#c24767;}
li.statPublicv, li.statPrivatev, li.statMembers, li.statWatchv, li.statVisitors{color:#c24767;}
p.textSmall{color:#c24767;font-weight:bold;}
.tabs-container{background:none;border-top:none;}
.mainBoxTitle{border:none;font-size:14px;}
h2.mainBoxHeader, h1.mainBoxHeader{color:#c24767;font-size:16px;}
h3.sidebarBoxHeader{color:#c24767;font-size:14px;font-weight:bold;}
ul#rightNavigation li.rightNavUpload a:hover{color:#c24767;}
#mainNavigation a:hover{color:#c24767;}
#smallNavigation{border-bottom: 2px dashed #000;margin-top:5px!important;}
#smallNavigation a{color:#fff;}
.fbFeedbackContent.fbDarkWidget .composer{background-color:none!important;border:none;}
.uiBoxDarkgray{background:rgba(35, 30, 34, 0.6);}
table#tableForm td.smallCol2{background:none;}
table#tableForm td.smallCol{background:none;}
table#tableForm td.smallCol3{background:none;}
p.viewVideoText{color:#fff;}
p.viewVideoTags{color:#fff;}
#anuncios-uol a.AdBox, #anuncios-uol a.AdBox:visited,li#videoPlayer{box-shadow: inset 0px 0px 20px #000!important;}
#pageAdvert {border:none;}
#footerAdvert{border:none;}
#cometchat_userstabm,#cometchat_optionsbutton,.cometchat_tab{border:none;}
.cometchat_tabclick{color:#fff!important;border:none!important;}
font{visibility:hidden!important;}
a{color:#c24767;}
.moreLink{float:right;margin-right:10px;font-size:0.9em;color:#fff;}
li.videoDesc{width:auto;font-size:11px;margin-top:-15px;}
li.videoRate{padding:initial;width:auto;color:#fff;margin-bottom:5px;margin-right:10px;}
#logo{margin-top: -20px;}
#mainContent ul{margin-top:10px;}
#cometchat_userstab{border:none;width: 181px!important;}
.cometchat_userstabtitle,.cometchat_statusinputs,.cometchat_tab{background:rgba(255, 255, 255, 0.2) !important;}
.cometchat_tabsubtitle{background:#000;border:none;color:#fff;border-bottom: 2px solid #c24767;}
.cometchat_userstabtitle_available{border-bottom: 2px solid #c24767;}
.cometchat_tabcontent{background:rgba(0, 0, 0, 0.7);border:none;color:#fff;}
#cometchat_searchbar{background:#333}
.cometchat_tabcontent a{color:#c24767!important;}
.cometchat_statusinputs{border: 1px solid #c24767;margin-top:1px;}
.viewVideoThumb{box-shadow: 0px 0px 10px rgba(0, 0, 0, 1);}
p.viewVideoText a{color:#c24767!important;text-decoration:none;}
.fbFeedbackContent.fbDarkWidget .composer{background-color: rgba(0, 0, 0, 0.4);}
p.viewVideoText a:hover{color:#fff!important;}}
