css:
@-moz-document domain("tweetdeck.twitter.com") {
body {
    font-family: "Open Sans", sans-serif;
  }
  
  .js-app-columns, .app-columns .horizontal-flow-container, .without-tweet-drag-handles {
   padding: 0px;
  }
  
  .txt-sub-antialiased {
    opacity: 0.2;
  }
  
  .js-column, .column{
    margin: 0 auto;
    background-color: #1d1d1d;
    border: none;
  }
  
  /* Workaround to remove scrollbar in Firefox, lol */
  
  .antiscroll-inner {
    width: 95% !important;
    overflow-x: hidden;
  }
  
  .scroll-v {
    width: 106%;
  }
  
  .compose-text {
    width: 95%;
  }
  
  .js-tweet-actions {
    opacity: 1;
  }
  
  .js-customtimeline-description {
    border: none;
  }
  
  .inline-reply {
    background-color: #1d1d1d;
  }
  
  .nbfc {
    color: #DDDDDD;
  }
  
  .compose-header {
    border-color: /*[[color]]*/;
  }
  
  .stream-item {
    font-family: "Open Sans", sans-serif !important;
    border: none !important;
    font-size: 10pt;
    padding: 5px;
    padding-right: 10px;
  }
  
  .js-column-header, .js-action-header, .column-header {
    background-color: #1d1d1d;
    border: none ;
    opacity: 0.3;
    transition: opacity 0.3s 0.1s;
  }
  
  .column-header:hover {
    opacity: 1;
  }
  
  .column-options {
    background-color: #1d1d1d;
  }
  
  .avatar, .compose-account-img, .size48, .js-account-item {
    border-radius: 100px;
    transition: transform 0.1s ease-out;
  }
  .avatar:hover, .compose-account-img:hover, .size48:hover, .js-account-item:hover {
    transform: scale(1.1,1.1);
  }
  .avatar:active, .compose-account-img:active, .size48:active, .js-account-item:active {
    transform: scale(0.9,0.9);
  }
  
  .s-profile, .prf {
    border-radius: 0px;
  }
  
  .js-follow-button, .btn, .btn-alt, .js-hide-drawer, .margin-bm, .btn-alt:focus, .btn-aquare, .attach-compose-buttons, .tweet-button, .btn-positive, .js-user-actions-menu .is-inverted-dark, .compose {
    background-color: #1d1d1d;
    color: #DDDDDD;
  }
  
  .is-vishidden-wide {
    display: none;
  }
  
  .tweet-dogear {
    display: none;
  }
  
  .stream-item .is-retweet {
    border-right: solid 2px #2ecc40;
  }
  
  .is-retweet .link-complex {
    color: #2ecc40;
  }
  
  .stream-item .is-favorite {
    border-right: solid 2px #f44336;
  }
  
  .is-favorite .link-complex {
    color: #f44336;
  }

  .txt-sub-antialiased {
    opacity: 0.5;
  }

  .quoted-tweet {
    border-color: #333;
    transition: transform 0.0875s;
  }
  
  .quoted-tweet:hover {
    border-color: #333;
    transform: scale(1.05, 1.05);
  }
  
  .icon-small-context {
    opacity: 0.3;
  }
  
  .js-column-nav {
    overflow: hidden;
  }
  
  .prf-bio {
    letter-spacing: -0.5px;
  }
  
  .attach-compose-buttons .tweet-button {
  background-color: #1d1d1d;
  }
  
  .js-follow-button:hover, .btn:hover, .btn-alt:hover, .btn-alt:hover, .btn-aquare:hover, .js-user-actions-menu:hover .is-inverted-dark:hover {
  background-color: #222;
  color: #DDDDDD;
  }
  
  .js-send-tweet-button, .js-hide-drawer, .tweet-button, .column-message {
  color: #DDD;
  background-color: #1d1d1d;
  }
  
  .js-send-tweet-button:hover .js-hide-drawer:hover {
  color: #111;
  background-color: #7fdbff;
  }
  
  .btn-positive-on-blue[disabled], .btn-positive-on-blue[disabled]:hover, .btn-positive-on-blue[disabled]:active, .btn-positive-on-blue.is-disabled, .btn-positive-on-blue.is-disabled:hover, .btn-positive-on-blue.is-disabled:focus, .btn-positive-on-blue.is-disabled:active {
  background-color: #eee;
  }
  
  .js-detail-container {
  background-color: #1d1d1d;
  }
  
  .tweet-detail-wrapper {
    background: none;
    border: none;
  }
  
  .is-unread, .js-unread-count {
  background-color: #2d2d2d;
  }
  
  .btn-on-blue, .btn-on-blue:focus {
  border: 0px;
  }
  
  .link-complex, .link-complex:hover, .url-ext, .url-ext:hover, .column-type-icon {
  color: /*[[color]]*/;
  }
  
  a.account-link, .block {
  color: #DDD;
  }
  
  .btn-neutral, .btn-neutral:hover {
  background: none;
  }
  
  .btn-on-blue.is-disabled, .btn-on-blue.is-disabled:hover {
  background-color: #222;
  color: #999;
  }
  
  .app-columns-container {
  background-color: #222;
  }
  
  .prf-img {
  background-color: #1d1d1d;
  border: 1px #fff solid;
  border-radius: 100px;
  }

.app-header, .js-column-nav-list, .js-column-nav-menu-item, .app-title {
    background-color: #1d1d1d;
  }
  
  .icon-compose {
      color: /*[[color]]*/;
    }
}
