@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com") {

/*body*/
html, body {
  background: url(http://nyapics.ru/images/930bb90e8215d567eaef8a255918ccf1.png) no-repeat right bottom fixed;
    color: #333 !important;
}
a {
    color: #333 !important;
}
  
  
  
/*header*/
#page_header {
  width: 100% !important;
  position: fixed !important;
  left: 0 !important;
  z-index: 999;
}
#page_header .content {
  margin: 0 auto !important;
  position: relative !important;
  width: 776px !important;
}

#page_header .back, #page_header .left, #page_header .right {
    background: #666 !important;
}
.top_home_link{
      background: url("data:image/png;base64,") no-repeat scroll 20px 10px, #666;
  width: 140px;
  transition: 0.3s;
}
.top_home_link:hover {
  background: url("data:image/png;base64,") no-repeat scroll 20px 10px, #444;
  transition: 0.3s;
}
.head_nav a.top_nav_link {
    color: #fff !important;
    text-shadow: none !important;
    background: #666 !important;
  transition: 0.3s;
}
.head_nav a.top_nav_link:hover, .head_nav a.top_nav_link.over, #top_back_link:hover{
    color: #fff !important;
    background-color: #444 !important;
  transition: 0.3s;
}
.head_nav a.top_nav_link span#head_music_text {
    color: #fff !important;
}
  

