css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("greasyfork.org") {
    #new_script_version {
        position: relative;
        padding-top: 2em;
    }
    #new_script_version > input {
        position: absolute;
        top: 0px;
    }
}

@-moz-document domain("sexinsex.net"), domain("sis001.com") {
.mainbox.viewthread table {
	direction: rtl;
}
.mainbox.viewthread table td {
	direction: ltr;
}

.slideshow {
    position: fixed;
    /*border: solid 1px red;*/
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    overflow-y: hidden;
    overflow-x: scroll;
    white-space: nowrap;
    background-color: black;
    z-index: 1000;
}
.slideshow :not(img) {
    font-size: 0px !important;
    /*ndisplay: none; */
}
.slideshow br {
	display: none;
}
.slideshow img {
    height: 100%;
    width: auto;
}

}
