css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://zsm1.bydgoszcz.pl/1plan/plany/o25.html") {

  body
  {
    line-height:1.28;
    background:#E9EAED;
    color:#9197A3;
  }
  
  a:link
  {
      color:#9197A3;
      text-decoration:none;
      font-weight:bold;
  }
  

  td.tytul
  {
      border:none;
      text-align:left;
      color:white;
      background:#666666;
      font-size:12px;
      height:42px;
      padding:10px;
  }
  
  td.tytul:before
  {
     padding:4px 18px;
     color:#87CEFA;
     border-bottom:4px solid #87CEFA;
     content:"#PLANLEKCJI_4F";
     color:black;
     background:white;
     transition:2s;
  }

  
  span.tytulnapis
  {
      font-size:0px;
  }
  
  img
  {
    display:none;
  }
  
  table.tabela,
  table.tabela tr td,
  table.tabela tr th
  {
    font-size:12px;
    padding:0px 0px;
    color:black;
    background:none;
    border:none;
  }
  
  
  table.tabela
  {
     border:1px solid;
     border-color:#E5E6E9 #DFE0E4 #D0D1D5;
     background:#fff;
     margin-top:12px;
     border-radius:5px;
  }
  
  table.tabela tr th
  {
    display:none;
    color:#141823;
    background:#F6F7F8;
  }
  
  table.tabela tr td
  {
    border:1px solid #F6F7F8;
    height:35px;
  }
  
    table.tabela tr:hover td
      {
        background:#87CEFA !important;
        border:1px solid #87CEFA;
      }
  
  table.tabela tr td.G,
  table.tabela tr td.nr
  {
    background:#F6F7F8;
    font-weight:normal;
    font-size:10px;
    line-height:35px;
  }
  
  table.tabtytul
  {
    border:none;
  }
  
  a:link
    {
      display:none;
    }
  
  td.op a
  {
    display:inline;
  }
}
