css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cheezburger.com") {

body {
    background-color: rgb(10,10,10)!important;
    background-image: url(http://i44.tinypic.com/9fsn50.jpg)!important;
  
}
.navbar-inner{
    background: #2E2E2E !important;
    border-color: rgb(10,10,10)!important;
}
.navbar .flyout {
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 1);
    background: #2E2E2E !important;
    
}
.post-asset-tags {
    border: 1px solid rgb(10,10,10)!important;
    background: #2E2E2E !important;
    
}
.search-container{
    display: None;
}
.js-asset-tags{
    background: rgb(0, 173, 238) !important;
    color: rgb(0, 73, 138) !important;
}
.post .post-asset {
    background: none;
       
}
.content-card {
    border-style: solid;
    border-color: rgb(10,10,10)!important;
    border-width: 3px 3px 3px 3px;
}
.echo-streamserver-controls-cardcomposer-container{
    border: none;
}
.post.js-post.post-spp.social-buttons{
    border: none;
}
.echo-streamserver-controls-card-body .echo-streamserver-controls-card-text{
    color: rgb(0, 73, 138) !important;
}
.js-vote-down.btn.btn-chz-vote-down.icons{
    background: rgb(200, 0, 0) !important;
    color: rgb(100, 0, 0) !important;
    border-color: rgb(100, 0, 0) !important; 
    text-shadow: 1px 1px rgb(175, 0, 0) !important;
}
.js-vote-up.btn.btn-chz-vote-up.icons{
    background: rgb(0, 200, 0) !important;
    color: rgb(0, 100, 0) !important; 
    border-color: rgb(0, 100, 0) !important; 
    text-shadow: 1px 1px rgb(0, 175, 0) !important;
}
a.alt{
    color: rgb(0, 173, 238) !important;
}
P{
    color: rgb(0, 173, 238) !important;
    width: 100%;
}
h4.orange-header {
    color: rgb(0, 173, 238) !important;
    background: none repeat scroll 0% 0% #2E2E2E;
    border-style: none;
    width: 177px;
}
.sidebar {
    background-color: #2E2E2E;
    border-style: none;
}
  .stream-sidebar-left .sticky-enabled-left.sticky-sidebar {
    width: 177px;
}
li.vertical:first-of-type {
    border-left: none !important; 
}
li.vertical {
    border-right: none !important;            
  }
}
