css:
@-moz-document domain("yandex.ru") {
/* ESI core v2.5.5 */
/* Author: Danila Isakov ◀http://dreamject.org▶ */
/* Most actual version: https://github.com/dreamject/ESI/blob/master/esi-core.css */
/* ESI (eyes-saving interface) is a project that allows you to improve quality of vision, */
/* prevent yourself from myopia progression (and, if possible, to reduce it), improve your posture and readability*/
/* Get more: http://dreamject.org/dreamjects/esi/ */
	html {font-size: /*[[fs]]*/ !important; }
		* {font-size: inherit !important; }
	html {line-height: /*[[lh]]*/; }
		* {line-height: inherit !important; }
	a {text-decoration: none !important; }
		/*a:hover { text-decoration: none; border-bottom: 1px solid; }*/
		esi-text-block {width: xrem !important; height: yrem !important; }	
	html:not(.no-such-class):not(.nope) h1 {font-size: 1.7rem !important; font-size: calc(1.43rem + 8px) !important;
			line-height: 120% !important; line-height: calc(1.43rem * 1.052 + 8px * 1.052 + 5px) !important; }
	html:not(.no-such-class):not(.nope) h2 {font-size: 1.4rem !important; font-size: calc(1.1rem + 2px) !important;
			line-height: 125% !important; line-height: calc(1.1rem * 1.052 + 1.052 * 2px + 5px) !important; }
	html:not(.no-such-class):not(.nope) h3 {font-size: 1.17rem !important; font-size: calc(1.06rem + 1px) !important;
			line-height: 130% !important; line-height: calc(1.06rem * 1.052 + 1px * 1.052 + 5px) !important; }
	html:not(.no-such-class):not(.nope) sup,html:not(.no-such-class):not(.nope)  sub{font-size: 0.68rem !important; font-size: calc(1rem - 5px) !important; line-height: 1rem !important; }
	html:not(.no-such-class):not(.nope) small,html:not(.no-such-class):not(.nope)  xx-small, html:not(.no-such-class):not(.nope) x-small, smaller, html:not(.no-such-class):not(.nope) h5, html:not(.no-such-class):not(.nope) h6 {font-size: 0.9rem !important; font-size: calc(0.95rem - 1px) !important; }
/*ESI core end*/

select {
	 width: auto !important;

}
.layout_type_serp .layout__col_size_9 {
    width: 36rem !important;
}
}
