css:
@namespace url(http://www.w3.org/1999/xhtml);@-moz-document domain("liblia2.culture.hu-berlin.de") {

html {overflow: -moz-scrollbars-vertical}
body {margin:0;padding:0em 1em;font:11px/18px sans-serif}
img {border:none}
td {vertical-align:top}

td#col-left {width:111px;overflow:auto;white-space:nowrap}
#main {width:100%}
#top-left,#top-left a, #top-middle,#top-right  {display:none}

a {text-decoration:none !important}
a:hover {color:#00e}
a:visited:hover {color:#00e}

div.navitem.blank {display:none}

#col-content {padding:0}
#contentwrap a {text-decoration:none;color:#09e}
#content {}

div#nav {font-weight:bold;clear:both}
div#nav a {color:#000;background:#f0f0f0;padding:0.5em;border-right:1px solid #fff}
div#nav a:hover {background:#e0e0e0}

#loginformleft input[type=image] {margin:0;padding:0}
#loginformleft button {margin:0;padding:0;vertical-align:middle;background:none;border:none}

#content div.startbox ul  {padding:0 0 0 2em}
#content div.startbox li  {color:#999}
#content div.startbox     {border:1px solid #eee;float:left;width:300px;margin:1em 1em 0 0}
#content div.startboxhead {background:#eee;padding:5px;font-weight:bold}
#content div.startboxhead a {color:#000;display:block}
#content div.startboxdesc {padding:3px}


.ftppath {white-space:nowrap}
.ftperror {font-family:monospace;color:red;padding:10px}

tr.head {background:#ddd}
.sortable tr.head {cursor:pointer}
tr.even, tr.roweven {background:#f9f9f9}
tr.odd, tr.rowodd  {background:#ffffff}

th.resize-handle-active {cursor:e-resize}

div.resize-handle {
  cursor: e-resize;
  width: 2px;
  border-right: 1px dashed #1E90FF;
  position:absolute;
  top:0;
  left:0;
}

.style-char     {cursor:pointer;font-weight:bold;border-left:1px solid #fff}
.style-chardesc {cursor:pointer;padding-right:10px}

.listcell {overflow:hidden;white-space:nowrap;padding:0 5px;vertical-align:middle}

.usererror {color:red}
.usermessage {color:red}

.inline input {border:1px solid red;color:red}
.inline fieldset {display:none}
.inline legend {display:none}

table.statuslist {border-spacing:0px}
.statuslist td {vertical-align:top;}
 
.nowrap {white-space:nowrap}

table#browsehead {width:222px;position:absolute;right:1em}
table#browsehead tr {display:inline}
td#browseheadleft   {display:block;clear:both;}
td#browseheadmiddle {display:block;clear:both;text-align:center;white-space:nowrap}
td#browseheadright  {display:block;clear:both;text-align:center;white-space:nowrap}
table#browsehead img {vertical-align:middle}

#listinghead {}
#pagesection {width:150px}

#pageimageouter {width:500px;position:absolute;border:0px solid #666}
#pageedit {position:absolute;top:-15px;right:0px;display:none}
.pageedit-item {padding:0 5px;border:1px solid #000;border-bottom:none}
#pageversions {display:none}
#pageimage, #pagetext {border:solid 1px #666}

#pageimage {text-align:center}
#pagetext  {font-family:serif;display:none;text-align:justify}
#pageeditor-wrap {display:none;width:500px}
#pageeditor {width:100%;height:100%;font-family:serif;font-size:11px}

.excerpt              {margin:5px 5px 10px 5px}
#excerpt-status       {font-size:7px}
#excerpt-text-wrap    {border:1px solid #aaa;text-align:left;min-height:10px}
.excerpt-text-page    {float:left;font-size:10px;font-style:italic;text-decoration:underline;cursor:pointer}
.excerpt-text-action  {right:0;text-align:right;font-size:9px}
.excerpt-text-preview {font-size:10px;line-height:12px;cursor:pointer}
.excerpt-text-content {font-size:10px;line-height:12px;cursor:pointer}

#excerpt-editor      {text-align:left;line-height:12px}
#excerpt-editor-page {text-align:center;background:#efefef}
#excerpt-editor-ta   {width:200px;height:50px}

div.textblock {width:500px;margin-left:10px}

/* Liblia News */
div.newsitem  {margin-bottom:20px}
div.newshead  {background:#F3F3F4;border:1px solid #09e;-moz-border-radius:5px;padding:5px 10px}
div.newsdate  {font-size:0.9em}
div.newstitle {font-weight:bold;font-size:1.4em}
div.newstext  {}
.newscode {background:#F3F3F4}


#filterlist {width:148px;position:relative;top:0px;left:0px;z-index:100;background:#fff;border:1px solid #000}
.filteritem       {z-index:100;cursor:pointer}
.filteritem:hover {background:#678db2}

.screenshot {float:left;padding:2px;margin:2px;border:1px solid #ccc;text-align:center}
.screenshot img {width:265px;height:150px}

table.search {border-collapse:collapse}
table.search td, table.search th {padding-right:10px}
tr.searchhead {border-bottom:1px solid #333}
tr.searchhead th {text-align:left;font-weight:bold}
tr.searchresource {border-bottom:1px solid #aaa}
.search td.hits {text-align:right}

.bc {position:absolute;right:2px;background:#f3f3f3;padding:2px;font-size:.8em}

.textflow {max-width:44em}
.textflow p {text-align:justify}




}
