css:
@-moz-document domain("www.biblionetka.pl") {
 /* zamiana na stare logo*/
#ctl00_customizableLogo_imgLogo {
  box-sizing: border-box;
  background: url(https://imageshack.com/a/img537/2449/tdYfoP.gif) no-repeat;
  width: 422px;
  height: 104px; /* Height of new image */
  padding-left: 24422px; /* Equal to width of new image */
  margin: -15px;} 

/*ocenione ksiazki w innym kolorze*/
  a.rated-book { color: #888888 !important; } /* kolor ocenionych */
  a.rated-book { font-weight:800 !important;  } /* pogrubienie ocenionych */
  
/* autorzy, nieocenione ksiazki, linki */
a, a:link,  a:visited, span.shortUrl, span.NewAnswerWarningDismissLink{ color: #E66C00; font-weight:400; font-size: 100%;}

/* kolor linkow do serii itp. */
h2 a { color: #E66C00 !important;}

/* link do tekstu recenzji */
#wsrodku .item h4 .uppercas a { color: #E66C00 !important; font-weight:800!important; }


/* menu gorne*/
div#topmenu a:link, div#topmenu a:visited { color: #808080  !important; }
div#topmenu { color: #808080; background: #fff !important; }

/*szukaj*/
#ctl00_SearchButton {color: #000000; background: #fff !important; text-transform: lowercase; font-weight:normal; border: solid 1px #000000; margin-left:-2px !important;}
td.searchInputCell {border: solid 1px #000000;}
div#searchAlphabeticalLinks { color: #000000 !important;}
div#szukaj a:link, div#szukaj a:visited { color: #5daceb !important;}
div#szukaj {padding-left:10px !important; padding-right:240px !important;}
table#searchWrapper {border:none !important;}

/*okienko logowania*/
div#login {background: #dcefff !important; color: #a7befd; border-top: solid 3px  #E66C00;}
div#login a:link {color: #ff720a;}
#ctl00_loginUser_LoginButton {background: #fff; color: #000000; border: solid 1px #000000; box-shadow:none; font-weight: normal; text-transform: lowercase;} 
div#login .logpass {border: solid 1px #a7befd !important; background-color: none !important;}

/*prawa kolumna*/
div#moja {  background-color: #fff !important;font-weight: 400; margin-top:10px; border: none; }
#moja p { background-color: #dcefff !important; color: #6bbafd !important; font-weight: bold; border-top: solid 3px  #E66C00;}
#moja a {color:  #E66C00 !important; font-weight: bold !important; text-decoration: none !important;}
#moja li a { background-color: #f0f0f0 !important; color: #6bbafd !important; font-size: 100% !important;font-weight: bold !important; border: solid 3px #fff;}
#moja li a:hover {color: #9bd0ff !important; font-size: 100% !important; text-decoration: none !important;}
#moja li.tutaj a {color: #fff !important; background: #9bd0ff !important;}
#moja ul li a:link, #moja ul li a:visited {background: none;}

/*lewa kolumna*/
div#lewemenu { background: none; border:  none !important; margin-top:10px; color: #579edc;}
 #navleft_rb li a {background: #add2f3; margin-right:-13px; text-align: left !important; font-size: 100% !important; font-weight: bold !important;  color: #579edc;}
 #navleft_rb li a:hover { color: #fff; text-decoration: none;}
div#lewemenuHeader {margin-top:-10px;}
div#lewemenuHeader .side-header {background: #dcefff; padding-top: 41px; margin-top: -11px;  margin-right: -13px; margin-bottom: -3px;}
#navleft_rb .firstChild  a { color: #E66C00 !important; font-size: 120% !important;}
body#polecksiazki #polecksiazkimenu a {color: #b30066;background-color: #2b84d1;}

/*menu glowne*/
body#start #startmenu a, body#katalog #katmenu a, body#ksiegarnia #ksiemenu a, body#recenzje #recemenu a, body#czytatnik #czytmenu a, body#forum #forumenu a,#navtop, li.menuActive a {  background-color: #2b84d1; color: #fff !important; font-size:100% !important; font-weight: bold !important; border-right: solid 5px #fff !important;} 
#navtop li a:link, #navtop li a:visited{color: #fff !important; background-color: #9bd0ff;  font-weight: bold !important; border-right: solid 5px #fff !important;}
#navtop a:hover {color: #fff !important; background: #b5d8ee !important; font-weight: bold !important;}

/*belka*/
div#kontekstowe a {font-size:80% !important;  font-weight: bold !important;}
div#kontekstowe_background  { background: #2b84d1 ; color: #fff !important; font-weight: bold !important; margin-bottom:10px;} 
div#kontekstowe_background a:hover{ background: #b5d8ee; color: #fff !important; font-size:80% !important;  font-weight: bold !important;} 
#kontekstowe li a.tutaj{ background-color: #9bd0ff; color: #fff !important; font-size:80% !important;  font-weight: bold !important;}

/*narzednik*/
div#small_menu { background: #fff !important; color: #000000 !important; border-style: solid;  border-color: #9bd0ff;  border-width: thin; margin-top: 34px;} 
div#small_menu li a { background: #fff !important; color: #000000 !important; font-size: 100%} 
div#small_menu_header { background: #9bd0ff !important; border-style: solid;  border-bottom-color: #9bd0ff;  border-bottom-width: thin; color: #fff !important;}

/*nagłówki menu*/
.side-header{ color: #2b84d1 !important; text-align: center; font-size: 140% !important; }

/*okienko opiekunow*/
div#patronProgressBarMain { border: solid 1px #fff !important; background: #dcefff; color: #ff8431; margin-right:-13px;}
div#patronProgressBarMain p { background: #dcefff; margin: 0; padding: 0px; font-size: 12px}
div#patronProgressBarMain p:first-child a { color: #ff8431 !important; }
div#patronProgressBarMain a, span.shortUrl, span.NewAnswerWarningDismissLink  { color: #ff8431; }
#ctl00_PatronProgressBar1_patronBar {background: #f29400 !important;}

/*polecane recenzje*/
#recenbox ul li {background: none !important; border: none;}
#recenbox {background-color: #fff !important; margin-top: -10px; border: solid 1px #9bd0ff; margin-top: 40px !important; font-size:90% !important;}
#recenbox a {font-size:80% !important;}
div#recenbox p {border-bottom:solid 1px #b4b4b4 !important; color: #000000 !important;}

/*srodek*/
div#wsrodku h1 {border-bottom: 1px solid #5f5f5f !important; color: #f29400; font-weight: 200 !important;} 
div#ctl00_MCP_articlePanel h1 {text-transform: none !important;}
div#wsrodku .infoitem {background-color: #fff;}
div#wsrodku .infoitem p {overflow:hidden; text-overflow:ellipsis;}
div#wsrodku .subitem {background-color: #fff;  color: #000000;}
div#wsrodku .subitem a {background-color: #fff;  color:#E66C00;}
div#wsrodku {background: #fff;}
.statsTable tr:hover td{background: #dcefff !important;}

/*strona uzytkownika*/
div#wsrodku .contentHalfLeft h2 {background:  #add2f3 !important; color: #fff !important; font-weight: 800 !important;} 
div#wsrodku .contentHalfRight h2 {background:  #add2f3 !important; color: #fff !important; font-weight: 800 !important;} 
div#wsrodku .contentFullSize h2 {background:  #add2f3 !important; color: #fff !important; font-weight: 800 !important;} 
div#wsrodku .contentHalfLeft, .contentHalfRight, .contentFullSize {background:  #fcfcff !important;  border: none !important;} 
div#wsrodku .item h2 {background:  none !important; color: #fff; font-weight: 200 !important;}

/*forum*/
div#wsrodku .tabela_forum th {background:  #b5d8ee !important; color: #334c68 !important;} 
div#wsrodku .tabela_forum td {border-color:#334c68 !important; background:  #e3f5ff; font-size: 120%;}
div#wsrodku .td_sub {background:  #c3e4f7 !important; border-top: 1px solid #334c68 !important; border-left: 1px solid #334c68 !important; border-right: 1px solid #334c68 !important;}
div#wsrodku h2 {color: #334c68 !important; font-weight: 400;}
div#wsrodku .td_lewy {background:  #edf8ff !important; color: #334c68 !important;}
div#wsrodku .td_lewy a { font-weight:600;}
div#wsrodku .td_bold {background:  #e3f5ff !important; color: #334c68 !important; font-weight: 400;}
div#wsrodku .td_male {background:  #e3f5ff !important; color: #334c68 !important; border-right: 1px solid #334c68 !important; font-size:80% !important;}
div#wsrodku .tabela_forum a {color: #ff8431 !important;} 
div#wsrodku .tbody {border-top: 5px solid #334c68 !important;} 

/*komentarze*/
div#ctl00_MCP_answersTree .forum_odd, .forum_odd h3, .forum_odd h4 {background: #e3f2ff !important; border: solid 1px #c3d2df important; font-size: 100% !important;}
div#ctl00_MCP_answersTree .forum_even, .forum_even h3, .forum_even h4 {background: #f1f9ff !important; border: solid 1px #c3d2df important; font-size: 100% !important;}

/*rozniaste*/
input.zatwierdz, input.platnosci { background: #E66C00 !important; }
a#AddTags { background-color: #E66C00 !important;}
.sideBoxContent {border: none !important; display:none;}
div#container .adsbygoogle {display:none !important; visibility:hidden !important;}
div#stopka a { color: #b4b4b4 !important;}
div#stopka {background: #f8f8f8 !important;}
div.likeButton:hover {border-color: #E66C00 !important;background: #fcf1cf}
#wsrodku p.likeButtonText { color:#444444;}
#wsrodku p.likeButtonText:hover { color:#000000;}
div#szukaj {background: #dcefff;}
div#lewakolumna, div#srodek_wrapper {background: #f8f8f8 !important;}
a.LTU_Author {background: #fcf7c0;}
a.LTU_Admin {color: #2b84d1 !important;}
img[src$="tick.png"] { height: 0px !important;width:0px !important;} 
}
