css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
  
  /*Voir les styles qui ont inspiré celui là : style 46060, style 44099, 102306 */

/*Fond de page Stylish*/
  html {
    background :
      linear-gradient(to bottom, #C2DF70, #C2DF70 32%, black 32%, black 34%, transparent 34%, transparent),
      linear-gradient(to bottom, #E66856, #E66856 68%, black 68%, black 70%, transparent 70%, transparent),
      linear-gradient(to right, #E1A97F, #E1A97F 32%, black 32%, black 34%, #EFE982 34%, #EFE982 65%, black 65%, black 67%, #9DCBE3 67%, #9DCBE3);
    background-size : 65% 100%, 33% 100%, 100% 100% ;
    background-position : top left, top right, top center ;
    background-attachment : fixed ;
    background-repeat : no-repeat;
  }

/*Partie centrale*/ 
  body {
    background :
      url("http://nsm08.casimages.com/img/2014/09/08/14090804122210339312507568.jpg"), linear-gradient(to right, #B5DCEF, #B5DCEF 180px, black 180px, black 183px, white 183px, white) ;
    background-repeat : no-repeat ;
    background-position : top left, left ;
    background-size : 180px auto, auto ;
    border-radius : 0 0 10px 10px ;
    border : 3px solid black ;
    border-top : none ;
    margin-top : 0 ;
    box-shadow : 2px 2px 5px rgba(0,0,0,0.8), -2px -2px 5px rgba(0,0,0,0.8) ;
  }

/*Colonne de gauche*/
  #left-sidebar {margin-top : 120px ;}
  
  #left-sidebar > * {background : none ;}
  
  #left-sidebar nav {
    font-size : 1.2em ;
    font-weight : bold ;
  }
  
  #left-sidebar #related ul {font-size : 0.8em ;}
  
/*Bannière*/
  #main-header {
    background : linear-gradient(to right, #C9E683, #C9E683 180px, #90C706) ;
    border-bottom : 3px solid black ;
    border-radius : 0 ;
    margin-left : 183px ;
    padding-left : 30px ;
  }
  
  header h1 a {
    display : block ;
    margin-left : -20px ;
  }
  
  
  
/*Page utilisateur*/  
  table.author-styles td {border-top : 1px solid black !important ;}
  
  td.obsolete, td.obsolete~td {
    background : grey ;
    opacity : 0.3 ;}
  
  table.author-styles th:nth-child(6), table.author-styles td:nth-child(6) {display : none ;}
  table.author-styles th:nth-child(2), table.author-styles td:nth-child(2) {background : #E1A97F ;}
  table.author-styles th:nth-child(3), table.author-styles td:nth-child(3), table.author-styles th:nth-child(5), table.author-styles td:nth-child(5) {width : 40px ;}
  table.author-styles th:nth-child(4), table.author-styles td:nth-child(4) {background : #EFE982 ; width : 40px ;}
  table.author-styles td:nth-child(1) {font-size : 1.2em ;}
  
  table.author-styles td:nth-child(7) {width : 185px ; height : 35px ; text-align : right ;}
  
  table.author-styles td:nth-child(7) a[href*="edit"], #control-panel a[href*="edit"] {text-decoration : none ; background : #C2DF70 ; Border : 2px solid black ; color : white ; font-weight : bold ; padding : 5px ;}
  table.author-styles td:nth-child(7) a[href*="delete"], #control-panel a[href*="delete"] {text-decoration : none ; background : #E66856 ; Border : 2px solid black ; color : white ; font-weight : bold ; padding : 5px ;}
  table.author-styles td:nth-child(7) a[href*="stats"], #control-panel a[href*="stats"] {text-decoration : none ; background : #9DCBE3 ; Border : 2px solid black ; color : white ; font-weight : bold ; padding : 5px ;}
  
  #info-tables {
    border : 2px black dotted ;
    width : 420px ;
    border-radius : 5px ;
    padding : 5px ;
    margin-bottom : 10px ;
    font-size : 0.8em ;
  }
  #info-tables a#show-button {
    text-decoration : none ;
    background : grey ;
    border : 2px solid black ;
    border-radius : 10px ;
    color : white ;
    font-weight : bold ;
    padding : 2px 5px 5px 5px ;
  }
  
  #left-info {float : right ;}
  #main-style-info {margin-left : 0 ;}
  
  #additional-info div#additional-info-text {display : none ;}
  #additional-info:after {content : "Read it ! Or not(es)..." ;}
  #additional-info:hover #additional-info-text {display : block ;}

/*Résultat de recherches*/ 
  
  .style-brief-text header a {font-weight : bold ;}
  .style-brief-text p {padding-left : 10px ;}
  .style-brief-stats {border : 1px dotted black ; border-radius : 10px ; padding : 5px ; width : 325px ; margin-left : 15px ; margin-top : 10px ; font-size : 0.7em ;}
  
  article .style-brief-text p {display : none ;}
  article .listing-left-info:hover~.style-brief-text p {display : block ;}

  
  #left-sidebar nav a {color : black ;}
  li#sidebar-my-account {
    position : absolute ;
    top : 20px ;
    left : auto ;
    margin-left : 3px ;
    width : 40px ;
    font-size : 1.2em ;
  }
  li#sidebar-browse {
    position : absolute ;
    top : 130px ;
    left : auto ;
    margin-left : -24px ;
    transform : rotate(-90deg);
    font-size : 1.2em ;
  }
  li#sidebar-help {
    position : absolute ;
    top : 95px ;
    left : auto ;
    margin-left : 106px ;
    transform : rotate(-90deg);
    font-size : 1.2em ;
  }
  li#sidebar-forum {
    position : absolute ;
    top : 133px ;
    left : auto ;
    margin-left : 34px ;
    transform : rotate(-90deg) ;
    font-size : 1.2em ;
  }
  
  #view-code {
    background : rgba(0,0,0,0.1) ;
    text-overflow : ellipsis ;
    width : 760px ;
  }
  
  #related {margin-top : 40px ;}
  
}
