css:
@-moz-document url-prefix("http://p.nju.edu.cn"),url-prefix("http://210.28.129.9"),url-prefix("http://219.219.114.15")
{
  *{font-family: 楷体 !important;}
	body{
		background-image:url("/*[[image]]*/");
		background-size:cover;
	}
  
  .loginbox{background:rgba(255,255,255,0.4);padding:40px 40px 60px 40px;margin-top:-200px;}
  
  .inputbg{background: transparent !important;}
  input[type="text"],input[type="password"]{height:30px !important;background:rgba(255,255,255,0.7);}
  .inputbg>.inputdiv:first-child::before{content:'学号：';color:#666666;font-size:140%;}
  .inputbg>.inputdiv:nth-child(2)::before{content:'密码：';color:#666666;font-size:140%;}
  span{color:#666666 !important;}
  
  button{background:rgba(125,200,100,1);color:white;font-weight:normal;}

  .noticetitle{color:#666666;font-size:140% !important;position: fixed;}
  .tishi{font-size:120% !important;}
  .tishi *{font-size:120%;}
  .tishi>h2{color:#666666 !important;font-size:120% !important;}
  .tishi>p:not(:last-child){color:#336666;}
  .tishi>p:last-child{color:#6633FF;text-align: left;}
  
  .mainbox{background:rgba(255,255,255,0.3) !important;margin-top:-300px;padding:40px 40px 110px 40px;}
}
