css:
#toolbar-context-menu, menupopup, popup { border: 0px solid !important; -moz-border-top-colors: #000 !important;
-moz-border-right-colors: #000 !important; -moz-border-bottom-colors:#000!important;-moz-border-left-colors: #000 !important;}

#toolbar-context-menu, menuitem, menupopup, popup, menupopup menu, menupopup menuitem, popup menu, popup menuitem, #bookmarks-menu autorepeatbutton,
#personal-bookmarks autorepeatbutton {background-color: #121949 !important;color: #000 !important;}

#toolbar-context-menu:hover, menupopup menu:hover, menupopup menuitem:hover, popup menu:hover, popup menuitem:hover, 
#bookmarks-menu autorepeatbutton:hover, #personal-bookmarks autorepeatbutton:hover {color: #65E8FA !important;}

menuseparator {display: none!important;}

menupopup, .menupopup, popup, .popup {-moz-appearance: none !important; -moz-border-left-colors: none !important;
-moz-border-right-colors: none !important; -moz-border-top-colors: none !important; -moz-border-bottom-colors: none !important; 
-moz-border-radius: 0px !important; background: #121949 none repeat-x top !important;
border: solid 0px #000 !important; margin: 0px 0px 0px 1px !important;}

menupopup:hover, .menupopup:hover, popup:hover, .popup:hover,menupopup[placespopup="true"]:hover {
border-color: rgba(64,140,200,1) !important;}

menupopup > menu,menupopup > menuitem,popup > menu,popup > menuitem {-moz-appearance: none !important; color: #fff !important;
font-weight: bold !important;font-size: 12px !important;min-height: 18px !important;height: 24px !important;
margin: 0px 1px 1px 1px !important;-moz-border-radius: 0px !important;border: solid 1px transparent !important;}

menupopup > menu:first-child,menupopup > menuitem:first-child,popup > menu:first-child,
popup > menuitem:first-child {margin: 1px 1px 0px 1px !important;}

menupopup > menu[_moz-menuactive="true"]:not([disabled="true"]),
menupopup > menuitem[_moz-menuactive="true"]:not([disabled="true"]),
popup > menu[_moz-menuactive="true"]:not([disabled="true"]),
popup > menuitem[_moz-menuactive="true"]:not([disabled="true"]),
#personal-bookmarks menupopup > menuitem[_moz-menuactive="true"]:not([disabled="true"]),
#personal-bookmarks menupopup > menu[_moz-menuactive="true"]:not([disabled="true"]) {
color: #fff !important;
background: #121949 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAjCAIAAAAxJEzgAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAj0lEQVR4nAXBwVEDQRADQEmeo4ocyMk5kR2h8OVD+XYkuZvP75+hhCyboV1g7LCdtRmObBBDlIWYCB3EBbW+48zxClTSrbV5xSsiQESyyBjLds7e1Uzddic5LsddVMpu61FMVg9QhdblcrAnsnpe9U6PW07sAEruxkrrRAIeiERQnf/tKefS3wev+fr8vTRvSCeBHF0J2fAAAAAASUVORK5CYII=") repeat-x top !important;}

#PersonalToolbar 
toolbarbutton[class="bookmark-item"][type="menu"],menu[container="true"][class="menu-iconic bookmark-item"]
{ list-style-image:  url("data:image/png;base64,R0lGODlhEAANAJECAP7/AFFRUQAAAAAAACH5BAEAAAIALAAAAAAQAA0AAAInlI+ZwawYgJThiThnawbnD1QXCFbbuYUjSUUmqzkry820aJcQegoFADs=") !important; -moz-image-region: rect(0px, 0px, 0px, 0px) !important;}


menupopup menu > .menu-right 
{background:url("data:image/png;base64,R0lGODlhDAAMAJECAP/3KSAlKe3z+AAAACH5BAEAAAIALAAAAAAMAAwAAAIalI+py90RXAARwQuozTxXMXXecUHjMn0KhBQAOw==") no-repeat center !important;}

popup menu > .menu-right,popup menupopup menu > .menu-right,menupopup >menu >.menu-right {-moz-appearance: none !important;
min-width: 9px !important; max-width: 9px !important; min-height: 10px !important; max-height: 10px !important;
margin: -2px 5px 0px 10px !important; opacity: 1.9 !important;}

popup menu > .menu-right,popup menupopup menu > .menu-right,menupopup menu:hover > .menu-right {
background:url("data:image/png;base64,R0lGODlhDAAMAJECAP/3KSAlKe3z+AAAACH5BAEAAAIALAAAAAAMAAwAAAIalI+py90RXAARwQuozTxXMXXecUHjMn0KhBQAOw==") no-repeat center center !important;}
