css:
@-moz-document domain('teamddas.co.uk') {

table.tborder,
td.thead,
table.tborder2
{
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
}

input#shout_data
{
  padding: 3px 8px;
  margin-left: 5px;
  border-radius: 3px;
  border: medium none;
  background: none repeat scroll 0% 0% #FAF;
}

.thead
{
  background: rgba(255, 0, 255, 1);
  border-bottom: 3px solid #ffaaff
}

.selected2
{
  background: rgba(255, 0, 255, 1);
}

input#shouting-status
{
  padding: 3px 8px;
  border-radius: 3px;
  border: medium none;
  background: none repeat scroll 0% 0% #FAF;
}

td.trow1
{
  background: none repeat scroll 0% 0% #454545;
}

td.trow2
{
  background: none repeat scroll 0% 0% #383838;
}

input.button
{
  border: medium none;
  background: none repeat scroll 0% 0% #FAF;
  color: black;
  border-radius: 3px;
  margin-top: 3px;
  padding: 3px 8px;
}

input.textbox
{
  padding-top: 3px;
  padding-bottom: 3px;
  padding-left: 8px;
  padding-right: 8px;
  border: none;
  background: #ffaaff;
  color: black;
  border-radius: 3px;
  margin-bottom: 3px;
}

div.fotter2
{
  background: none repeat scroll 0% 0% #F0F;
}

#shoutbox_e form
{
  text-align: left;
  padding-left: 8px;
}

td.tcat
{
  background: rgba(23, 23, 23, 1);
}

.probando
{
  background: rgba(255, 0, 255, 1);
  color: white;
  font-weight: bold;
  font-size: 12px;
  opacity: 1;
}

.probando2
{
  background: rgba(255, 0, 255, 1) !important;
  border-top-right-radius: 3px;
}

thead .thead
{
  border-top-right-radius: 0px;
}

span.online,
span.offline
{
  padding: 3px 5px;
}

.post_author_info
{
  background: none repeat scroll 0% 0% transparent !important;
  border-radius: 3px !important;
  color: black !important;
}

.post_author_info .postbit0 .todo,
.post_author_info .todo
{
  background: none !important;
  border: none !important;
}

select
{
  background: #ffaaff;
}

#userStats h1
{
  color: white !important;
}

#userStats .data
{
  background: none !important;
  border: none !important;
}

.mensajes40,
.botonenviarmp
{
    background: none repeat scroll 0% 0% #FF61FF;
    border: medium none;
}

#headerarriba,
#breadcrumb
{
  background: none repeat scroll 0% 0% #2C2C2C;
}

.textosearch
{
  background: rgb(41,41,41) !important;
  border: none !important;
  padding-top: 2px;
  padding-bottom: 2px;
  padding-left: 10px;
}

.headermenu,
#headerwelco
{
  background: #0E0E0E !important;
}

#header,
.headermenu,
.headermenu ul,
a.home
{
  border-top-left-radius: 8px;
}

.header400 .moverlo
{
  visibility: hidden;
}

#headerwelco
{
  border-top-left-radius: 8px;
}

.arrow_box
{
  border-color: rgba(255, 150, 255, 0) #ffaaff rgba(255, 150, 255, 0);
}

textarea#message
{
  background: #ffaaff;
}

.messageEditor
{
  background: rgba(255, 242, 255, 1);
}

.editor_dropdown_label,
#message_new
{
  background: #ffaaff;
}

div.logo img
{
  visibility: hidden;
}

div.logo
{
  background: url('http://imgur.com/lq3sfZ2.png') no-repeat scroll right bottom transparent;
  width: 615px;
  height: 70px;
}

}
