css:
@-moz-document domain("www.animetake.com") {
a{
  color: #63808D !important;
}
#topbar li a{
  font-size: 110%;
  padding: 5px 15px;
  color: #84B2FF !important;
}
#topbar ul{
  width: 100%;
}
h4{
  color: #5B8FA6 !important;
}
h3{
  color: #5B8FA6 !important;
}
h2{
  color: #5B8FA6 !important;
}
span{
  color: #ABCCDB !important;
}
p{
  color: #8D9CB0 !important;
}
#topbar-outer{
  margin-bottom: 40px;
  border-bottom: 0px;
}
#topbar{
  background: #30596B;
  width: 90%;
  height: 50px;
}
#header{
  background-image: url("http://oi62.tinypic.com/2elur05.jpg");
  background-repeat: no-repeat;
  background-size: 100% 200%;
  margin-left: 5%;
  margin-right: 5%;
  width: 90%
}
body{
  background: #121212;
}
#updates{
  background: #2F3639 !important;
}
#updates h1{
  background: #1C1C1C;
  color: #5B8FA6 !important;
}
#content{
  background: #1B1B1B;
  width: 98%;
  margin-left: auto;
  margin-right: auto;
}
#wrapper{
  width: 90%;
  margin: 30px auto;
}
#mainContent{
  width: 54% !important;
}
div.ddmcc{
  width: 50%;
  padding-right: 0px;
}
div.ddmcc p.sep{
  color: #8AAEC1;
  width: 100%;
}
div p.sep{
  background: #30596B;
}
div.sidebar h2, .navheader{
  background: #1C1C1C;
  color: #5F5F5F;
}
div.login{
  background: #202020;
}
div.comment_box{
  background: #2E3636;
}
div.coment_cont{
  background: #2C2C2C;
}
#categories{
  background: #212121;
  width: 19%;
}        
#rightsidebar{
  width: 25%
}
#description{
  background-color: #000 !important;
  color: #9F9F9F;
  border: 1px solid #478E9A;
  width: 89%;
  margin-top: 30px;
  margin-left: 5%;
}
div.catg_page{
  width: 54%;
}
div.entry{
  background: #202020;
}
ul.catg_list li.main{
  background: rgba(46, 65, 112, 0.29) !important;
}
div.cat_list_title{
  background: #B24141;
}
div.sidebar li{
  background: #2A2A2A;
}
ul.catg_list li{
  background: #2D2D2D !important;
}
div.post-info .post-info-info{
  background: #2D2D2D;
}
#footer{
  background: #001C23;
  width: 93%;
  font-size: 10px;
  border-bottom: 8px #00315B solid;
}
div.comment_box{
  background: #282828;
  color: #FFF;
}
#mainContent #updates ul{
  width: 95% !important;
}
#mainContent #updates ul li{
  width: 100%; 
}
#about{
  background: #212121;
}
li.catdisp0{
  background: #1B1B1B !important;
}
li.catdisp1{
  background: #202020 !important;
}
#calendar-wrap{
  width: 100%;
}
#wp-calendar caption{
  background: #2B637C !important;
  color: #AFC7D4 !important;
}
#wp-calendar td{
  background: #23252A !important;
  color: #02374E;
}
#wp-calendar th{
  background: #0E1D2F !important;
  color: #53ACD4 !important;
}
div.logo, img{
  display: none;
}
div.post-info-thumb img{
  display: block !important;
}
div.listupdate img{
  display: block !important;
  width: 70px !important;
  height: 70px !important;
  margin-top: 5px;
}
div.updateinfo{
  float: left !important;
  width: 87% !important;
  height: 70px !important;
  margin-top: 5px;
}
div.affiliates{
  background: #4C4C4C;
}
#updates, h3{
  background: #1E1E1E;
}
div.listupdate	div.updateinfo{
  background: #1E2123;
}
}
