/*======================================
   shimotsuke.shinbun tochigi ja jp liquid compact 2014/05
  @-moz-document url-prefix("")
  @-moz-document url("")
  @-moz-document regexp("")
  ======================================*/
@-moz-document domain("shimotsuke.co.jp") {

/*--- unwanteds ---*/
.subColumn .marT10,
[class~="advertisement"],
#centerpain_0_mainleftpain_0_ctl06_LeftPainPanel,
.floatL.padB15.padR15,
.adlantiss_frame,
ul.marT10,
[id$="_GooglePanel"],
#centerpain_0_mainleftpain_0_ctl07_ParentPanel+div,
#MainBody>iframe[src*="google.com"],
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
div {box-sizing: border-box; -moz-box-sizing: border-box;}
body{
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
.main_container {
 background-size: 100% auto !important;
 width: auto !important; padding: 0 !important;
 }
[class*="pad"] {padding: 0 !important;}
[class*="mar"] {margin: 0 !important;}

.floatL, .floatR {position: relative !important;}

/*--- header ---*/
.id_header>div {padding: 0 !important;}
/*-- path --*/
.id_siteHeader ul li
 {padding: 0 !important; font-size: medium !important; text-indent: 1em;}
.id_siteHeader ul li:not([class="mobile"]) {background-position: 5px 50% !important;}
.id_siteHeader ul li[class="mobile"] {width: auto !important; height: auto !important;}
/*-- logo --*/
.id_siteBrand {float: left !important;}
.id_siteBrand a img{height: 2em !important; width: auto !important;}
/*-- navigation --*/
.id_siteGlovalNavi, .id_siteGlovalNaviBg
 {height: auto !important; padding: 0 !important;}
.id_siteGlovalNaviBg li {
 width: auto !important; height: auto !important; padding: 0 0.5em !important;
 font-size: medium !important; line-height: inherit !important;
 }
.id_siteGlovalNaviBg li a {height: auto !important;}
.id_siteGlovalNaviBg li.home:after {content: "ホーム"; color: white;}

/*--- footer ---*/
.id_footer {margin: 0 !important;}
.pagetop
 {width: auto !important; height: auto !important; line-height: inherit !important;}
.button a {height: auto !important;}
.id_copy {font-size: medium !important; margin: 0 !important; padding: 0 !important;}

/*--- main contents ---*/
.id_contents {padding: 0 !important;}
.id_contents>.mainColumn, .subColumn {position: relative !important;}
.id_contents>.mainColumn {width: 100% !important; margin: 0 -220pt 0 0 !important;}
.id_contents>.mainColumn>* {width: auto !important; margin: 0 220pt 0 0 !important;}
.id_contents>.subColumn {width: 220pt !important;}

/*-- topic not found error page --*/
[class^="not_found"], [class^="notfound"]
 {margin: 0 !important; font-size: medium !important;}
[class^="not_found"]  br, [class^="notfound"]  br {display: none !important;}

/*-- floating navigation bar --*/
.index-sideicon {right: 0 !important;}

/*-- weater on top page ---*/
.weatherDetail {width: auto !important;}
.weatherDetail .weatherList {width: 50% !important;}

/*-- 2columns topic on top page --*/
.index-2columnBox {
 background-size: 100% auto !important;
 width: 50% !important; margin: 0 !important; padding: 0 !important;
 }

/*-- DoSOON on top page --*/
.titleH1 {min-height: 0 !important; padding: 0 !important;}
.titleH1 span:first-child {margin: 0 !important;}
.titleH1 span:nth-child(2), .titleMenu .moreList
 {height: auto !important; line-height: inherit !important;}
.titleH1 .titleMenu .moreList a {height: auto !important;}
.titleH1, .titleH2 {background-size: auto 100% !important;}
.section {margin: 0 !important;}
.titleH2 {padding: 0 !important;}
.titleH2 .titleText {margin: 0 !important;}
.titleMenu .moreList a {margin: 0 !important; padding: 0 !important;}
.titleH2 .titleMenu .moreList a {height: auto !important; line-height: 1.4em !important;}
.button {line-height: 1.4em !important;}
.article .article-text .text p {margin: 0 !important;}
[id^="centerpain_0_mainleftpain_0_ctl06"] br {display: none !important;}
[id^="centerpain_0_mainleftpain_0_ctl06"] hr {margin: 0 !important;}
.paragraphList,
.photoListBox .articleList .title,
.photoListBox .articleListNoPhoto .title
 {padding: 0 !important;}
.photoListBox .photoList,
.photoListBox .articleList
 {width: auto !important; height: auto !important;}
.photoListBox .articleList {float: none !important;}

/*-- 2clolumns topic by category on top --*/
.id_contents.index-articleList.post .floatL
 {width: 100% !important; margin: 0 -250pt 0 0 !important;}
.id_contents.index-articleList.post .floatL>*
 {width: auto !important; margin: 0 250pt 0 0 !important;}
.id_contents.index-articleList.post .floatR {width: 250pt !important;}

}
