css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

/* sets font */
body, span#navtop, td.replyhl, td.reply, a, a:hover, a.quotejs, a.quotejs:hover, a.quotelink, span.filesize, blockquote, div, from {font:17px calibri !important;} 

/* hides ads */
div.logo img{display:none !important;}
iframe{display:none !important;}
center{display:none !important;}

/* hr */
hr{display:none !important;}
form hr{display:block !important;}
hr{height:1px !important;border:none !important;background:#999 !important;}


/* links */
a{text-decoration:none  !important; color:#cd6889 !important;}
a.quotejs:hover, a:hover{font-weight:normal !important;text-decoration:underline !important;} 
a:visited {        color:#cd6889 !important; }
a:hover {        color:#000000 !important; }

/* removes ugly borders on page overview [0,1,2...] */
table,td{border:none !important;}
td.rules, div.logo br, div.logo font[size="1"]{display:none !important;}

/* post area */
input,textarea{border:#aaa 1px solid !important;margin-right:2px !important;}
div.postarea{position:fixed !important;width:472px !important;height:0px !important;opacity:0.0 !important;right:0px !important;}
div.postarea:hover{opacity:1 !important;}
td.postblock{vertical-align:top !important;font-size:15px !important; border: solid 1px #eee0e5 !important; color:#8b5f65 !important; background:#fff0f5 !important;}

.postblock {
        background:#fff0f5 !important;
        color:#8b5f65 !important;
      /*  font-weight:800 !important; */
	border: solid 1px #eee0e5 !important;
	padding: 0 !important;
	margin: 0 !important;
}
/* blurry thumbnails fix */

img[md5] {

height: auto !important;

width: auto !important;

}
.postername, .commentpostername {
	color:#cd6889 !important;
	font-weight:bold !important;
}
.postertrip {
	color:#8b7765 !important;
}
.oldpost {
        color:#8b7765 !important;
        font-weight:800 !important;
}
.omittedposts {
        color:#8b7765 !important;
}
.reply {
	background:#fff0f5 !important;
	color:#8b5f65 !important;
	border: solid 1px #eee0e5 !important;
	padding: 0 !important;
	margin: 0 !important;
}
html, body {
         background:#fffaf0 !important;
         color:#8b7765 !important;
	}
hr {	border: 0 !important;
	color:#eee0e5 !important;
	background-color:#eee0e5 !important;
	height: 3px !important;
}
th {

background-color:#eee0e5 !important;

}

}
