css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bitbucket.org") {

    body, #footer-logo {
        background-color : #222;
        color : #888;
    }
    h1,h2,h3,h4,h5,h6{
        color : #888 !important;
    }
    
    a.execute{
        color : #45a0ff;
    }
    
    /* Sidebar */
    .aui-sidebar .aui-sidebar-wrapper, .aui-sidebar-submenu .aui-sidebar-wrapper{
        background-color : #333 !important;
        color : #888 !important;
        border-color : #555 !important;
    }
    
    .aui-navgroup-horizontal{
        background-color : #333;
        border-color : #222;
    }
    .aui-navgroup-horizontal .aui-nav > li a:hover{
        background-color : #555;
    }
    .aui-page-panel, #repo-getting-started-fixed .tab-pane{
        background-color : #444;
        border-color : #333;
    }
    /* Issue Tracker item */
    table.aui > tbody > tr, table.aui > tfoot > tr{
        background-color : #555;
        color : #888;
        border-color : #333;
    }
    table.aui.aui-table-interactive > tbody > tr:hover{
        background-color :#333;
    }
       
}
