 * Updated     : June 5th, 2014
 * Version     : 1
 * Description : Remove right bar and resize columns for better visibility
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 * Warning     : Font (Calibri) only available on Windows
 *****************************************/
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("facebook.com") {
    
    div#mainContainer{
        margin-left:100px !important;
    }
    
    div#rightCol{
        display:none !important;
    }
    
    div#contentArea{
        width:786px !important;
        margin-left:0px !important;
        margin-right:auto !important;
        font-family:Calibri !important;
    }
   
    div#leftCol{
        width:250px !important;
        word-wrap:normal !important;
        margin-left:-100px !important;
    }
    
    div.linkWrap.hasCount{
        max-width:230px !important;
    }
    
      div.linkWrap.noCount{
        max-width:230px !important;
    }
    
    p,span.userContent{
        font-size:16px !important;
    }
    
    div.fbTimelineUFI.uiCommentContainer,ul.UFIList{
        font-size:13.5px !important;
    }
    
    div.photoUfiContainer,span.UFICommentBody{
        font-family:Calibri !important;
    }
    
    span.hasCaption{
        font-size:16px !important;
    }
}
