@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
body {
 background: #222 !important;
 color: #E6C06E !important;
 font-size: 11px !important;
 font-family: Tahoma !important;
}

a {
 color: #808768 !important;
 text-decoration: none !important;
}

hr {
 display: none !important;
}
#navtop a {
 color: #314746 !important;
 }
#navtop {
 position:fixed !important;
 background: #EDE993 !important;
 width: 100% !important;
 text-align: center !important;
 padding: .4em !important;
 top: 0;
 z-index: 30;
 font-size: 11px;
}

#navtop a:hover {
 font-weight: bold !important;
}

#navbot {
 display: none !important;
}

.logo,.logo font b span{
 color: #808768 !important;
 font-weight: normal !important;
}

.logo a {
 color: #fff !important;
 text-decoration: none;
 font-weight: bold !important;
}

.quotejs {
 color: #808768 !important;
 font-weight: bold !important;
 font-size: 10px !important;
}

.filesize {
 color: #808768 !important;
}

.filesize a{
 color: #808768 !important;
 text-decoration: none !important;
 font-weight: bold !important;
}

.quotejs a {
 color: #808768 !important;
 font-size: 10px !important;
}

blockquote {
 color: #808768 !important;
 text-transform: lowercase;
}

.postarea form {
 margin-left: 55% !important;
 position: fixed !important;
 z-index: 40 !important;
 margin-top: 48% !important;
}

.inputtext, textarea {
 width: 200px !important;
 font-size: 11px !important;
 color: #808768 !important;
}

.rules{
 display: none !important;
}

td.postblock {
 background: transparent !important;
 border: 1px !important;
 color: #808768 !important;
 text-align: right !important;
}

.postblock {
 font-size: 11px !important;
}

td.reply {
background-color: #111 !important;
width: 300px !important;
padding: .2em !important;
border: 0px solid #808768 !important;
color: #808768 !important;
z-index: 20 !important;
float: left !important;
position: relative !important;
}

td.replyhl {
background-color:#555 !important;
width: 100% !important;
padding: .2em !important;
border: 0px solid #555 !important;
color: #fff !important;
-moz-border-radius:3px !important;
}

label {
 color: #808768 !important;
}

.quotelink {
 color: #808768 !important;
 text-decoration: none !important;
 font-weight: bold !important;
 font-size: 11px;
}

.postername, .commentpostername, .omittedposts, .abbr {
 color: #E6C06E !important;
}

.filetitle, .replytitle {
color: #E6C06E !important;
font-size: large;
}

.postertrip, .linkmail {
 color: #d49090 !important;
 text-decoration: none !important;
}

.doubledash {
visibility: hidden !important;
}

.unkfunc {
 color: #8da971 !important;
}

IMG[id^="4chan_ext_"], IMG[class^="4chan_ext_"], SPAN[id^="4chan_ext_"], DIV#twi_table_div {
opacity: .75 !important;
}

.deletebuttons {
 color: #61664F !important;
}

th {
 background: #555 !important;
 padding: .2em !important;
 font-size: small;
 width: 200px !important;
}

#hd {
display: none;
}

.boxbar {
background: #61664F !important;
color: #808768 !important;
}

.box-outer {
border: none !important;
background: #61664F !important;
color: #808768 !important;
}

.box-inner {
 background: #61664F !important;
color: #808768 !important;
}

#filter-button, #option-button {
display: none;
}

#ft{
display: none;
}

.pages, .pages a {
 background: transparent !important;
 border: none !important;
 color: #808768 !important;
}

.pages td, .pages td b {
color: #61664F !important;
font-weight: bold !important;
}

#recent-images li{
 background: #314746 !important;
}

#footer {
 display: none !important;
}

[class='4chan_ext_hiddenthreadinfo'] span.reply{
 background: none !important;
 border:0 !important;
 color: #314746 !important;
}
}
