@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
    
html,body{
    font-family:Calibri !important;
}
    
    div.topbar.js-topbar{
        font-family:Calibri !important;
    }
    
    a.js-nav{
        font-size:14px !important;
    }
    
    div.content{
        font-family:Calibri !important;
    }
    
    p.js-tweet-text.tweet-text,a.twitter-hashtag.pretty-link.js-nav{
        font-family:Calibri !important;
        font-size:16px !important;
    }
    
    strong.fullname.js-action-profile-name.show-popup-with-id,b.ProfileTweet-fullname.u-linkComplex-target,strong.fullname,a.twitter-timeline-link{
        color:#ff5a5a !important;
    }
    
    span.at,span.ProfileTweet-screenname.u-inlineBlock.u-dir,a.twitter-atreply.pretty-link{
        color:#ff7d7d !important;
    }
    
    div.dashboard.dashboard-right{
        display:none !important;
    }
    
    div.module.trends{
        display:none !important;
    }
    
    div.Grid-cell.u-size1of3{
        display:none !important;
    }
    
    span.text{
        display:none !important;
    }
    
    h1.Icon.Icon--bird.bird-topbar-etched{
        margin-left:150px !important;
    }
    
    div#timeline.content-main,div.Grid-cell.u-size2of3{
        width:700px !important;
    }

}
