css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("walmart.com"),
domain("walmartimages.com"),
domain("walmartwom.com")

{

/*text&background*/

html, body, thead, font

{
color: white !important;
background: #4D4D4D !important;
}

.BodyXS, .BodyXSBold, .BodySLtgry2, .BodyS, .BodySBold, .BodySGrey,   .BodyM, .BodyMBold, .BodyL, .BodyLBold, .BodyXL, .BodyXLBold, .Body14XL, .Body14XLBold, .Body2XL, .Body25XLGray, .Body25XLBoldGray, .Body3XL, .Body4XL, .Body5XL, .Body5XLBoldGry, .Body6XL, .PriceMItalicGry

{
  color: #FFFFFF !important;
}

.productTitle, #wc-aplus, #wc-aplus div.wc-document-title, #wc-aplus div.wc-document-description, #wc-aplus h2, #wc-aplus h3, #wc-aplus h4, #wc-aplus h5, #wc-aplus small, #wc-aplus table.wc-data-table tr.wc-header-row, #wc-aplus table.wc-data-table tr th, #wc-aplus table.wc-data-table tr.wc-odd-row td.wc-first-column, #wc-aplus table.wc-data-table tr.wc-odd-row td.wc-inbetween-column, #wc-aplus table.wc-data-table tr.wc-odd-row td.wc-last-column, #wc-aplus table.wc-data-table tr.wc-even-row td.wc-first-column, #wc-aplus table.wc-data-table tr.wc-even-row td.wc-inbetween-column, #wc-aplus table.wc-data-table tr.wc-even-row td.wc-last-column

{
  color: #FFFFFF !important;
}

/*links*/

a

{
color: #9D9D9D !important;
}

/*highlight*/

textarea, pre

{
background: #333333 !important;
}

}
