css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hitbox.tv")
{

:root
{
    --collink: /*[[linkcolor]]*/;
    --colbutton: /*[[buttoncolor]]*/;
}

body
{
    background-color:#0d0d0d !important;
    color:#cccccc !important;
}

a
{
    color:var(--collink) !important;
}
a:hover
{
    text-decoration:underline !important;
}
.ui-slider-range
{
    background-color:var(--collink) !important;
}

.homeContent section
{
    border-color:#262626 !important;
}

h1, h2, h3, h4, h5, h6, h7, p
{
    color:#ccc!important;
}

.tName
{color:#555!important;}
.tStatus
{color:#ccc!important;}

button
{
    background-color:var(--colbutton) !important;
    color:#eeeeee !important;
    border-width:0px !important;
    border-color:#262626 !important;
}

.followBubble
{
    background-color:#000000 !important;
    border-width:0px !important;
    border-color:#262626 !important;
    color:#e5e5e5 !important;
}

button.following
{
    background-color:#000000 !important;
    color:#cccccc !important;
}

.title-title
{
background-color:#0d0d0d !important;
color:#e5e5e5 !important;
}
.name
{
font-weight:bold !important;
}
.name.ng-binding.ng-scope
{
font-weight:bold !important;
color:#e5e5e5 !important;
}
.status
{

}
.label
{
border-color:#e5e5e5 !important;
color:#e5e5e5 !important;
}

.detailContentBtn
{
color:#555 !important;
border-color:#0d0d0d !important;
}

.detailContentBtn.active
{
color:#aaaaaa !important;
border-color:#aaaaaa !important;
}

.detailContentBtn:hover
{
color:var(--collink)!important;
border-color:var(--collink)!important;
}

.viewers
{
color:#e5e5e5 !important;
}

.detailInner
{background-color:#080808!important;}
.userContent
{background-color:#080808!important;}

.meta
{
    background-color:#0d0d0d !important;
    border:none !important;
    border-color:#333333 !important;
}

.logo
{
border-color:#333333 !important;
}

/* CHAT */

.chat
{
background-color:#0d0d0d !important;
}

.chat-compact-time
{
color:#cccccc !important;
}

.message
{
color:#cccccc !important;
}

.message a
{
font-weight:bold !important;
}

.textarea-wrap
{
background-color:#000000 !important;
border-color:#333333 !important;
}

.chat-input
{
color:#cccccc !important;
}

.chat-status-message
{
color:#cccccc !important;
}

/*dropdown menus*/

.dropdown-menu
{
background-color:#000000 !important;
border-color:#333333 !important;
color:#cccccc !important;
}

.emote-nav
{
background-color:#0d0d0d !important;
border-color:#333333 !important;
}

.ng-scope.active
{
background-color:#2f2f2f !important;
}

.settings-slider
{
background-color:#555555 !important;
}

/* popups */

.chat-sticky
{
background-color:#0d0d0d !important;
}

.pollWrap
{
background-color:#0d0d0d !important;
}



/* NAVBAR */

.userSubNav .head:hover, .userSubNav .sub:hover, .userSubNav .hoverG:hover, .nav .item:hover
{
    color:var(--collink) !important;
}
.subNav
{
    background-color:#1A1A1A !important;
}



/* LOGO */
.logoIco
{
    background-image: url('http://i.imgbox.com/t2d44WtJ.png') !important;
}

/*[[mininavbar]]*/

}

style_setting_options.value:
/* RETRACTABLE NAVBAR */

.channel .nav, .video .nav
{
    width: 1em !important;
    z-index:903 !important;
}

.channel .nav:hover, .video .nav:hover
{
    width: 4em !important;
}

.channel .view, .video .view
{
    left: 1em !important;
    box-shadow:none !important;
}

.channel .subNav, .video .subNav
{
    
    padding-left:6em !important;
    left:0em !important;
    max-width:36em !important;
}
.channel .userSubNav, .video .userSubNav
{
    padding-left:6em !important;
    left:0em !important;
    width:16.5em !important;
}

.channel .userSubNav .logout, .video .userSubNav .logout
{
    left:6em !important;
}

.channel .subNav .scroll, .video .subNav .scroll
{
    margin-left:5em !important;
}
