css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.solarmovie.is") {

/*BG:*/
body {
    background-color: #000 !important;/*yes kind of*/
    color: rgb(0, 250, 0);
    cursor: default;
}

div#content {
background-color: #000 !important; /*yes the other third turned blk*/
}

div.relative {
background-color: #000 !important; /*yes last third turned blk*/
}
/*minor white fix*/
html {
background-color: #000 !important;
}


/*Header*/
.headerpage {
    background: none repeat scroll 0% 0% rgb(0, 0, 0); /*yes*/
    border-top: 3px solid rgb(40, 40, 41);
}

/*removed Footer(cause it doesn't really serve a purpose)*/
#footer {
    display: none !important;
}

/*removing useless footer notice*/
div#footer > p {
display: none !important;
}
/*removing twitter timeline*/
dic#sidebar > a.twitter-timeline {
display: none !important;
}

/*search bar(input)*/
/*BG*/
.searchBoxHeader .searchField {
    width: 339px;
    height: 24px;
    font-size: 15px;
    font-family: "Lucida Grande",Arial,Helvetica,Verdana,sans-serif;
    float: right;
    background: none repeat scroll 0% 0% rgb(0, 0, 0);
    border-radius: 3.1px 3.1px 3.1px 3.1px;
    border: medium none;
    border-color: #fff;
    padding: 2px 10px 3px;
    margin: 1px 5px 0px 0px;
    line-height: 100% !important;
    color: rgb(0, 255, 0); 
}
/*focussed*/
input:focus:invalid, textarea:focus:invalid {
    color: rgb(5, 175, 39);
    box-shadow: 0px 0px 6px rgb(5, 175, 39);
}
/*Search Button on the right*/
.searchBoxHeader .searchButton {
    background-color: rgb(70, 71, 70);
    background-image: linear-gradient(to top, rgb(71, 91, 105), rgb(70, 71, 70));
    text-shadow: -1px 0px 0px rgb(6, 152, 19);
    border: medium none;
    width: 98px;
    float: right;
    height: 30px;
    margin-top: 1px;
    border-radius: 3.1px 3.1px 3.1px 3.1px;
    color: rgb(255, 255, 255);
    text-align: center;
    font-size: 15px;
} /*the hover needs a fix*/

/*Hover*/



/*Sidebar second child*/
.recentItems li:nth-child(2n) {
    background: none repeat scroll 0% 0% rgb(20, 20, 20); /*yes*/
}
/*sidebar header*/
div#sidebar > div.SearchesBlock > h3 {
background-color: rgb(30, 30, 30);
color: #0f0 !important;
font-weight: bold !important;
}

/*listed threads(sidebar)*/
.recentItems li .recentItemTitle {
    color: rgb(0, 170, 0);
}

/*new series header*/
.tabCategories a {
    color: rgb(109, 109, 109);
    border-top: 1px solid rgb(182, 182, 182);
    border-bottom: 1px solid rgb(182, 182, 182);
    border-right: 1px solid rgb(182, 182, 182);
    font-weight: bold;
    transition: background 0.1s linear 0s;
    font-size: 14px;
    text-transform: uppercase;
    text-decoration: none;
    display: block;
    text-align: center;
}
.Button, .tabCategories a {
    text-decoration: none;
    -moz-box-sizing: border-box;
    font-family: 'Lucida Grande',Tahoma,Verdana,Arial;
    font-size: 12px;
    color: rgb(109, 109, 109);
    background-color: rgba(40, 40, 40, 0.7); /*yes all but the active one*/
    transition: all 0.2s ease-in-out 0s;
}

/*active one*/
.tabCategories ul li.active a {
    color: rgb(0, 255, 0);
    background: none repeat scroll 0% 0% rgb(50, 50, 50); /*yes*/
}

/*sidebar container*/
div#sidebar > ul.tagContainer {
background-color: #000 !important; /*yes*/
}
/*tags*/
.tagContainer li a {
    border: 1px solid rgb(157, 157, 157);
    background-color: rgb(39, 39, 39);
    background-image: linear-gradient(to top, rgb(57, 57, 57), rgb(39, 39, 39));
    color: rgb(255, 255, 255);
    text-shadow: -1px 1px 0px rgb(115, 115, 115);
    text-decoration: none; /*yes*/
}

/**Linklist**/
/*transparent Overlay*/
.dataTable tbody tr, .modevents tbody tr {
    background-color: rgb(20, 20, 20); /*yes*/
}
/*solid columns*/
/*Quality top*/
.dataTable thead tr th, .dataTable thead tr td, .dataTable tfoot tr th, .dataTable tfoot tr td, .modevents thead tr th, .modevents thead tr td, .modevents tfoot tr th, .modevents tfoot tr td {
    color: rgb(0, 255, 0); /*yes*/
    font-weight: bold !important;
}
/*odd tops*/
.dataTable thead tr .oddCell div, .dataTable tfoot tr .oddCell div, .modevents thead tr .oddCell div, .modevents tfoot tr .oddCell div {
    color: rgb(0, 255, 0);
    background-color: rgb(20, 20, 20); /*top*/
    text-shadow: none !important;
    font-weight: bold !important;
}
/*1-vote&age cell*/
.dataTable tbody .qualityCell, .modevents tbody .qualityCell {
    font-size: 12px;
    color: rgb(255, 255, 255); /*text(global)*/ 
    text-align: left;
    padding-left: 15px;
}

/*-->BG*/
.dataTable tbody .oddCell, .modevents tbody .oddCell {
    background-color: rgb(10, 10, 10); /*global for 2nd child cell*/
}

/*2-Quality cell*/
/*adopted from the row-defined colors*/

/*SEO container alias Keyword box*/
.tagContainer, .tagSeoContainer {
    border-radius: 6px 6px 6px 6px;
    border: 1px solid;
    border-color: #fff;
    background-color: rgb(0, 0, 0);
    padding: 5px;
    display: block;
    overflow: auto;
    color: #0F0 !important; /*since the keywords themselves are hyperlinks this only applies to special characters*/
}

/*Keywords*/
div.tagSeoContainer > a {
    color: #0F0 !important;
}

/*plot*/
div.mediaDescription.latestTvEpisode {
    background-color: #101010 !important;
}
/*search results bar*/
div.calendarFilters.comingSoonPage {
    background-color: #101010 !important; /*not quite*/
}

ul.calendarSort.floatright {
    background-color: #101010!important; /*fixed*/
}

/*Comment box*/
.commentForm textarea {
    width: 500px !important;
    min-width: 500px;
    max-width: 500px;
    height: 120px;
    background: none repeat scroll 0% 0% rgb(0, 0, 0); /*yes*/
    border: 1px solid rgb(205, 205, 205);
    display: block;
    margin-bottom: 13px;
    color: #0F0 !important;
}

/*login to post comment - pop-up*/
div.ui-dialog.ui-widget.ui-widget-content.ui-corner-all.ui-front.ui-draggable.ui-no-title {
    background-color: #000 !important; /*yes*/
    color: #0F0 !important;
}

div.indentOneCol.twoCol.container {
    background-color: #000; /*almost*/
}

div#ui-id-2.ui-dialog-content.ui-widget-content {
    background-color: #000;
}
/*notify container*/
.ui-pnotify.spinner .ui-pnotify-container {
    background: none repeat scroll 0px 0px rgb(245, 247, 248);
    border: 1px solid rgb(0, 0, 0);
    padding: 6px;
    cursor: default !important;
}

h3 {
    color: #0F0 !important;
}

/*Sidebar listing 1st child hover*/
/*under Construction*/

/*movie list hyperlinks*/
span.name > a {
    color: #0F0; /*yes*/
}

/*advanced search*/
div.searchBoxHeader > a {
    color: #009900;
}

/*tip for results*/
div.tablePseudoHeader.botmarg > a {
    color: #009900;
}

/*comment section*/
a.js-comment-author.plain {
    color: #0F0;
}

a.commentDate {
    color: #009900;
}

/*the counter (letters)*/
div#ui-1410713916145.counter.safe {
    color: #0F0;
}

/*avatar pic*/
.userPic img, .userPic.nohover:hover img {
    max-width: 100px;
    max-height: 100px;
    vertical-align: top;
    border: 1px solid rgb(0, 255, 0);
    border-radius: 3.1px 3.1px 3.1px 3.1px;
}

/*movie title*/
.newMovieBlock .content h1, .newMovieBlock .content h2 {
    font-size: 16px;
    text-shadow: -1px 1px rgb(0, 150, 0);
    color: rgb(0, 250, 0);
    margin-top: 1px;
    font-family: "Trebuchet MS",Helvetica,sans-serif;
    margin-bottom: 0px;
    border: medium none;
    padding-bottom: 0px;
}

/*year of release*/
.newMovieBlock .content h1 .year, .newMovieBlock .content h2 .year {
    color: rgb(0, 190, 0);
    text-decoration: none;
    margin-right: 5px;
}

/*additional info in listed format*/
.newMovieBlock .content .links li a, .newMovieBlock .content .links li span {
    color: rgb(0, 128, 0);
    text-decoration: none;
    vertical-align: middle;
    transition: color 0.2s linear 0s;
    text-shadow: none !important;
}

/*listed format (release Date and duration)*/
/*indicator*/
    div.overViewBox > dl > dt {
    color: #009900;
    }
/*integer*/
    div.overViewBox > dl > dd {
    color: #009900;
    }
    
/*info Box*/
    div.yourRating.ratingB > p.title {
    color: #009900;
    }
    
    div.solarRating.ratingB > p.title {
    color: #009900;
    }
    
    div.imdbRating.ratingB > p.title {
    color: #009900;
    }
    
/*series title*/
.newMovieBlock .content h1 > a, .newMovieBlock .content h2 > a {
    color: rgb(0, 250, 0);
}

/*episode*/
.newMovieBlock .content h1 .season_episode, .newMovieBlock .content h2 .season_episode {
    color: rgb(0, 158, 0);
    text-transform: lowercase;
    font-size: 15px;
    font-weight: bold;
    text-shadow: none !important;
}

/*categories(top)*/ /*all but the current one*/
.menuBoxHeader li a {
    display: inline-block;
    padding: 10px 10px 5px;
    text-decoration: none;
    color: rgb(0, 255, 0) !important;
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
    text-transform: uppercase;
    font-size: 13px;
    font-family: "Lucida Grande",Arial,Helvetica,Verdana,sans-serif;
    text-shadow: 0px 1px rgba(0, 0, 0, 0.97);
}

/*removing the not working "stream now" button*/
a.floatright.siteButton.partnerButton {
    display: none !important;
}

/*another useless button*/
a.siteButton.partnerButton {
    display: none !important;
}

/*login&register(top right)*/
/*login*/
a#login.js-check {
    color: #009900;
}
/*register*/
a#register_link {
    color: #009900;
}

/*Forum post date(thread)*/
.lightGrey, a.lightGrey {
    color: rgb(0, 153, 0); /*yes*/
}

/*post text*/
div.js-comment-text {
     color: #00BB00;
}

/*nav bar(small)*/
.tabs .tabNavigation {
    list-style: none outside none;
    margin: 4px 0px 15px !important;
    padding: 0px;
    height: 24px;
    border-bottom: 1px solid rgb(108, 108, 108);
    font-size: 12px;
    display: block;
}

/*selected*/
.tabs > ul > li.selected a {
    color: rgb(0, 234, 0);
}

/*unselected*/
.tabs > ul > li a {
    display: inline-block;
    padding: 2px 9px;
    height: 21px;
    text-decoration: none;
    color: rgb(0, 78, 0);
    position: relative;
}

/*removed the annoying Facebook-box*/
iframe#facebookIframe {
    display: none !important;
}
/*and the twitter timeline*/
a.twitter-timeline {
    display: none !important;
}

/*Main page*/
a.coverImage > span.cover > ul  li {
    color: #00cc00; /*yes*/
}

/*long list*/
.threeColList li a.mainLink {
    color: rgb(0, 150, 0);
    max-width: 100%;
    -moz-box-sizing: border-box;
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
    text-decoration: none;
    border-bottom: 1px solid transparent;
    vertical-align: middle;
}

/*main page tabs*/
.Button, .tabCategories a {
    text-decoration: none;
    -moz-box-sizing: border-box;
    font-family: 'Lucida Grande',Tahoma,Verdana,Arial;
    font-size: 12px;
    color: rgb(10, 190, 10);
    background-color: rgba(24, 24, 24, 0.7);
    transition: all 0.2s ease-in-out 0s;
}

/*watch movie block*/
.watchMovieBlock {
    background-color: #000;
}

.watchMovieBlock > strong > a.plain {
    color: #0F0;
}
/*title*/
div#content > h1.withLinks > a {
    color: #0F0;
}

/*previos&next link*/
.withLinks .linksWrapper a {
    color: rgb(0, 110, 0);
    padding-left: 8px;
    vertical-align: middle;
}

/*show more (links)*/
.typicalGrey {
    border: 1px solid rgb(157, 157, 157);
    background-color: rgb(100, 100, 100);
    background-image: linear-gradient(to top, rgb(127, 127, 127), rgb(90, 90, 90));
    color: rgb(255, 255, 255);
    text-shadow: -1px 1px 0px rgb(115, 115, 115);
    text-decoration: none;
}

/*actual video page*/
/*1*/
/*2*/
.frameWrapper .wrapper {
    display: block;
    margin: 0px auto;
    width: 100%;
    background-color: #000; 
}
/*3*/
.ratingsWrapper {
    width: auto;
    padding-left: 104px;
    padding-top: 5px;
    padding-bottom: 5px;
    overflow: auto;
    background-color: rgb(0, 0, 0);
    background-image: linear-gradient(to top, rgb(0, 0, 0), rgb(0,0,0));
}
}
