@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {



/*General Changes ****************************************************************************/
body, :root{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg) bottom !important;}

#output, .bubbleview, #world td{
background-color: transparent!important;}

#deviant .blues-bar{
padding-bottom: 0!important;}

#deviant ul.iconset-gruser li.active, ul.iconset-gruser li.active{
background: -moz-linear-gradient(center top , #E1E7E1, #c9dbec)!important;
box-shadow: 0 2px 0 #c9dbec, 0 1px 0 rgba(255, 255, 255, 0.8) inset!important;}

#comment .ch-ctrl .ch-ctrl{
background: rgba(223, 234, 223, 0.6)!important;}

.ccomment i.l {
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg) no-repeat !important;}

.tt-fh .tt-w .tt-bb { display: none !important; }
.tt-fh .tt-w .shadow { background: transparent !important; }

.gb.gr3, .gr-countdown .gr-body, .gr-affiliates .gr-body, .gr-aggregation-list .gr-body, .gr-polls .gr-body, .gr-social .gr-body, .gr-gallery_folders .gr-body,
.gr-aboutusbox .gr-body, .gr-membox .gr-body, .gr-aboutus-filterbox .gr-body,  .gr-twitter .gr-body, .gr-groupstats_monthly .gr-body,
.gr-customtext .gr-body, .gr-groupstats_weekly .gr-body, .gr-info .gr-body, .gr-devmeet .gr-body, .gr-faves_contributors .gr-body, .gr-recent_polls .gr-body,
.gr-recent_devmeets .gr-body, .gr-bloggers .gr-body, .gr-collections .gr-body, .gr-recent_blogs .gr-body, .gr-countdown .gr-midbody, .gr-visitors .gr-body,
.gr-group_list .gr-body, .gr-aboutme .gr-body, .gr-featured_deviation .gr-body, .gr-newest .gr-body, .gr-watchers .gr-body, .gr-forum .gr-body, .gr-delicious .gr-body,
.gr-shoutbox .gr-body, .gr-ijustsold_prints .gr-body, .gr-remote_picture .gr-body, .gr-shop_prints .gr-body, .gr-friends .gr-body, .gr-featured_prints .gr-body,
.gr-recent_journals .gr-body, .gr-critique .gr-body, .gr-donations .gr-body, .gr-commissions .gr-body, .gr-wishlist .gr-body, .gr-faves .gr-body{
background: rgba(223, 234, 223, 0.4)!important;}

.gm-no-content-thumbs{
background-color: transparent!important;}




/*Front Page / Browsing Galleries, Groups ****************************************************/
.browse-top-bar, .browse-container, .browse-content, .browse-left-bar{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}


/*Link Colors*/
.browse-results-page .details a{
color: #fff!important;}

.browse-results-page .details .category, .browse-results-page .details .commentcount, 
.browse-results-page .details .category a, .browse-results-page .details .commentcount a{
color: #8ca7ba!important;}

.browse-facet a.selected, .autocomplete-popup-results a.selected{
color: #72a7cc!important;}


/*Navigation Sidebar*/
.browse-left-bar{
background: transparent!important;}

.browse-left-bar li .selected{
background: #fff!important;}

.browse-left-bar li .selected small{
background-color: #72a7cc!important;
color: #fff!important;}


/*Commission Portal*/
.commissions-portal .journal-browse{
background: transparent!important}

.commissions-portal .rightCol, .commissions-portal .waterfall{
background: rgba(223, 234, 223, 0.5)!important;}





/*Daily Deviations Page *********************************************************************/
#dailyDevs, #dailyDevs .catbar {
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}

#dailyDevs h1{
color: #fff!important;
letter-spacing: 1px!important;
text-shadow: 2px 2px 4px #68839a!important; }

#dailyDevs .alt, #dailyDevTopNav{
background: rgba(223, 234, 223, 0.3)!important;}

#dailyDevBottomNav{
border-color: #9fbcd0!important;
background: transparent!important;}




/*Deviation Page *****************************************************************************/
.dev-page-container, .dev-right-col-bg{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}


.dev-page-below-tabs .ch-ctrl .ch-ctrl, .dd-container .gr-body, .critique-plate .gr-body, #deviation_critiques .gr-body{
background: rgba(223, 234, 223, 0.6)!important;}

.ddbox, .dd-container .gr3, .dd-container i.gt3 i, .dev-view-deviation, .critique-plate .gr, .critique-plate .gr3,
#deviation_critiques .gr-body .gr, #gmi-CCommentMaster .box{
background: none!important;}

#gmi-CCommentMaster .box{
margin-bottom: 40px!important;}

.dev-page-view .dev-description .text-ctrl {
border-top: 1px solid #6898b9!important;}




/*Message Center *****************************************************************************/

/*Messages*/
.messages, .messages .mczone, .messages-left{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}

.mczone-title, .pager-messages .header {
background: #e7e9ec!important;}

.mczone-empty, .mc-notice{
color: #81919f!important; }

.mcbox .ch-ctrl, .tabtext{
background: rgba(255, 255, 255, 0.5)!important;}

.mcbox-inner-full-poll .talkmessage, .mcbox-inner-full-comment .talkmessage, .mcbox-inner-full-deviationwide .talkmessage{
background: rgba(255, 255, 255, 0.4)!important;}

.votearea .mcb-vote {
background: rgba(255, 255, 255, 0.3)!important;}

.mcbox i.dd i{
border-color: #f1f6fa #f1f6fa transparent!important;}

.mcb-whoicon, .pager-messages .page2, .pager-messages, .pager2 a.f {
background: transparent!important;}

.page2 a.selected {
background: #498091!important;}

div.pager-messages i.icon {
background-image: url("http://fc01.deviantart.net/fs71/f/2013/338/7/9/mc2czmzh4_by_ginkgografix-d6wp961.gif")!important;}


/*Notes*/
#notes .notes-center, #notes .notes-center .footer, #notes .notes-left, #notes .message-folders {
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}


#notes .mcb-note-box {
background-color: rgba(223, 234, 223, 0.6)!important;}

#notes .gr-body, #notes li.note,  #notes .notesfmessage,  #notes .mcb-note-box{
background: rgba(223, 234, 223, 0.6)!important;}

#notes li{
border-bottom: 1px dotted #FFFFFF!important;}

#notes .pagination-wrapper  li{
border-bottom: none!important;}

#notes .folders{
background: transparent!important;}

#notes .notesuccess, #notes .notesuccess .mcb-note-box {
background: rgba(223, 234, 223, 0.3)!important;}




/*Profile Page *******************************************************************************/
#gruze-columns{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}

.blues-bar-ctrl{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg) bottom!important;}


/*.mods-column-ll .gr-body,.mods-column-rr .gr-body,*/
.mods-column-ll .gr3, .mods-column-ll .ch-ctrl .ch-ctrl, .mods-column-ll .f .a, .mods-column-rr .gr3, .mods-column-rr .ch-ctrl .ch-ctrl, 
.mods-column-rr .f .a, .gr-donations .gr-midbody{
background: rgba(223, 234, 223, 0.4)!important;}

.gr-midbody .gr{
border-color: #9fbcd0!important;}
 
/*.journal-wrapper .gr-body, .gr-headless .gr-body .gr {
background:transparent!important;}*/
 
.journal-footer, .journal-footer a{
color: #81919f!important; }

.more-journals-link {
color: #fff!important;}

.gr-commissions .ppp.main-listing .commission-listing:nth-child(2n){
background: rgba(223, 234, 223, 0.6)!important;}




/*Widget Area */
.gzone-label, .zone-holder .gruze-main{
background:transparent!important;}

.zone-holder #module-arrange-map .sealed-zone.ch .ch-ctrl, #user-edit-header .ch.ch-indi{
background: rgba(223, 234, 223, 0.4)!important;}




/*Gallery /Favourites Page *******************************************************************/
#gmi-GalleryEditor .left-sidebar, #gmi-GalleryEditor .gruze-main{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}

#gmi-GalleryEditor .left-sidebar .gr-body{
background: transparent!important;}

#gmi-GalleryEditor .ch-ctrl .ch-ctrl{
background: rgba(223, 234, 223, 0.6)!important;}

.gallery-categories li .selected{
background: #fff!important;
color: #72a7cc!important;}




/*Journal / Polls Page ***********************************************************************/

.journal-wrapper2{
background: inherit!important;}

#journal-moreoptions{
background: rgba(223, 234, 223, 0.6)!important;}

.ccwriter-journal #output{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg) bottom !important;}

.tt-fh .details small span, .tt-fh .details small a{
color: #7b9ebe!important;}

#journal_sidebar .gr-body{
background: transparent!important;}




/*Critiques***********************************************************************************/
#critique-fullpage-page .gr-body, #critique-fullpage-page i.gt3 i,  #critique-fullpage-page i.gr3 {
background: rgba(223, 234, 223, 0.6)!important;}







/*Print Pages ******************************************************************************/
.wood-pane, #shopCatBar{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg) bottom !important;}

#print, #print .catbar{
background: url(http://fc06.deviantart.net/fs71/f/2013/322/f/9/cp_2013__pattern_by_ginkgografix-d6up72b.jpg) bottom !important;}

#deviation-links{
background: rgba(223, 234, 223, 0.3)!important;}

.altview, .altaltview{
background: rgba(223, 234, 223, 0.6)!important;}

#print div.iconset-art i{
background-image: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}

.prints .gr-body, #eoLoading {
background:transparent!important;}

.prints .gr-box{
color: #81919f!important;}

.prints li .selected{
background: #fff!important;
color: #72a7cc!important;}

.prints .details a{
color: #81919f!important;}





/*Correspondence Page ***********************************************************************/
#correspondence-page .gruze-sidebar{
background: transparent!important;}

#correspondence-page .catbar{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg) bottom !important;}
 
#correspondence-page .guideblock, #correspondence-page .gruze-sidebar{
border: none!important}

#correspondence-page .match-title{
color: #36526b!important;}

#correspondence-page h1{
color: #fff!important;
letter-spacing: 1px!important;
text-shadow: 2px 2px 4px #68839a!important;}




/*Groups ************************************************************************************/
#group .gruze-sidebar, #group .shineblock, #group .blues-bar-ctrl,#group .ppp, #group .backroomblock, .gr-membox .gr-body, .layout-changer{
background: transparent!important;}

#groupsDirRight{
background: url(http://fc04.deviantart.net/fs70/f/2014/257/6/e/red_galaxy_michalv_109567_by_l_gotta_believe-d7z76i5.jpg)!important;}

#group .gruze-sidebar ul.list li.a{
background: rgba(223, 234, 223, 0.3)!important;}

#group .gr-wonkwonk{
background-color: rgba(223, 234, 223, 0.7)!important;}

#group .guideblock{
border-bottom: none!important;}

#group .guideblock .link-more, .groupEntry .readmore, .groupRightHeader{
background-color: #fff!important;}

.groupRightHeader h2{
color: #72a7cc!important;}

.groupEntry .groupStats, .groupEntry .details{
color: #fff!important; }


}
