css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
* Name        : Better VNDB.org
* Author      : tomchen <tomchen.org>
* Version     : 2.1
* Description : VNDB.org (The Visual Novel Database): add name in original language (mostly Japanese) after every game title link and character / voice actor / staff link; remove margin at the top of the page. See screenshots for details.
*/

@-moz-document domain("vndb.org") {

/* MAIN */

/* TOP AREA START */
#header h1, #header h1 a {
	font-size: 26px !important;
}
#header h1 a {
	color: #FFF !important;
	text-shadow: 1px 0px 20px #FFF;
	text-transform: capitalize;
    transition: color 0.5s ease-in-out;
}
#header h1 a:hover {
	color: #FFFFB0 !important;
	text-shadow: 1px 0px 20px #FFFFB0;
}
#menulist {
	top: 60px !important;
}
#maincontent {
	top: 39px !important;
}
#header {
	top: 16px !important;
	left: 100px !important;
}
/* TOP AREA END */

/* User profile page: Vote statistics - block width */
#maincontent > div:nth-child(3).mainbox > div.votestats {
	width: 830px !important;
}
#maincontent > div:nth-child(3).mainbox > div.votestats > table.recentvotes.stripe {
	width: 500px !important;
}

/* Home page: Recent Changes */
#maincontent > div.homepage > div:nth-child(1) > ul > li > span:nth-child(1) > a[title]:after,
/* Home page: Upcoming Releases */
#maincontent > div.homepage > div:nth-child(5) > ul > li > span > a[title]:after,
/* Home page: Just Released */
#maincontent > div.homepage > div:nth-child(6) > ul > li > span > a[title]:after,
/* Game page: Releases */
#maincontent > div.mainbox.vnreleases > details > table > tbody > tr > td.tc4 > a[title]:after,
/* Game page: Staff */
#maincontent #staff a[title]:after,
/* Game page: Character summary */
#maincontent div.charsum_list a[title]:after,
/* Game page: Relations (Prequel etc.) */
#maincontent div.vndetails > table dl > dd > a[title]:after,
/* Game page: Related anime */
#maincontent div.vndetails > table > tbody > tr > td.anime > abbr[title]:after,
/* /v page */
#maincontent div.mainbox.browse.vnbrowse > table > tbody > tr > td > a[title]:after,
/* /r page */
#maincontent > div.mainbox.browse:not(.history):not(.discussions) > table > tbody > tr > td.tc4 > a[title]:after,
/* User profile page: Vote statistics */
#maincontent > div:nth-child(3).mainbox > div.votestats > table.recentvotes.stripe > tbody > tr > td > a[title]:after,
/* User "My list" pages (and other similar pages maybe) */
tbody > tr > td.tc_title > a[title]:after,
/* Voice actor page (and other similar pages maybe) */
#maincontent > div.mainbox.browse.staffroles > table.stripe > tbody > tr > td.tc1 > a[title]:after,
#maincontent > div.mainbox.browse.staffroles > table.stripe > tbody > tr > td.tc3 > a[title]:after,
#maincontent > div.mainbox.browse.staffroles > table.stripe > tbody > tr > td.tc4[title]:after,
/* Game "characters" tab page */
#maincontent > div.mainbox > div.chardetails > table.stripe > tbody > tr > td > a[title]:after,
/* /c page */
#maincontent > form > div.mainbox.browse.charb > table > tbody > tr > td.tc2 a[title]:after,
/* /p/all page */
#maincontent > div.mainbox.producerbrowse > ul > li > a[title]:after,
/* /s page */
#maincontent > div.mainbox.staffbrowse > ul > li > a[title]:after
{
	content: " (" attr(title)")";
	color: #FFF;
}

}
