css:
@-moz-document domain("reddit.com") {
	#header {
		border-bottom: 5px solid #98c0ea;
		padding-left:75px;
	}

	#sr-header-area {
		display: none;
	}

	#header-bottom-left {
		height:70px
	}

	#header-img.default-header {
		margin-left: 15px;
	}

	.content, body.with-listing-chooser.listing-chooser-collapsed>.content, body.with-listing-chooser.listing-chooser-collapsed .footer-parent {
		margin-right:75px;
		margin-left:75px;
	}

	ul.tabmenu {
		position: absolute;
		bottom: 0;
		border:none;
	}

	.tabmenu li {
		margin:0;
		font-weight: lighter;
		color: #333;
		background-color: #eff7ff;
	}

	.tabmenu li a {
		color: #333;
	}

	.tabmenu li.selected a {
		border: none;
		background-color: #98c0ea;
		color: #333;
	}

	.tabmenu li.selected {
		padding: 10px 10px 0 10px;
		background-color: #98c0ea;
	}

	#header-img.default-header {
		margin-bottom: 3px;
		margin-top: 30px;
	}

	.entry {
		background: #eff7ff;
		padding: 15px;
	}

	.thumbnail {
		margin:0;
	}

	.midcol {
		padding: 15px 5px;
	}

	.link {
		margin-bottom:15px;
		margin-right:15px;
	}

	.link .rank {
		margin-top: 30px;
	}

	.thing .title {
		color: #333;
		font-size: small;
	}

	.thing .title:visited, .thing.visited .title {
		color: #999;
	}

	.side {
		margin: 5px 75px 0 15px;
	}

	.morelink a {
		color:#333;
		font-weight: lighter;
	}

	.morelink, .nub {
		background: #eff7ff;
		border:none;
		border-bottom: 5px solid #c5e2f9;
		height:40px;
		line-height: 40px;
	}

	.morelink:hover {
		background: #eff7ff;
		border:none;
		border-bottom: 5px solid #c5e2f9;
		color:#999;
	}

	.morelink a:hover {
		color:#999;
	}

	.nub {
		display: none;
	}

	#search input[type=text] {
		color: #666;
		text-align: center;
		height: 40px;
		line-height: 40px;
	}

	.goldvertisement {
		border:none;
		box-shadow: none;
	}

	.goldvertisement .inner {
		border:none;
	}

	.goldvertisement a {
		font-size:large;
		height: 40px;
		line-height: 40px;
		width: 100%;
		background: #fcf2b4;
		border:none;
		border-bottom: 5px solid #f7e794;
	}

	.goldvertisement a:hover {
		border-bottom: 3px solid #f7e794;
		-ms-transform: translateY(2px); /* IE 9 */
	   	-webkit-transform: translateY(2px);
	    transform: translateY(2px);
	    padding-bottom:2px;
	}

	.goldvertisement .progress .bar span {
		background-image:none;
	}

	.link .usertext .md {
		border:none;
		border-radius:0px;
	}

	.link.last-clicked {
		border: 1px solid #eff7ff;
	}
}
