css:
@-moz-document domain("tieba.baidu.com"){
 /*字体强迫症*/     
*{font-family:"Hiragino Sans GB W3","文泉驿微米黑","XinGothic-Regular","Microsoft Yahei"!important; }  
*:not(.badge_index):not(.d_badge_lv):not(.search_btn):not(.nav_item):not(a):not(em):not(span):not(strong),a[class*="j"]:not(.search_btn_enter_ba),a[target*="_blank"],.lzl_content_main{color:#274 !important; }/*全局字体*/
DIV[id*="post"]>a[target*="_"]:visited,SPAN[class*="lzl"]>a[target*="_"]:visited,span[class*="edit"]>a[target*="_"]:visited,strong>a[target*="_"]{color:#4cf !important; }/*已点击链接*/
    DIV[id*="post"]>a[target*="_"]:link,SPAN[class*="lzl"]>a[target*="_"]:link,span[class*="edit"]>a[target*="_"]:link,strong>a[target*="_"]{color:#f80 !important; } /*未点击链接*/    
.edit_font_color,span>strong{color:#c0c !important; }/*帖内红字变紫*/ 
.wrap2{
background:#e9f1f1!important;
}
/* 总体拉宽 */ 
.content {
	width: 98%!important;
}
/* 列表区域拉宽 */    
.content .main#contet_wrap {
    width: -moz-calc(100% - 238px)!important;  
}
/* 列表区域背景 */ 
.forum_content{
   background:rgba(255,255,255,.4) !important;
}
/* 列表拉宽 */    
.threadlist_li_right {
    width:-moz-calc(100% - 80px)!important;  
} 
/*会员位置*/
.vip_red{
padding:20px!important;
}
/*右侧*/
#aside {
    background:rgba(215,215,245,.4)!important; 
}   
.user_level{margin-left:50% !important;}
.rank{float:left !important;margin:5px -100px !important;}
.region_cnt{margin:0 !important;}
#balv_mod{padding:20px!important;}
.exp{width:200px !important;margin: 5px -100px!important;} 
.exp_bar{margin-right:0!important;}
.fMember_cnt{display:none !Important;}
/* 列表彩色背景 */     
#thread_list li:nth-last-child(4n+0){
   background-color:rgba(215,245,215,.4) !important;    
} 
#thread_list li:nth-last-child(4n+1){
   background-color:rgba(245,215,245,.4) !important;    
}
#thread_list li:nth-last-child(4n+2){
   background-color:rgba(245,245,215,.4) !important;    
}
#thread_list li:nth-last-child(4n+3){
   background-color:rgba(245,215,215,.4) !important;    
}    
/*鼠标经过效果*/
.clearfix>ul>li:not([class*="folder"]):hover,li>ul>li:hover,#topic_post_thread:hover{
    background:-moz-linear-gradient(top,rgba(55,55,55,.1),rgba(55,55,55,.1))!important;  
}      
/*内容页整体*/    
.l_container{
    width:-moz-calc(100% - 1.9%)!important; 
}
/*回复区*/    
.d_post_content_main{
    width:-moz-calc(100% - 150px)!important;}    
    
.l_post_bright .core_reply_wrapper,.pb_footer,#tb_nav,#pb_content,.left_section,div[class*="thread_theme"],.lzl_simple_wrapper,.lzl_panel_wrapper{
    width:100%!important;  
}
#j_core_title_wrap,.l_post.noborder.l_post_bright,l_post.noborder.l_post_bright,.l_post.l_post_bright,.sofa_post{
    width:-moz-calc(100% - 1px)!important;  
}
/*沙发*/    
#sofa_post>.d_post_content_main{
    width:-moz-calc(100% - 130px)!important;  
} 
/*楼中楼*/
.edui-container{width:100%!important;}
#j_editor_for_container,[class*="lzl"]>*>.edui-editor-body{float:right!important;width:99%!important;}
.lzl_edui_dialog_container{position:relative!important;left:-20px!important;height:0!important;}
.tbui_panel_content{width:100%!important;}
/*消息提醒调整*/    
.userbar{right:40px!important;}
.ui_bubble_up{width:130px!important;opacity:0.7;box-shadow:0 0 6px  #0d0!important;border-radius:0!important;}
#message_favts,.tb_msg_tip_rightpanel,#message_friendapply{display:none !important;} 
.ui_triangle_up,.tb_paper_wrap{display:none !important;}       
/*去除广告 */   
.userbar>ul>li:not(#j_u_username):not(.u_setting):not(.u_news):not(.u_login),#tbmall_score_region,.hgame_mod_theme2,.lot_wrap,#aside_ad,#j_ten_years,.poster_rewards,.poster_rewards_disabled,.tb_poster_placeholder,.banner_post,#pb_adbanner,[id*="game"],[class*="game"],[data-field*="game"],.worldcup_rank,.u_mytbmall,.tbnav_tab_list,.tbui_aside_float_bar,.d_icons,.d_nameplate,.save_face_bg ,[class*="fifa"],.profile_bottom,.pre_icon_wrap,td>p,.per_list_frs,[id*="BAIDU_CLB_AD"],#encourage_entry,#forum_recommend,#search_union_mod,[data-dauserid="37676788"]{display:none !Important;}
/*百度导航*/ 
#head{height:90px!important;background:transparent!important;margin-left:250px!important;}
.search_top{margin-top:-25px!important;}   
.search_logo{background-size:90% !important;margin:40px 0 -40px -120px !important;}
.search_ipt{background:none!important;}
.search_btn_wrap,.search_btn{background:#6dd690!important;}
.main_wrapper{margin-top:30px!important;}    
/*签到  */  
.card_top_right{position: absolute;top:10px;left:100%;z-index:555;margin-left:-210px!important;}
/*顶部浮动*/ 
[class$="search_main_fixed"],[class*="_title_wrap_bright tbui"]{display:none !important;}
    /*导航*/
    .nav_wrap,.space,#tb_nav{background:rgba(215,245,245,0.4)!important; }
    .focus,.j_tbnav_tab:hover,.star_nav_tab:hover{background:rgba(50,255,255,0.4)!important;margin:0 0 0 -2px!important; }   
    .tbnav_tab_inner{background:none!important; }       
    /*今日话题*/
    #topic_post_thread{background:rgba(215,215,215,0.4)!important; }
    #topic_thread_wrapper,.tt_guidetexture{background-color:transparent!important;}
    .tt_guidetexture{width:100%!important;}
    .live_thread_cnt{width:98.5%!important;}
    .live_item_wrapper,.live_item_cnt{width:100%!important;}
    /*↓明星贴吧*/
     /*整体*/  
    .core{
    width: 100% !important;        
    border: 1px solid #aaa !important;
    }    
     /*内容区域*/  
    .l_post.noborder,.l_post{
     background-size:100%!important;   
     width:100% !important;      
    }
     /*背景图片*/  
    .l_post.noborder,.l_post,#sofa_post{
     background-size:3000px!important;
     background-position:-401px!important;
    }        
    /*会员背景*/
[class^="post_bubble_"]{background:transparent!important;}
.post_bubble_top,.post_bubble_bottom{height:0!important;}
		
/*↑明星贴吧*/    
    /*楼中楼位置*/
    .core_reply_wrapper{
     margin:0px !important;     
    }
    .lzl_simple_wrapper{
    float:right;
    } 
    /*签名档悬浮显示*/
    .j_user_sign:not(:hover){opacity:0;height:10px;transition:1.5s;}
    .j_user_sign:hover{opacity:1;transition:2.5s;}
    /*屏蔽下方自动显示滚动条,你特么逗我*/
   .user-hide-post-down{display:none !Important;}
/*吧装修*/
.card_top.clearfix {width:-moz-calc(100% - 200px)!important;}
    .forum_header,.card_top,.star_head,.plat_header.clearfix{
    background:url("/*[[bbb]]*/") repeat-x !important;
    background-size:auto 90px !important;} 
    .card_top{height:75px!important;} 
    .card_top_theme1,.content{border:0!important;}
}
@-moz-document regexp("^https?:\/\/tieba\.baidu\.com\/f.(?!ct).*"){
/*普通吧*/     
.card_banner,.star_banner{display:none!important;}    
.card_head,.card_head_img{height:80px!important;width:80px!important;top:-20px!important;}   
.card_title,.card_slogan,.card_info{left:-60px!important;}
/*明星吧头像*/      
.star_head{height:90px!important;}
.star_picbox{margin-top:110px!important;}
.star_picbox,.star_picbox img {width:85px!important;height:85px!important;}
.star_info{margin:150px 0 0 30px !important;}
.fans_numbox{margin-top:30px!important;}

/*软件吧头像*/
.j_platform_banner{display:none!important;} 
.plat_header.clearfix{height:63px!important;}
.plat_picbox,.plat_picbox img{width:85px!important;height:85px!important;}
.plat_picbox{margin-top:-24px!important;}
 } 
@-moz-document url-prefix("http://tieba.baidu.com/f?ct"),url-prefix("http://tieba.baidu.com/p"){ 
/*右侧信息,签到*/ 
    .right_section,[class*="theme2"] [class*="right"]{display:none !important;}    
/*帖内装修*/ 
.card_top.clearfix {width:-moz-calc(100% - 100px)!important;}
    .card_head,.card_head_img,.star_picbox,.star_picbox img,.plat_picbox,.plat_picbox img{height:80px!important;width:80px!important;top:-15px!important;}
[class*="theme2"]>*,.card_top{padding-top:0!important;height:85px!important;}
    .card_title{margin:15px 50px !important;} 
[class*="theme2"]{height:85px!important; padding:0!important;}   
.star_picbox{background:none!important;border:0!important;}
.plat_picbox{margin-top:-15px!important;}
[class*="header_left"]{margin-top:20px!important;}
.star_collect_body,#star_collect_time_axis_body{display:none !important;} 
}  
@-moz-document url("http://tieba.baidu.com/"){
.search_logo{background: url("http://tb2.bdstatic.com/tb/static-common/img/search_logo_7835b03.png") no-repeat !important;margin-top:15px!important;}}
@-moz-document url-prefix("http://tieba.baidu.com/f/"){#head{margin-left:0!important;}}
