css:
@-moz-document domain("www.biblionetka.pl") {
/*tło*/
div#top {background-color: #b5d8ee;}
div#container {background-color: #edf8ff !important;}
#srodek_wrapper {background: #edf8ff !important;}
body#katalog, #start, #ksiegarnia, #recenzje, #czytatnik, #forum, #polecksiazki, #none {margin-left: 30px; margin-right: 30px; background-color: #edf8ff !important;}

/* zamiana na stare logo*/
#ctl00_customizableLogo_imgLogo {
  box-sizing: border-box;
  background: url(http://imagizer.imageshack.us/a/img537/1879/q2NA33.gif) no-repeat;
  width: 422px;
  height: 104px; /* Height of new image */
  padding-left: 24422px; /* Equal to width of new image */
  margin-left:20px;} 

/*ocenione ksiazki w innym kolorze*/
  a.rated-book { color: #E66C00 !important; } /* kolor ocenionych */
  a.rated-book { font-weight:600 !important;  } /* pogrubienie ocenionych */
  
/* autorzy, nieocenione ksiazki, linki */
a, a:link,  a:visited, span.shortUrl, span.NewAnswerWarningDismissLink{ color: #385874; font-weight:400; font-size: 100%;}

/* kolor linkow do serii itp. */
h2 a { color: #385874 !important;}

/* link do tekstu recenzji */
#wsrodku .item h4 .uppercas a { color: #385874 !important; font-weight:800!important; }


/* menu gorne*/
div#topmenu a:link, div#topmenu a:visited { color: #385874  !important; }
div#topmenu { color: #385874; background: #b5d8ee !important; }

/*szukaj*/
#ctl00_SearchButton {color: #000000; background: #e5e5e5 !important;border: 4px outset buttonface; box-sizing: border-box; }
#ctl00_SearchButton:hover {color: #000000; background: #e5e5e5 !important;border: 4px inset buttonface; box-sizing: border-box;}
td.searchInputCell {border: solid 1px #385874;}
div#searchAlphabeticalLinks { color: #385874 !important;}
#ctl00_SearchType td { color: #385874 !important;}
div#szukaj a:link, div#szukaj a:visited { color: #385874 !important;}
div#szukaj {padding:10px !important; background-color:#b5d8ee !important;}
table#searchWrapper {border:none !important; }

/*okienko logowania*/
div#login {background: #b5d8ee !important; color: #385874; border-top: none;}
div#login a:link {color: #385874;}
#ctl00_loginUser_LoginButton {color: #000000; background: #e5e5e5 !important;border: 4px outset buttonface; box-sizing: border-box; box-shadow:none; font-weight: normal; text-transform: lowercase;} 
#ctl00_loginUser_LoginButton:hover {color: #000000; background: #e5e5e5 !important;border: 4px inset buttonface; box-sizing: border-box; box-shadow:none; font-weight: normal; text-transform: lowercase;} 
div#login .logpass {border: solid 1px #385874 !important; background-color: none !important;}

/*prawa kolumna*/
div#moja {  background-color: #b5d8ee !important;font-weight: 400; border: none; }
#moja p { background-color: #b5d8ee !important; color: #385874 !important; font-weight: bold; margin-top:-9px; border-bottom: solid 10px #edf8ff;}
#moja a {color:  #E66C00 !important; font-weight: bold !important; text-decoration: none !important;}
#moja li a { background-color: #b5d8ee !important; color: #385874 !important; font-size: 100% !important;font-weight: bold !important;}
#moja li a:hover {color: #6397c5 !important; font-size: 100% !important; text-decoration: none !important;}
#moja li.tutaj a {color: #385874 !important; background: #b5d8ee !important; text-decoration: underline !important;}
#moja ul li a:link, #moja ul li a:visited {background: none;}

/*lewa kolumna*/
div#lewemenu { background: b5d8ee; border:  none !important; margin-top:10px;}
 #navleft_rb li a {background: #b5d8ee;  text-align: left !important; font-size: 100% !important; font-weight: bold !important;  color: #385874;}
#navleft_rb  {background: #b5d8ee;}
 #navleft_rb li a:hover { color: #6397c5; text-decoration: none;}
div#lewemenuHeader {margin-top:-10px;}
div#lewemenuHeader .side-header {background: #b5d8ee; padding-top: 10px; margin-top:-9px; margin-bottom:10px !important;}
#navleft_rb .firstChild  a { color: #E66C00 !important; font-size: 120% !important;}
body#polecksiazki #polecksiazkimenu a {color: #b30066;background-color: #b5d8ee;}

/*menu glowne*/
body#start #startmenu a, body#katalog #katmenu a, body#ksiegarnia #ksiemenu a, body#recenzje #recemenu a, body#czytatnik #czytmenu a, body#forum #forumenu a,#navtop, li.menuActive a {  background-color: #edf8ff; color: #385874 !important; font-size:100% !important; font-weight: bold !important;} 
#navtop li a:link, #navtop li a:visited{color: #385874; background-color: #b5d8ee;  font-weight: bold !important;}
#navtop a:hover {background-color: #385874 !important; color: #edf8ff !important ; font-weight: bold !important;}

/*belka*/ 
div#kontekstowe a {font-size:80% !important;  font-weight: bold !important; color: #385874;  font-size:80% !important;}
div#kontekstowe_background  { background: #edf8ff ; font-weight: bold !important; margin-bottom:10px;} 
div#kontekstowe_background a:hover { font-size:80% !important;  font-weight: bold !important; background-color: #385874; color: #edf8ff !important;} 
#kontekstowe li a.tutaj{ background-color: #b5d8ee; color: #385874 !important; font-size:80% !important;  font-weight: bold !important;}
.siteInfo span {font-size:80% !important;  font-weight: bold !important; color: #385874;  font-size:80% !important;}

/*narzednik*/
div#small_menu { background: #def0fe !important; color: #385874 !important;  border: none; margin-top: 34px;} 
div#small_menu li a { background: #def0fe !important; color: #385874 !important; font-size: 100%; font-weight: bold;} 
div#small_menu_header { background: #b5d8ee !important; border: none; color: #385874 !important;}

/*nagłówki menu*/
.side-header{ color: #385874 !important; text-align: center; font-size: 140% !important; border-bottom: none !important; padding-top:10px;}

/*okienko opiekunow*/
div#patronProgressBarMain { margin-top: 10px; background: #b5d8ee; color: #385874; border: none;}
div#patronProgressBarMain p { background: #b5d8ee; margin: 0; padding: 0px; font-size: 12px}
div#patronProgressBarMain p:first-child a { color: #385874 !important; }
div#patronProgressBarMain a, span.shortUrl, span.NewAnswerWarningDismissLink  { color: #ff8431; }
#ctl00_PatronProgressBar1_patronBar {background: #f29400 !important;}

/*polecane recenzje*/
#recenbox ul li {background: none !important; border: none;  color: #385874 !important;}
#recenbox {background-color: #b5d8ee !important; margin-top: -10px; border: none; margin-top: 40px !important; font-size:90% !important; color: #385874 !important;}
#recenbox a {font-size:80% !important;  color: #385874 !important;}
div#recenbox p {border-bottom: solid 2px #def0fe !important; color: #385874 !important;}

/*srodek*/
div#wsrodku h1 {border-bottom: 5px solid #def0fe !important; color: #f29400; font-weight: 200 !important;} 
div#ctl00_MCP_articlePanel h1 {text-transform: none !important;}
div#wsrodku .item {background-color: #e7f5ff !important; border: none;}
div#wsrodku .infoitem {background-color: #def0fe !important;}
div#wsrodku .infoitem p {overflow:hidden; text-overflow:ellipsis;}
div#wsrodku .subitem {background-color: #b5d8ee;  color: #385874; border: none;}
div#wsrodku .subitem a {background-color: #b5d8ee;  color:#385874;}
div#wsrodku {background: #edf8ff;}
.statsTable tr:hover td{background: #b5d8ee !important;}

/*strona uzytkownika*/
div#wsrodku .contentHalfLeft h2 {background:  #b5d8ee !important; color: #fff !important; font-weight: 800 !important;} 
div#wsrodku .contentHalfRight h2 {background:  #b5d8ee !important; color: #fff !important; font-weight: 800 !important;} 
div#wsrodku .contentFullSize h2 {background:  #b5d8ee !important; color: #fff !important; font-weight: 800 !important;} 
div#wsrodku .contentHalfLeft, .contentHalfRight, .contentFullSize {background:  #b5d8ee !important;  border: none !important;} 
div#wsrodku .item h2 {background:  none !important; color: #fff; font-weight: 200 !important;}

/*forum*/
div#wsrodku .tabela_forum th {background:  #b5d8ee !important; color: #334c68 !important;} 
div#wsrodku .tabela_forum td {background:  #e3f5ff; font-size: 120%;}
div#wsrodku .td_sub {background:  #c3e4f7 !important;}
div#wsrodku h2 {color: #334c68 !important; font-weight: 400;}
div#wsrodku .td_lewy {background:  #edf8ff !important; color: black !important;}
div#wsrodku .td_lewy a { font-weight:600;}
div#wsrodku .td_bold {background:  #e3f5ff !important; color: black !important; font-weight: 400;}
div#wsrodku .td_male {background:  #e3f5ff !important; color: black !important;  font-size:80% !important;}
div#wsrodku .tabela_forum a {color: #385874 !important;} 
div#wsrodku .tbody {border: none !important;} 

/*komentarze*/
div#ctl00_MCP_answersTree .forum_odd, .forum_odd h3, .forum_odd h4 {background: #e7f5ff !important; border:none; font-size: 100% !important;}
div#ctl00_MCP_answersTree .forum_even, .forum_even h3, .forum_even h4 {background: #def0fe !important; border:none; font-size: 100% !important;}
div#d0 {border: none !important;}

/*rozniaste*/
input.zatwierdz, input.platnosci { background: #E66C00 !important; }
a#AddTags { background-color: #E66C00 !important;}
.sideBoxContent {border: none !important; display:none;}
div#container .adsbygoogle {display:none !important; visibility:hidden !important;}
div#stopka a { color: #385874 !important;}
div#stopka {background: #b5d8ee !important;}
div.likeButton:hover {border-color: #E66C00 !important;background: #fcf1cf}
#wsrodku p.likeButtonText { color:#444444;}
#wsrodku p.likeButtonText:hover { color:#000000;}
div#szukaj {background: #dcefff;}
div#lewakolumna, div#srodek_wrapper {background: #edf8ff !important;}
a.LTU_Author {background: #fcf7c0;}
a.LTU_Admin {color: #2b84d1 !important;}
img[src$="tick.png"] { height: 0px !important;width:0px !important;} 
img[src$="lightning.png"] {background:#cde4f2 !important;   border: solid 4px #c0e3f9; border-radius:7px;}
#ctl00_MCP_answerPanel tr th {background:#def0fe; border: none;}
.tabela_srodek tr th {background:#b5d8ee; border: none; color: #385874 !important;}
.tabela_srodek tr td {background:#edf8ff; border: solid 2px white; color: #385874 !important;}
.tabela_srodek {border: none !important;}
}
