css:
@-moz-document url-prefix("http://music.163.com/") {
@media screen and (max-width: 768px) {
  .m-playbar {
      margin-top: 0;
      top: -100px !important;
      background-image: linear-gradient(rgb(62,62,62), rgb(0,0,0));
      background-color: rgb(62, 62, 62);
      height: 100px !important;
  }
  
  .m-playbar .updn .left, .m-playbar .updn .right {
      height: 50px;
  }
          
  .m-playbar .bg {
      height: 50px;
  }
              
  .m-playbar .oper {
      width: 60px;
      position: relative;
      top: -70px;
      left: 60%;
  }
  
  .m-playbar .ctrl {
      position: relative;
      top: -70px;
      left: 60%;
  }
  
  .m-playbar .btns {
      width: 157px;
      padding: 6px 0 0 6px;
      position: relative;
      left: 35%;
  }
              
  .m-playbar .play {
      position: relative;
      width: 80%;
  }
}
}
