css:
@-moz-document domain(gigapause.com) {

/* GENERAL PROFILE LINK COLORS*/
a:hover, .tabs_alt>ul>li.tab_active>a, .tabs_alt>ul>li.active>a, .tabs_alt>ul>li>a:hover {
color:#73700F;
}

#profile_status>span a, .profile_fields>ul>li a{
color:#73700F;
}

/* TOP NOTIFICATION BAR */
.layout_core_menu_mini {
background-color:transparent;
}

div#global_wrapper {
background-image:none;
}

/* Buttons */
.layout_core_menu_mini>div>ul>li {
border-left:2px solid #73700F;
border-right:2px solid #73700F;
background-color:#73700F;
}

/* Extra orange borders */
#core_menu_mini_menu_update {
border-left:1px solid #000000;
border-right:1px solid #000000;
}

.layout_core_menu_mini #core_menu_mini_menu>ul>li>a {
border-color:#000000;
}

.layout_core_menu_mini #core_menu_mini_menu>ul>li:hover>a {
border-color:#000000;
}

/* Button Hover*/
.layout_core_menu_mini #core_menu_mini_menu>ul>li:hover>a {
background-color:#000000;
-moz-border-radius:0px;
-webkit-border-radius:0px;
border-radius:0px;
}


/* UPDATE Button NO NOTES */
.updates_pulldown>a {
background-color:#73700F;
}

/* UPDATE Button NO NOTES HOVER */
.updates_pulldown>a:hover {
background-color:#B0AB13;
color:#000000;
}

/* UPDATE Button NEW NOTES */
.updates_pulldown>a.new_updates {
background-color:#B0AB13;
}

/* UPDATE Button NEW NOTES HOVER */
.updates_pulldown>a.new_updates:hover {
background-color:#000000;
}

/* UPDATE Button ON CLICK */
#core_menu_mini_menu_update .updates_pulldown_active a#updates_toggle {
background-color:#B0AB13;
color:#000000;
}

/* Menu options */
.layout_core_menu_main>ul>li {
border-color:#000000;
background-color:#73700F; 
background-image:none;
}

/* Top-most border */
.layout_core_menu_main {
border-color:#000000;
}

/*Menu options hover */
.layout_core_menu_main>ul>li:hover {
background-color:#000000;
}

/* Menu text color */
.layout_core_menu_main>ul>li a:link, .layout_core_menu_main>ul>li a:visited {
color:#000000;
}

/*NAME HEADER */
#profile_status>h2 {
background-color:#73700F
}

/* LATEST UPDATE */
/* Background */
div#profile_status {
background-color:#B0AB13;
border-color:#000000;
}

/* Text */
#profile_status>span {
color:#000000;
}

/*RIGHT SIDEBAR*/
/*Background*/
div.layout_middle {
background-color:#73700F;
}

/*INFORMATION */
/* Backgrounds */
.layout_core_container_tabs {
background-color:#73700F;
border-color:#000000;
}

/* TaBs*/
/* Main tab background */
ul#main_tabs {
background-color:#B0AB13;
background-image:none;
border-color:#000000;
}

/* Tab colors */
.tabs_alt>ul>li {
background-color:#B0AB13;
border-color:#000000;
}

/* Active Tab */
.tabs_alt>ul>li.active>a {
background-color:#73700F;
}

/* Profile information */
/* Info headers */
.profile_fields h4 {
background-color:#000000;
color:#B0AB13;
}

/* Info text */
.profile_fields>ul {
color:#000000;
}

/*Chum page statuses */
.profile_friends_status {
color:#000000;
}

/* BEGIN PESTERING... AREA */
/* POST AREA BG */
div.activity-post-container {
background-color:#000000;
}

/* Link color*/
a#compose-photo-activator, a#compose-link-activator, a#compose-music-activator {
color:#73700F;
}

/* SHARE button */
button {
background-color:#73700F;
}

/* POSTS */
/* Post */
ul.feed .feed_item_body {
background-color:#B0AB13;
border-color:#B0AB13;
box-shadow:none;
-moz-box-shadow:none;
-webkit-box-shadow:none;
}

/* Post text */
ul.feed .feed_item_bodytext, ul.feed .feed_item_generated, ul.feed .feed_item_attachments, ul.feed .feed_item_link_desc, .feed_poll_rich_content .poll_option, .feed_poll_rich_content .poll_answer_total {
color:#000000;
}

/* Post image borders */
ul.feed .feed_attachment_photo a>img , ul.feed .feed_attachment_core_link>div>a>img, img.thumb_normal {
border-color:#000000;
}

/* Small avatar */
ul.feed .feed_item_photo>a>img {
border-color:#000000;
}

/* See More hover */
a#feed_viewmore_link:hover {
border-color:#000000;
}

/* LEFT SIDEBAR */
/* Background */
div.layout_left {
background-color:#B0AB13;
border-color:#000000;
box-shadow:none;
-moz-box-shadow:none;
-webkit-box-shadow:none;
}

/* Headers (bg image must be set to none for color to show)*/
div.layout_left>div.generic_layout_container h3 {
background-color:#73700F;
border-color:#000000;
background-image:url(http://www.gigapause.com/public/album_photo/09/61/0a/a4cb2_f254.png?c=7f82);
color:#B0AB13;
}

/* Text */
.layout_user_profile_info>ul, .layout_familymember_profile_family .familymembers_type {
color:#B0AB13;
}

/* Options separator */
#profile_options li+li {
border-color:#73700F;
}

body

{

background-image:url(http://www.gigapause.com/public/album_photo/07/61/0a/a4cb0_8dba.png?c=746b);

background-repeat:repeat;

background-position:left top;

}

div#global_wrapper

{

background-image:url(http://www.gigapause.com/public/album_photo/07/61/0a/a4cb0_8dba.png?c=746b);

background-repeat:repeat;

background-position:left top;

}

/* FOOTER */
/* Background color */
.layout_page_footer {
background-image:none;
margin-top:20px;
}

.layout_page_footer .layout_main {
background-color: #73700F;
margin-left: -10px;

-moz-border-radius:0px;
-webkit-border-radius:0px;
border-radius:0px;
}

/* Set the text colour */
.layout_core_menu_footer {
color:#000000;
margin-left: 11px; /* This puts the text back where it should be */
}

/* Link colors */
a.menu_core_footer {
color:#73700F;
}
/* LATEST UPDATE */

/* Background */

div#profile_status {

background-color:#73700F;

border-color:#000000;

}



/* Text */

#profile_status>span {

color:#000000;

}

}
