css:
@-moz-document regexp("http://(gall|job).dcinside.com/board.*"), regexp("http://(gall|job).dcinside.com/upload.*") {
#dgn_wrap {
	width: auto !important;
	margin: auto 10px auto 10px !important;
}
body {
	padding: 0px 0px 0px 0px !important;
}

*[id^="ad_"],*[class^="ad_"],
*[id$="_ad"],*[class$="_ad"],
*[id^="ad-"],*[class^="ad-"],iframe[src*="itemmania.com"],
*[id*="soeaFrame"],*[id*="NeoScreen"],
iframe[src*="ad.about.co"],.banner_dc,
#isx,#div_neo,.banner_box,#dc_advertisement,
#uniqubeAd_area,.con_banner{display: none !important;}
*[id*="banner"],*[class*="banner"] {visibility: hidden !important;}

.gallery_box {
	position: static !important;
	margin-bottom: 10px !important;
	height: auto !important;
}

#dgn_gallery_detail {
	top: 0px !important;
	margin-top: 10px !important;
}

#dgn_gallery_write {
	top: 0px !important;
	margin-bottom: 0px !important;
}
#dgn_content_write {
	padding-bottom: 0px !important;
	border-bottom: 1px solid #ddd !important;
	min-height: 725px !important;
}
.s_btn_box {
	height: 30px !important;
	padding-bottom: 30px !important;
}
.write_gall_box_2 {
	border-top: 0px !important;
}

#dgn_wide {
	bottom: 55px !important;
}

.gallery_btn_bottom {
	height: 35px !important;
}

.con_lately {
	height: 30px !important;
	border-top: 1px solid #d3d6dd !important;
	bottom: 1px !important;
}

.con_lately * {
	margin-top: 1px !important;
}

.gallery_list {
	padding: 0 0 0 0 !important;
}

.s_txt {
	margin-bottom: 18px !important;
}

.t_subject a {
	vertical-align: bottom !important;
}

.t_subject a[class^="icon_"] {
	max-width: 470px !important;
	overflow: hidden !important;
	white-space: nowrap !important;
}

.user_layer span:first-child {
	max-width: 100px;
	overflow: hidden;
	display: inline-flex;
	padding-right: 2px;
}

#dgn_pop_gall_2 {
	width: 100% !important;
	height: 100% !important;
	overflow: hidden !important;
}

.box_3 {
	height: 44px !important;
	overflow: auto !important;
}
.re_input, .user_layer, .retime {
	white-space: nowrap !important;
}

.con_recommend > .con_concept {
	display: block !important;
	padding-top: 0px !important;
}

.r_concept > .box_btn {
	display: none !important;
}

.con_recommend {
	padding-top: 8px !important;
}

.s_write {
	word-break: break-all !important;
}

.r_concept .box_title_link {
	cursor: default !important;
}

.t_subject a[href^="http://w"] {
	cursor: not-allowed !important;
	pointer-events: none !important;
}

.t_subject a[href^="http://"] b {
	display: none !important;
	font-weight: normal !important;
	color: black !important;
}
.icon_notice {
	color: black !important;
}

.t_subject a[href^="http://w"]::before {
	content: "스타일 시트 사용중입니다." !important;
	color: black !important;
}
td.t_writer[user_name=" 운영자"] b {
	display: none !important;
}
td.t_writer[user_name=" 운영자"]::before {
	content: "클린디씨" !important;
	color: black !important;
}
td.t_writer[user_name=" 운영자"]~
td {
	color: rgba(0, 0, 0, 0) !important;
}
td.t_writer[user_name=" 운영자"], td.t_writer[user_name="운영자"] {
	cursor: default !important;
	pointer-events: none !important;
}
td.t_writer[user_name="운영자"] b {
	opacity: 0 !important;
	cursor: default !important;
	pointer-events: none !important;
}

/*effect*/
tr {
	-webkit-transition: background-color 0.25s cubic-bezier(0.645, 0.045, 0.355, 1);
	transition: background-color 0.25s cubic-bezier(0.645, 0.045, 0.355, 1);
}

.tb > td, .tb a {
	vertical-align: middle !important;
}

.t_subject > a {
	-webkit-transition: padding 0.2s cubic-bezier(0.645, 0.045, 0.355, 1), text-shadow 0.8s ease !important;
	transition: padding 0.2s cubic-bezier(0.645, 0.045, 0.355, 1), text-shadow 0.8s ease !important;
}
/*
 .t_subject>a:hover {
 padding-top: 5px !important;
 padding-bottom: 5px !important;
 }
 */
.t_subject a[class^="icon_"]:hover {
	text-decoration: none !important;
	text-shadow: 0px 0px 0.5px rgba(0, 0, 0, 0.5) !important;
}

.gallery_box {
	max-height: 33px !important;
	overflow: hidden !important;
	-webkit-transition: max-height 0.5s cubic-bezier(0.645, 0.045, 0.355, 1) !important;
	transition: max-height 0.5s cubic-bezier(0.645, 0.045, 0.355, 1) !important;
}

.gallery_box:hover {
	-webkit-transition-delay: 0.3s !important;
	transition-delay: 0.3s !important;
	max-height: 1000px !important;
}

@-webkit-keyframes flp{
0%{-webkit-transform:rotateY(0deg);}
50%{-webkit-transform:rotateY(90deg);}
100%{-webkit-transform:rotateY(0deg);}
}
@keyframes flp{
0%{transform:rotateY(0deg);}
50%{transform:rotateY(90deg);}
100%{transform:rotateY(0deg);}
}

.b_re_up:hover {
	animation: flp 0.5s linear 0s 2 !important;
	-webkit-animation: flp 0.5s linear 0s 2 !important;
}

}
