css:
@-moz-document url-prefix("http://ask.fm") {
body, a.link-headline-active, a.link-headline:hover, #profile-head, a.link-profile-name, #profile-bio, .stasis-label, .stasis-digit, a.giftButton, #profile-input, #post-input-pre, .profile-title-counter, .typeCheckbox_text, a.link-name, .people-bio, .noQuestionBox, a.link-grey:hover, .widget-settingsSection-headline, .widget-settingsSection-colorLabel:hover, .widget-settingsSection-colorBox:hover, a.link-blue-underline  { 
 	color: white !important; 
  	border-bottom-color: #6D6D6D !important;
}

#post_options {
  	background: transparent none !important;
  	border: none !important;
}

a.link-profile-name:hover, .stasis-box:hover .stasis-digit, .text-grey-dark {
  	color: rgb(180, 180, 180) !important;
}

.qofday-label {
    color: #fff !important;
}


#wrapper, #profile-head, textarea {
    background-color: rgba(0, 0, 0, 0.7) !important;
}

a.link-time, .likeList.people-like-block, a.link-name:hover {
  	color: #D5D5D5 !important;
}

a.link-blue, a.link-blue-underline:hover {
  	color: #CBF0FF !important;
}

.infoBox, .questionBox, .questionBox-daily, .headline-menu {
    border-bottom: solid 1px rgba(255, 255, 255, 0.26) !important;
}

span.like-active {
    border-radius: 100px !important;
  	box-shadow: 20px 0px 0px rgb(233, 81, 81) inset !important;
	width: 10px !important;
	height: 10px !important;
	margin: 3px !important;
}

a.like {
  	border-radius: 100px !important;
	width: 10px !important;
	height: 10px !important;
	margin: 3px !important;
  	box-shadow: 20px 0px 0px rgb(190, 190, 190) inset !important;
}

#profile-head-picture {
	border-radius: 3px !important;
}

.stream-profile {
  	transition: 0.8s ease all !important;
  	-webkit-transition: 0.8s ease all !important;
	-moz-transition: 0.8s ease all !important;
	-o-transition: 0.8s ease all !important;
	border-radius: 100px !important;
}
  
.stream-profile:hover {
  	filter: opacity(.6) !important;
  	-webkit-filter: opacity(.6) !important;
	-moz-filter: opacity(.6) !important;
	-o-filter: opacity(.6) !important;
}

.photoAnswer-XL {
	background-color: transparent !important;
	border: none !important;
  	transition: 0.8s ease all !important;
  	-webkit-transition: 0.8s ease all !important;
	-moz-transition: 0.8s ease all !important;
	-o-transition: 0.8s ease all !important;
}

.photoAnswer-XL {
    border-radius: 3px !important;
}

.photoAnswer-XL:hover {
	border: none !important;
  	filter: opacity(.6) !important;
  	-webkit-filter: opacity(.6) !important;
	-moz-filter: opacity(.6) !important;
	-o-filter: opacity(.6) !important;
}

#post_options-border {
	border: none !important;
}

textarea {
  	border: 1px solid #666 !important; 
}

#profile-input, #post-input-pre {
  	border: 1px solid #666 !important;
}

#popup-content {
  	color: #000
}

a.link-news {
  	border-top: solid 1px #B4B4B4 !important;
	border-bottom: solid 1px #B4B4B4 !important;
	background-color: #FFFFFF !important;
}

a.link-news:hover {
  	background-color: #C4C4C4 !important;
}

#random-faces {
  	background: transparent none !important;
}

.border-none.hintable, .border-none {
	border-radius: 5px !important;
}

a.link-inviteLine, .popupBlockBox-line .link-name  {
  	color: #000 !important;
}

.registrationLevel .Form_text-multi, .input-multiMail {
  	color: inherit !important;
}

#more_loader, #postLoader, #cboxMiddleLeft, #cboxMiddleRight, #cboxBottomLeft, #cboxBottomCenter, #cboxBottomRight, #cboxTopLeft, #cboxTopCenter, #cboxTopRight {
  	display: none  !important;
}

#cboxOverlay {
	opacity: 0.8  !important;
}

.infoBox, .questionBox, .questionBox-daily, .headline-menu {
  	border-bottom: dotted 1px rgba(255, 255, 255, 0.26) !important;
}

.username {
  	color: #B8B8B8 !important;
}

.flyout_people-ask_follow-details .people-name .link-name {
  	color: black !important;
}

/**a.button-connect-fb, a.button-connect-fb span, a.button-connect-tw, a.button-connect-tw span, a.button-connect-vk, a.button-connect-vk span, a.notification-like, a.notification-like-active, a.notification-like-selected, a.notification-question, a.notification-question-active, a.notification-answer, a.notification-answer-active, a.notification-answer-selected, a.RSS_button, a.delete, a.delete-blue, a.birthDay-delete, a.like, span.like-active, input.link-logout, .video-play-button, a.reportPointer {
  	background-image: url('http://img824.imageshack.us/img824/6905/xn6a.png') !important;
}**/

#menu { 
  	background: black !important;
  	box-shadow: 0 0 30px white !important;
}
    
a.link-menu {
  	color: #BBBBBB !important;
}

#cboxTitle {
	border-bottom: 1px dotted grey !important;
  	background: black none !important;
}
#popup-content .link-name {
  	color: black  !important;
}
      
#popup-content .link-blue {
  	color: black  !important;
}

.like-fwd-icon {
  	content: url(http://img209.imageshack.us/img209/6985/j2vr.png) !important;
 	width: 10px !important;
    height: 10px !important;
  	margin-top: 3px !important;
  	opacity: 0.9 !important;
  	-webkit-opacity: 0.9 !important;
	-moz-opacity: 0.9 !important;
	-o-opacity: 0.9 !important;
}

#colorbox {
  	box-shadow: 0 0 30px white !important;
}

.input-message {
    background-color: #fff !important;
}
  
#login-buttonContainer .typeCheckbox_text, .uploadFile {
  	color: #000 !important;
}
  
#footer { 
        display:none !important;
}

#leaderboard {
  	display: none !important;
}

#kitten-message {
        color: white !important;
}
#recommendedBox_contour {
  display: none  !important;
}
#birthDay-wrapper { 
  background: transparent none  !important;
}

.birthDay-title {
  color: black !important;
}

.birthDay-title a {
  color: rgb(6, 0, 165) !important;
}
  
.birthDay-box {
  background-color: #E0E0E0 !important;
}
.photoAnswer-XL {
  border-radius: 7px !important;
}

#recommended_users_box { display: none }

.recentQuestionBox {
  	background-color: transparent !important;
    background-image: linear-gradient(bottom, transparent 5%, transparent 53%, rgba(223, 250, 255, 0.46) 100%) !important;
	background-image: -o-linear-gradient(bottom, transparent 5%, transparent 53%, rgba(223, 250, 255, 0.46) 100%) !important;
	background-image: -moz-linear-gradient(bottom, transparent 5%, transparent 53%, rgba(223, 250, 255, 0.46) 100%) !important;
	background-image: -webkit-linear-gradient(bottom, transparent 5%, transparent 53%, rgba(223, 250, 255, 0.46) 100%) !important;
	background-image: -ms-linear-gradient(bottom, transparent 5%, transparent 53%, rgba(223, 250, 255, 0.46) 100%) !important;

	background-image: -webkit-gradient(
	linear,
	left bottom,
	left top,
	color-stop(0.05, transparent),
	color-stop(0.53, transparent),
	color-stop(1, rgba(223, 250, 255, 0.46))) !important;
}

/**#followersRibbon {
	background: url(http://imgur.com/8Kzmq0L.png) no-repeat 0 0 !important;
}**/

.profileEditTile textarea {
  	background: none !important;
    border: none !important;
}

.profileEditTile textarea.profileEditTileMask {
    background: #fff !important;
    border: none !important;
}
}

