/* DOWNLOAD ITEMS SETTINGS */

#downloadView > richlistitem {
-moz-appearance: none !important;
padding: 0 !important;
margin: 1px 2px 1px 1px !important;
border: none !important;
outline: none !important;
}



/* NOTES:
-------------------------------------------------------------
STATE # | Description            | SIZE 1 | SIZE 2 | COLOR
-------------------------------------------------------------
state 0 = downloading            | big    | big    | Blue
state 1 = done                   | small  | medium | Green
state 2 = failed                 | small  | medium | Red
state 3 = aborted                | small  | medium | Red
state 4 = paused                 | big    | big    | Red
state 5 = starting               | big    | big    | Blue
state 6 = ???                    | big?   | big?   | Green
state 7 = checking for viruses   | big    | big    | Green
-------------------------------------------------------------
*/



/* SMALL SIZE ITEMS */

#downloadView > richlistitem[state="1"],
#downloadView > richlistitem[state="2"],
#downloadView > richlistitem[state="3"] {
min-height: 18px !important;
max-height: 18px !important;
}



/* MEDIUM SIZE ITEMS */

#downloadView > richlistitem[state="1"][selected="true"],
#downloadView > richlistitem[state="2"][selected="true"],
#downloadView > richlistitem[state="3"][selected="true"],
#downloadView > richlistitem[state="1"][current="true"],
#downloadView > richlistitem[state="2"][current="true"],
#downloadView > richlistitem[state="3"][current="true"]  {
min-height: 36px !important;
max-height: 36px !important;
}



/* BIG SIZE ITEMS */

#downloadView > richlistitem[state="0"],
#downloadView > richlistitem[state="4"],
#downloadView > richlistitem[state="5"],
#downloadView > richlistitem[state="6"],
#downloadView > richlistitem[state="7"] {
min-height: 48px !important;
max-height: 48px !important;
}



/* SMALL SIZE ITEMS BG (1: Done) */

#downloadView > richlistitem[state="1"] {
border-top: solid 1px #6bbbac !important;
border-bottom: solid 1px #04916c !important;
border-left: solid 1px #30a388 !important;
border-right: solid 1px #30a388 !important;
background: transparent  
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAQCAIAAABY/YLgAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAExJREFUeNpiOPPqIUP/hT1Mv/79Yfr59y/Tj7+/mb7/+cXy/c9vIP7FwLA8iomBj4mJgZeBiUGQiYHhSSQD2+sQBuH3wQw5v5oBAgwAcJMdWZo6Ls4AAAAASUVORK5CYII=") repeat-x center top !important;
}

#downloadView > richlistitem[state="1"]:hover {
background: transparent 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAQCAIAAABY/YLgAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAE5JREFUeNoAQAC//wDd8esAt+HUAv3//wL7/v0C9/74AvT99gL0/v0C8/35AADHYwIACfoAANhWAADjTQAA7j0ACPIxABr1NwCT/KACDADRzyEn0KpdVgAAAABJRU5ErkJggg==") repeat-x center top !important;
}



